  37→/// Timeout for network I/O in FUSE callbacks to prevent blocking the NFS thread.
  38→#[cfg(feature = "fuse")]
  39→const NETWORK_TIMEOUT: Duration = Duration::from_secs(10);
  40→
  41→/// Run an async future with a timeout on the tokio runtime.
  42→/// Prevents FUSE-T NFS thread hangs from indefinite network I/O.
  43→#[cfg(feature = "fuse")]
  44→fn block_with_timeout<F, T>(rt: &tokio::runtime::Handle, fut: F) -> Result<T, String>
  45→where
  46→    F: std::future::Future<Output = Result<T, String>>,
  47→{
  48→    rt.block_on(async {
  49→        match tokio::time::timeout(NETWORK_TIMEOUT, fut).await {
  50→            Ok(result) => result,
  51→            Err(_) => Err("Operation timed out".to_string()),
  52→        }
  53→    })
  54→}
