  91→/// Coordinates IPNS publish operations to prevent sequence number races
  92→/// and maintain a monotonic sequence number cache per IPNS name.
  93→///
  94→/// Shared via `Arc` between `CipherBoxFS` and background publish threads.
  95→#[cfg(feature = "fuse")]
  96→pub struct PublishCoordinator {
  97→    /// Per-IPNS-name sequence number cache (monotonically increasing).
  98→    seq_cache: std::sync::Mutex<HashMap<String, u64>>,
  99→    /// Per-IPNS-name publish locks to serialize concurrent publishes.
 100→    publish_locks: std::sync::Mutex<HashMap<String, Arc<tokio::sync::Mutex<()>>>>,
 101→}
 102→
 103→#[cfg(feature = "fuse")]
 104→impl PublishCoordinator {
 105→    pub fn new() -> Self {
 106→        Self {
 107→            seq_cache: std::sync::Mutex::new(HashMap::new()),
 108→            publish_locks: std::sync::Mutex::new(HashMap::new()),
 109→        }
 110→    }
 111→
 112→    /// Get or create a per-folder publish lock.
 113→    pub fn get_lock(&self, ipns_name: &str) -> Arc<tokio::sync::Mutex<()>> {
 114→        let mut locks = self.publish_locks.lock().unwrap();
 115→        locks
 116→            .entry(ipns_name.to_string())
 117→            .or_insert_with(|| Arc::new(tokio::sync::Mutex::new(())))
 118→            .clone()
 119→    }
 120→
 121→    /// Resolve current sequence number with monotonic cache fallback.
 122→    ///
 123→    /// - On successful resolve: returns max(resolved, cached)
 124→    /// - On failed resolve with cached value: returns cached value with warning
 125→    /// - On failed resolve with no cache: returns error (prevents seq rollback)
 126→    pub async fn resolve_sequence(
 127→        &self,
 128→        api: &crate::api::client::ApiClient,
 129→        ipns_name: &str,
 130→    ) -> Result<u64, String> {
 131→        match crate::api::ipns::resolve_ipns(api, ipns_name).await {
 132→            Ok(resp) => {
 133→                let resolved = match resp.sequence_number.parse::<u64>() {
 134→                    Ok(v) => v,
 135→                    Err(e) => {
 136→                        log::warn!(
 137→                            "Failed to parse IPNS sequence '{}' for {}: {}",
 138→                            resp.sequence_number, ipns_name, e
 139→                        );
 140→                        0
 141→                    }
 142→                };
 143→                let cached = self.get_cached(ipns_name).unwrap_or(0);
 144→                let seq = std::cmp::max(resolved, cached);
 145→                self.update_cache(ipns_name, seq);
 146→                Ok(seq)
 147→            }
 148→            Err(e) => match self.get_cached(ipns_name) {
 149→                Some(cached) => {
 150→                    log::warn!(
 151→                        "IPNS resolve failed for {}, using cached seq {}: {}",
 152→                        ipns_name,
 153→                        cached,
 154→                        e
 155→                    );
 156→                    Ok(cached)
 157→                }
 158→                None => Err(format!(
 159→                    "IPNS resolve failed and no cached sequence for {}: {}",
 160→                    ipns_name, e
 161→                )),
 162→            },
 163→        }
 164→    }
 165→
 166→    /// Record a successful publish — cache is monotonic (only increases).
 167→    pub fn record_publish(&self, ipns_name: &str, published_seq: u64) {
 168→        self.update_cache(ipns_name, published_seq);
 169→    }
 170→
 171→    fn get_cached(&self, ipns_name: &str) -> Option<u64> {
 172→        self.seq_cache.lock().unwrap().get(ipns_name).copied()
 173→    }
 174→
 175→    fn update_cache(&self, ipns_name: &str, seq: u64) {
 176→        let mut cache = self.seq_cache.lock().unwrap();
 177→        let entry = cache.entry(ipns_name.to_string()).or_insert(0);
 178→        if seq > *entry {
 179→            *entry = seq;
 180→        }
 181→    }
 182→}
