 184→/// Encrypt a FolderMetadata struct and package as JSON bytes ready for IPFS upload.
 185→/// CPU-only, no network I/O.
 186→#[cfg(feature = "fuse")]
 187→fn encrypt_metadata_to_json(
 188→    metadata: &crate::crypto::folder::FolderMetadata,
 189→    folder_key: &[u8],
 190→) -> Result<Vec<u8>, String> {
 191→    let folder_key_arr: [u8; 32] = folder_key
 192→        .try_into()
 193→        .map_err(|_| "Invalid folder key length".to_string())?;
 194→    let sealed = crate::crypto::folder::encrypt_folder_metadata(metadata, &folder_key_arr)
 195→        .map_err(|e| format!("Metadata encryption failed: {}", e))?;
 196→    let iv_hex = hex::encode(&sealed[..12]);
 197→    use base64::Engine;
 198→    let data_base64 = base64::engine::general_purpose::STANDARD.encode(&sealed[12..]);
 199→    let json = serde_json::json!({ "iv": iv_hex, "data": data_base64 });
 200→    serde_json::to_vec(&json).map_err(|e| format!("JSON serialization failed: {}", e))
 201→}
 202→
 203→/// Spawn a background OS thread to upload encrypted metadata and publish via IPNS.
 204→/// Returns immediately — does NOT block the calling thread.
 205→#[cfg(feature = "fuse")]
 206→fn spawn_metadata_publish(
 207→    api: Arc<ApiClient>,
 208→    rt: tokio::runtime::Handle,
 209→    metadata: crate::crypto::folder::FolderMetadata,
 210→    folder_key: Vec<u8>,
 211→    ipns_private_key: Vec<u8>,
 212→    ipns_name: String,
 213→    old_metadata_cid: Option<String>,
 214→    coordinator: Arc<PublishCoordinator>,
 215→) {
 216→    std::thread::spawn(move || {
 217→        let result = rt.block_on(async {
 218→            // Acquire per-folder publish lock to serialize concurrent publishes
 219→            let lock = coordinator.get_lock(&ipns_name);
 220→            let _guard = lock.lock().await;
 221→
 222→            // Encrypt metadata (CPU)
 223→            let json_bytes = encrypt_metadata_to_json(&metadata, &folder_key)?;
 224→
 225→            // Resolve current IPNS sequence number (monotonic cache fallback)
 226→            let seq = coordinator.resolve_sequence(&api, &ipns_name).await?;
 227→
 228→            // Upload encrypted metadata to IPFS
 229→            let new_cid = crate::api::ipfs::upload_content(&api, &json_bytes).await?;
 230→
 231→            // Create and sign IPNS record
 232→            let ipns_key_arr: [u8; 32] = ipns_private_key
 233→                .try_into()
 234→                .map_err(|_| "Invalid IPNS private key length".to_string())?;
 235→            let new_seq = seq + 1;
 236→            let value = format!("/ipfs/{}", new_cid);
 237→            let record = crate::crypto::ipns::create_ipns_record(
 238→                &ipns_key_arr,
 239→                &value,
 240→                new_seq,
 241→                86_400_000,
 242→            )
 243→            .map_err(|e| format!("IPNS record creation failed: {}", e))?;
 244→            let marshaled = crate::crypto::ipns::marshal_ipns_record(&record)
 245→                .map_err(|e| format!("IPNS record marshal failed: {}", e))?;
 246→
 247→            use base64::Engine;
 248→            let record_b64 = base64::engine::general_purpose::STANDARD.encode(&marshaled);
 249→
 250→            let req = crate::api::ipns::IpnsPublishRequest {
 251→                ipns_name: ipns_name.clone(),
 252→                record: record_b64,
 253→                metadata_cid: new_cid.clone(),
 254→                encrypted_ipns_private_key: None,
 255→                key_epoch: None,
 256→            };
 257→            crate::api::ipns::publish_ipns(&api, &req).await?;
 258→
 259→            // Record successful publish in coordinator cache
 260→            coordinator.record_publish(&ipns_name, new_seq);
 261→
 262→            // Unpin old metadata CID
 263→            if let Some(old) = old_metadata_cid {
 264→                let _ = crate::api::ipfs::unpin_content(&api, &old).await;
 265→            }
 266→
 267→            log::info!("Background metadata publish succeeded for {}", ipns_name);
 268→            Ok::<(), String>(())
 269→        });
 270→
 271→        if let Err(e) = result {
 272→            log::error!("Background metadata publish failed: {}", e);
 273→        }
 274→    });
 275→}
