 277→/// The main FUSE filesystem struct.
 278→///
 279→/// Implements `fuser::Filesystem` to serve decrypted folder listings
 280→/// and file content from the CipherBox vault.
 281→#[cfg(feature = "fuse")]
 282→pub struct CipherBoxFS {
 283→    /// Inode table mapping inode numbers to metadata.
 284→    pub inodes: inode::InodeTable,
 285→    /// Folder metadata cache with 30s TTL.
 286→    pub metadata_cache: cache::MetadataCache,
 287→    /// File content cache with 256 MiB LRU eviction.
 288→    pub content_cache: cache::ContentCache,
 289→    /// API client for IPFS/IPNS operations.
 290→    pub api: Arc<ApiClient>,
 291→    /// User's secp256k1 private key for ECIES decryption (32 bytes).
 292→    /// Wrapped in `Zeroizing` for automatic zeroization on drop.
 293→    pub private_key: Zeroizing<Vec<u8>>,
 294→    /// User's uncompressed secp256k1 public key (65 bytes, 0x04 prefix).
 295→    /// Wrapped in `Zeroizing` for automatic zeroization on drop.
 296→    pub public_key: Zeroizing<Vec<u8>>,
 297→    /// Root folder AES-256 key (32 bytes).
 298→    /// Wrapped in `Zeroizing` for automatic zeroization on drop.
 299→    pub root_folder_key: Zeroizing<Vec<u8>>,
 300→    /// Root IPNS name (k51... format).
 301→    pub root_ipns_name: String,
 302→    /// Tokio runtime handle for spawning async tasks from FUSE threads.
 303→    pub rt: tokio::runtime::Handle,
 304→    /// Next file handle counter.
 305→    pub next_fh: AtomicU64,
 306→    /// Map of open file handles (file_handle::OpenFileHandle for write buffering).
 307→    pub open_files: HashMap<u64, file_handle::OpenFileHandle>,
 308→    /// Temp directory for write-buffered files.
 309→    pub temp_dir: PathBuf,
 310→    /// TEE public key hex for encrypting IPNS private keys on new folder creation.
 311→    pub tee_public_key: Option<Vec<u8>>,
 312→    /// TEE key epoch for encrypting IPNS private keys on new folder creation.
 313→    pub tee_key_epoch: Option<u32>,
 314→    /// Receiver for background refresh results.
 315→    pub refresh_rx: std::sync::mpsc::Receiver<PendingRefresh>,
 316→    /// Sender clone for spawning background refreshes.
 317→    pub refresh_tx: std::sync::mpsc::Sender<PendingRefresh>,
 318→    /// Folders with recent local mutations — skip background refreshes
 319→    /// that would overwrite local state before IPNS publish propagates.
 320→    /// Maps folder ino → mutation timestamp.
 321→    pub mutated_folders: HashMap<u64, std::time::Instant>,
 322→    /// CIDs currently being prefetched in background (to avoid duplicate fetches).
 323→    pub prefetching: std::collections::HashSet<String>,
 324→    /// Receiver for background content prefetch results.
 325→    pub content_rx: std::sync::mpsc::Receiver<PendingContent>,
 326→    /// Sender for background content prefetch tasks.
 327→    pub content_tx: std::sync::mpsc::Sender<PendingContent>,
 328→    /// Plaintext cache for files whose upload is still in flight (keyed by inode).
 329→    pub pending_content: HashMap<u64, Vec<u8>>,
 330→    /// Receiver for background upload completion notifications.
 331→    pub upload_rx: std::sync::mpsc::Receiver<UploadComplete>,
 332→    /// Sender for background upload threads to notify completion.
 333→    pub upload_tx: std::sync::mpsc::Sender<UploadComplete>,
 334→    /// Shared coordinator for IPNS publish sequencing and per-folder locking.
 335→    pub publish_coordinator: Arc<PublishCoordinator>,
 336→    /// Debounced publish queue: folders needing metadata publish after mutations.
 337→    /// Publishes are coalesced and deferred until uploads settle.
 338→    publish_queue: HashMap<u64, PublishQueueEntry>,
 339→}
 340→
 341→#[cfg(feature = "fuse")]
 342→impl CipherBoxFS {
 343→    /// Build a FolderMetadata struct from the current inode tree (CPU-only, no network I/O).
 344→    /// Returns (metadata, folder_key, ipns_private_key, ipns_name, old_metadata_cid).
 345→    pub fn build_folder_metadata(
 346→        &self,
 347→        folder_ino: u64,
 348→    ) -> Result<
 349→        (
 350→            crate::crypto::folder::FolderMetadata,
 351→            Vec<u8>,
 352→            Vec<u8>,
 353→            String,
 354→            Option<String>,
 355→        ),
 356→        String,
 357→    > {
 358→        let (folder_key, ipns_private_key, ipns_name, child_inos) = {
 359→            let inode = self
 360→                .inodes
 361→                .get(folder_ino)
 362→                .ok_or_else(|| format!("Folder inode {} not found", folder_ino))?;
 363→
 364→            let children = inode.children.clone().unwrap_or_default();
 365→
 366→            match &inode.kind {
 367→                inode::InodeKind::Root {
 368→                    ipns_private_key,
 369→                    ipns_name,
 370→                } => {
 371→                    let key = ipns_private_key
 372→                        .as_ref()
 373→                        .ok_or("Root folder IPNS private key not available")?
 374→                        .to_vec();
 375→                    let name = ipns_name
 376→                        .as_ref()
 377→                        .ok_or("Root folder IPNS name not available")?
 378→                        .clone();
 379→                    (self.root_folder_key.to_vec(), key, name, children)
 380→                }
 381→                inode::InodeKind::Folder {
 382→                    folder_key,
 383→                    ipns_private_key,
 384→                    ipns_name,
 385→                    ..
 386→                } => {
 387→                    let key = ipns_private_key
 388→                        .as_ref()
 389→                        .ok_or("Subfolder IPNS private key not available")?
 390→                        .to_vec();
 391→                    (folder_key.to_vec(), key, ipns_name.clone(), children)
 392→                }
 393→                _ => return Err("Cannot update metadata for non-folder inode".to_string()),
 394→            }
 395→        };
 396→
 397→        let mut metadata_children = Vec::new();
 398→        for &child_ino in &child_inos {
 399→            let child = self
 400→                .inodes
 401→                .get(child_ino)
 402→                .ok_or_else(|| format!("Child inode {} not found", child_ino))?;
 403→
 404→            match &child.kind {
 405→                inode::InodeKind::Folder {
 406→                    ipns_name: child_ipns_name,
 407→                    encrypted_folder_key,
 408→                    ipns_private_key: child_ipns_key,
 409→                    ..
 410→                } => {
 411→                    let ipns_key_encrypted = if let Some(key) = child_ipns_key {
 412→                        let wrapped = crate::crypto::ecies::wrap_key(key, &self.public_key)
 413→                            .map_err(|e| format!("Failed to wrap IPNS key: {}", e))?;
 414→                        hex::encode(&wrapped)
 415→                    } else {
 416→                        String::new()
 417→                    };
 418→
 419→                    let now_ms = std::time::SystemTime::now()
 420→                        .duration_since(std::time::UNIX_EPOCH)
 421→                        .unwrap_or_default()
 422→                        .as_millis() as u64;
 423→                    let created_ms = child
 424→                        .attr
 425→                        .crtime
 426→                        .duration_since(std::time::UNIX_EPOCH)
 427→                        .unwrap_or_default()
 428→                        .as_millis() as u64;
 429→                    let modified_ms = child
 430→                        .attr
 431→                        .mtime
 432→                        .duration_since(std::time::UNIX_EPOCH)
 433→                        .unwrap_or_default()
 434→                        .as_millis() as u64;
 435→
 436→                    metadata_children.push(crate::crypto::folder::FolderChild::Folder(
 437→                        crate::crypto::folder::FolderEntry {
 438→                            id: uuid_from_ino(child_ino),
 439→                            name: child.name.clone(),
 440→                            ipns_name: child_ipns_name.clone(),
 441→                            folder_key_encrypted: encrypted_folder_key.clone(),
 442→                            ipns_private_key_encrypted: ipns_key_encrypted,
 443→                            created_at: if created_ms > 0 { created_ms } else { now_ms },
 444→                            modified_at: if modified_ms > 0 { modified_ms } else { now_ms },
 445→                        },
 446→                    ));
 447→                }
 448→                inode::InodeKind::File {
 449→                    cid,
 450→                    encrypted_file_key,
 451→                    iv,
 452→                    size,
 453→                    encryption_mode,
 454→                    ..
 455→                } => {
 456→                    let now_ms = std::time::SystemTime::now()
 457→                        .duration_since(std::time::UNIX_EPOCH)
 458→                        .unwrap_or_default()
 459→                        .as_millis() as u64;
 460→                    let created_ms = child
 461→                        .attr
 462→                        .crtime
 463→                        .duration_since(std::time::UNIX_EPOCH)
 464→                        .unwrap_or_default()
 465→                        .as_millis() as u64;
 466→                    let modified_ms = child
 467→                        .attr
 468→                        .mtime
 469→                        .duration_since(std::time::UNIX_EPOCH)
 470→                        .unwrap_or_default()
 471→                        .as_millis() as u64;
 472→
 473→                    metadata_children.push(crate::crypto::folder::FolderChild::File(
 474→                        crate::crypto::folder::FileEntry {
 475→                            id: uuid_from_ino(child_ino),
 476→                            name: child.name.clone(),
 477→                            cid: cid.clone(),
 478→                            file_key_encrypted: encrypted_file_key.clone(),
 479→                            file_iv: iv.clone(),
 480→                            size: *size,
 481→                            created_at: if created_ms > 0 { created_ms } else { now_ms },
 482→                            modified_at: if modified_ms > 0 { modified_ms } else { now_ms },
 483→                            encryption_mode: encryption_mode.clone(),
 484→                        },
 485→                    ));
 486→                }
 487→                _ => {}
 488→            }
 489→        }
 490→
 491→        let metadata = crate::crypto::folder::FolderMetadata {
 492→            version: "v1".to_string(),
 493→            children: metadata_children,
 494→        };
 495→
 496→        let old_cid = self.metadata_cache.get(&ipns_name).map(|c| c.cid.clone());
 497→
 498→        Ok((metadata, folder_key, ipns_private_key, ipns_name, old_cid))
 499→    }
 500→
 501→    /// Publish folder metadata immediately (no debounce).
 502→    /// Used for non-upload mutations (unlink, rmdir, rename) where the inode tree
 503→    /// is already in its final state and we want the IPNS record updated ASAP.
 504→    pub fn update_folder_metadata(&mut self, folder_ino: u64) -> Result<(), String> {
 505→        // Mark folder as locally mutated to prevent background refreshes
 506→        self.mutated_folders.insert(folder_ino, std::time::Instant::now());
 507→        // Build metadata and publish immediately (no debounce needed)
 508→        let (metadata, folder_key, ipns_private_key, ipns_name, old_cid) =
 509→            self.build_folder_metadata(folder_ino)?;
 510→        spawn_metadata_publish(
 511→            self.api.clone(),
 512→            self.rt.clone(),
 513→            metadata,
 514→            folder_key,
 515→            ipns_private_key,
 516→            ipns_name,
 517→            old_cid,
 518→            self.publish_coordinator.clone(),
 519→        );
 520→        Ok(())
 521→    }
 522→
 523→    /// Drain completed upload notifications and update inode CIDs + caches.
 524→    /// Also flushes the debounced publish queue when uploads settle.
 525→    pub fn drain_upload_completions(&mut self) {
 526→        while let Ok(result) = self.upload_rx.try_recv() {
 527→            log::debug!(
 528→                "Upload complete: ino {} -> CID {}",
 529→                result.ino,
 530→                result.new_cid
 531→            );
 532→            // Update inode CID from empty to real
 533→            if let Some(inode) = self.inodes.get_mut(result.ino) {
 534→                if let inode::InodeKind::File { ref mut cid, .. } = inode.kind {
 535→                    if cid.is_empty() {
 536→                        *cid = result.new_cid.clone();
 537→                    }
 538→                }
 539→            }
 540→            // Move plaintext from pending_content to content_cache
 541→            if let Some(plaintext) = self.pending_content.remove(&result.ino) {
 542→                self.content_cache.set(&result.new_cid, plaintext);
 543→            }
 544→            // Unpin old file CID in background
 545→            if let Some(old_cid) = result.old_file_cid {
 546→                let api = self.api.clone();
 547→                self.rt.spawn(async move {
 548→                    let _ = crate::api::ipfs::unpin_content(&api, &old_cid).await;
 549→                });
 550→            }
 551→            // Decrement pending upload count for this folder
 552→            if let Some(entry) = self.publish_queue.get_mut(&result.parent_ino) {
 553→                entry.pending_uploads = entry.pending_uploads.saturating_sub(1);
 554→            }
 555→        }
 556→        // Flush any publish queue entries that are ready
 557→        self.flush_publish_queue();
 558→    }
 559→
 560→    /// Queue a folder for debounced metadata publish.
 561→    /// Called after mutations (create, unlink, rmdir, rename) that change folder contents.
 562→    pub fn queue_publish(&mut self, folder_ino: u64, has_pending_upload: bool) {
 563→        let entry = self.publish_queue.entry(folder_ino).or_insert(PublishQueueEntry {
 564→            first_dirty: std::time::Instant::now(),
 565→            pending_uploads: 0,
 566→        });
 567→        if has_pending_upload {
 568→            entry.pending_uploads += 1;
 569→        }
 570→        // Mark folder as locally mutated to prevent background refreshes
 571→        self.mutated_folders.insert(folder_ino, std::time::Instant::now());
 572→    }
 573→
 574→    /// Flush publish queue entries that are ready.
 575→    /// Criteria: all uploads complete AND debounce period elapsed (1.5s),
 576→    /// OR safety valve: 10s since first change regardless of pending uploads.
 577→    fn flush_publish_queue(&mut self) {
 578→        let now = std::time::Instant::now();
 579→        let debounce = std::time::Duration::from_millis(1500);
 580→        let safety_valve = std::time::Duration::from_secs(10);
 581→
 582→        // Collect folders ready to publish
 583→        let ready: Vec<u64> = self.publish_queue.iter()
 584→            .filter(|(_, entry)| {
 585→                let elapsed = now.duration_since(entry.first_dirty);
 586→                (entry.pending_uploads == 0 && elapsed >= debounce)
 587→                    || elapsed >= safety_valve
 588→            })
 589→            .map(|(&ino, _)| ino)
 590→            .collect();
 591→
 592→        for folder_ino in ready {
 593→            self.publish_queue.remove(&folder_ino);
 594→            match self.build_folder_metadata(folder_ino) {
 595→                Ok((metadata, folder_key, ipns_private_key, ipns_name, old_cid)) => {
 596→                    spawn_metadata_publish(
 597→                        self.api.clone(),
 598→                        self.rt.clone(),
 599→                        metadata,
 600→                        folder_key,
 601→                        ipns_private_key,
 602→                        ipns_name,
 603→                        old_cid,
 604→                        self.publish_coordinator.clone(),
 605→                    );
 606→                }
 607→                Err(e) => {
 608→                    log::error!("Failed to build folder metadata for publish (ino {}): {}", folder_ino, e);
 609→                }
 610→            }
 611→        }
 612→    }
 613→
 614→    /// Drain background folder refresh results (non-blocking).
 615→    /// Called from lookup() and readdir() to apply results from async folder fetches.
 616→    /// Skips refreshes for folders with recent local mutations (prevents stale
 617→    /// remote metadata from overwriting local changes before IPNS publish propagates).
 618→    pub fn drain_refresh_completions(&mut self) {
 619→        // Clean up expired mutation cooldowns (>30s old)
 620→        let cutoff = std::time::Instant::now() - std::time::Duration::from_secs(30);
 621→        self.mutated_folders.retain(|_, ts| *ts > cutoff);
 622→
 623→        while let Ok(refresh) = self.refresh_rx.try_recv() {
 624→            // Extract a v1 FolderMetadata for cache (or synthetic for v2)
 625→            let cache_metadata = match &refresh.metadata {
 626→                crate::crypto::folder::AnyFolderMetadata::V1(v1) => v1.clone(),
 627→                crate::crypto::folder::AnyFolderMetadata::V2(_) => {
 628→                    crate::crypto::folder::FolderMetadata {
 629→                        version: "v2".to_string(),
 630→                        children: vec![],
 631→                    }
 632→                }
 633→            };
 634→
 635→            // Skip stale refreshes for recently-mutated folders or folders
 636→            // with pending publishes (to prevent re-adding deleted/stale children)
 637→            if self.mutated_folders.contains_key(&refresh.ino)
 638→                || self.publish_queue.contains_key(&refresh.ino)
 639→            {
 640→                log::debug!(
 641→                    "refresh skipped for ino {} (locally mutated or publish pending)",
 642→                    refresh.ino
 643→                );
 644→                // Still update cache so readdir doesn't re-fire refreshes
 645→                self.metadata_cache.set(&refresh.ipns_name, cache_metadata, refresh.cid);
 646→                continue;
 647→            }
 648→
 649→            self.metadata_cache.set(&refresh.ipns_name, cache_metadata, refresh.cid.clone());
 650→            // Background refresh: merge_only=true to preserve locally-created files
 651→            // that haven't been published to IPNS yet.
 652→            if let Err(e) = self.inodes.populate_folder_any(
 653→                refresh.ino, &refresh.metadata, &self.private_key, true,
 654→            ) {
 655→                log::warn!("Drain refresh apply failed for ino {}: {}", refresh.ino, e);
 656→            }
 657→
 658→            // For v2 metadata, resolve FilePointers eagerly
 659→            if matches!(&refresh.metadata, crate::crypto::folder::AnyFolderMetadata::V2(_)) {
 660→                let unresolved = self.inodes.get_unresolved_file_pointers();
 661→                if !unresolved.is_empty() {
 662→                    // Get folder key for FilePointer resolution
 663→                    let folder_key = match self.inodes.get(refresh.ino) {
 664→                        Some(inode) => match &inode.kind {
 665→                            inode::InodeKind::Root { .. } => Some(self.root_folder_key.to_vec()),
 666→                            inode::InodeKind::Folder { folder_key, .. } => Some(folder_key.to_vec()),
 667→                            _ => None,
 668→                        },
 669→                        None => None,
 670→                    };
 671→                    if let Some(fk) = folder_key {
 672→                        let api = self.api.clone();
 673→                        let rt = self.rt.clone();
 674→                        for (ino, ipns_name) in &unresolved {
 675→                            let fk_arr: Result<[u8; 32], _> = fk.as_slice().try_into();
 676→                            if let Ok(fk_arr) = fk_arr {
 677→                                let resolve_result = block_with_timeout(&rt, async {
 678→                                    let resp = crate::api::ipns::resolve_ipns(&api, ipns_name).await?;
 679→                                    let bytes = crate::api::ipfs::fetch_content(&api, &resp.cid).await?;
 680→                                    Ok::<Vec<u8>, String>(bytes)
 681→                                });
 682→                                match resolve_result {
 683→                                    Ok(enc_bytes) => {
 684→                                        match operations::decrypt_file_metadata_from_ipfs_public(&enc_bytes, &fk_arr) {
 685→                                            Ok(fm) => {
 686→                                                self.inodes.resolve_file_pointer(
 687→                                                    *ino, fm.cid, fm.file_key_encrypted,
 688→                                                    fm.file_iv, fm.size, fm.encryption_mode,
 689→                                                );
 690→                                            }
 691→                                            Err(e) => log::warn!("Drain FilePointer decrypt failed for ino {}: {}", ino, e),
 692→                                        }
 693→                                    }
 694→                                    Err(e) => log::warn!("Drain FilePointer resolve failed for ino {}: {}", ino, e),
 695→                                }
 696→                            }
 697→                        }
 698→                    }
 699→                }
 700→            }
 701→        }
 702→    }
 703→
 704→    /// Drain background content prefetch results into the content cache (non-blocking).
 705→    /// Called from read() and open() to apply results from async IPFS fetches.
 706→    pub fn drain_content_prefetches(&mut self) {
 707→        while let Ok(content) = self.content_rx.try_recv() {
 708→            self.prefetching.remove(&content.cid);
 709→            self.content_cache.set(&content.cid, content.data);
 710→        }
 711→    }
 712→}
 713→
 714→/// Generate a UUID-like string from an inode number (deterministic).
 715→/// Used for folder/file IDs in metadata when we don't have the original UUID.
 716→#[cfg(feature = "fuse")]
 717→fn uuid_from_ino(ino: u64) -> String {
 718→    format!(
 719→        "{:08x}-{:04x}-4{:03x}-{:04x}-{:012x}",
 720→        (ino >> 32) as u32,
 721→        ((ino >> 16) & 0xFFFF) as u16,
 722→        (ino & 0xFFF) as u16,
 723→        (0x8000 | (ino & 0x3FFF)) as u16,
 724→        ino & 0xFFFFFFFFFFFF,
 725→    )
 726→}
 727→
 728→/// Get the mount point path: ~/CipherBox
 729→#[cfg(feature = "fuse")]
 730→pub fn mount_point() -> PathBuf {
 731→    dirs::home_dir()
 732→        .expect("Could not determine home directory")
 733→        .join("CipherBox")
 734→}
 735→
 736→/// Mount the FUSE filesystem after successful authentication.
 737→///
 738→/// Creates the ~/CipherBox directory if it doesn't exist, builds the
 739→/// CipherBoxFS with keys from AppState, and spawns the FUSE event loop
 740→/// on a dedicated std::thread (not tokio -- fuser runs its own event loop).
 741→///
 742→/// Returns a JoinHandle for the mount thread.
 743→#[cfg(feature = "fuse")]
 744→pub async fn mount_filesystem(
 745→    state: &AppState,
 746→    rt: tokio::runtime::Handle,
 747→    private_key: Vec<u8>,
 748→    public_key: Vec<u8>,
 749→    root_folder_key: Vec<u8>,
 750→    root_ipns_name: String,
 751→    root_ipns_private_key: Option<Vec<u8>>,
 752→    tee_public_key: Option<Vec<u8>>,
 753→    tee_key_epoch: Option<u32>,
 754→) -> Result<std::thread::JoinHandle<()>, String> {
 755→    let mount_path = mount_point();
 756→
 757→    // Refuse to proceed if mount point is a symlink (TOCTOU defense)
 758→    if mount_path.is_symlink() {
 759→        return Err("Mount point is a symlink — refusing to proceed".to_string());
 760→    }
 761→
 762→    // Create mount directory if it doesn't exist
 763→    if !mount_path.exists() {
 764→        std::fs::create_dir_all(&mount_path)
 765→            .map_err(|e| format!("Failed to create mount point: {}", e))?;
 766→        #[cfg(unix)]
 767→        {
 768→            use std::os::unix::fs::PermissionsExt;
 769→            let _ = std::fs::set_permissions(&mount_path, std::fs::Permissions::from_mode(0o700));
 770→        }
 771→    } else {
 772→        // Clean stale files left after a crash (e.g. .DS_Store, .metadata_never_index).
 773→        // FUSE mount will fail or behave unexpectedly if the directory isn't empty.
 774→        if let Ok(entries) = std::fs::read_dir(&mount_path) {
 775→            for entry in entries.flatten() {
 776→                let path = entry.path();
 777→                if path.is_dir() {
 778→                    let _ = std::fs::remove_dir_all(&path);
 779→                } else {
 780→                    let _ = std::fs::remove_file(&path);
 781→                }
 782→            }
 783→            log::info!("Cleaned stale mount point: {}", mount_path.display());
 784→        }
 785→    }
 786→
 787→    // Prevent Spotlight from indexing the mount (creates .metadata_never_index)
 788→    let never_index = mount_path.join(".metadata_never_index");
 789→    if !never_index.exists() {
 790→        let _ = std::fs::File::create(&never_index);
 791→    }
 792→
 793→    // Create temp directory for write buffering
 794→    let temp_dir = std::env::temp_dir().join("cipherbox");
 795→    std::fs::create_dir_all(&temp_dir)
 796→        .map_err(|e| format!("Failed to create temp directory: {}", e))?;
 797→    #[cfg(unix)]
 798→    {
 799→        use std::os::unix::fs::PermissionsExt;
 800→        let _ = std::fs::set_permissions(&temp_dir, std::fs::Permissions::from_mode(0o700));
 801→    }
 802→
 803→    // Build the filesystem
 804→    let mut inodes = inode::InodeTable::new();
 805→
 806→    // Set root inode's IPNS data
 807→    if let Some(root) = inodes.get_mut(inode::ROOT_INO) {
 808→        root.kind = inode::InodeKind::Root {
 809→            ipns_private_key: root_ipns_private_key.map(Zeroizing::new),
 810→            ipns_name: Some(root_ipns_name.clone()),
 811→        };
 812→    }
 813→
 814→    // Channels for background operations
 815→    let (refresh_tx, refresh_rx) = std::sync::mpsc::channel::<PendingRefresh>();
 816→    let (content_tx, content_rx) = std::sync::mpsc::channel::<PendingContent>();
 817→    let (upload_tx, upload_rx) = std::sync::mpsc::channel::<UploadComplete>();
 818→
 819→    // Pre-populate root folder BEFORE mounting so init()/readdir() have no network I/O.
 820→    // This runs on the calling thread (tokio context available via rt handle).
 821→    let mut metadata_cache = cache::MetadataCache::new();
 822→    log::info!("Pre-populating root folder from IPNS...");
 823→    let fetch_result: Result<(Vec<u8>, String), String> = async {
 824→        let resolve_resp =
 825→            crate::api::ipns::resolve_ipns(&state.api, &root_ipns_name).await?;
 826→        let encrypted_bytes =
 827→            crate::api::ipfs::fetch_content(&state.api, &resolve_resp.cid).await?;
 828→        Ok((encrypted_bytes, resolve_resp.cid))
 829→    }.await;
 830→    match fetch_result {
 831→        Ok((encrypted_bytes, cid)) => {
 832→            match operations::decrypt_metadata_from_ipfs_public(&encrypted_bytes, &root_folder_key) {
 833→                Ok(any_metadata) => {
 834→                    // Cache metadata for readdir staleness checks
 835→                    let cache_meta = match &any_metadata {
 836→                        crate::crypto::folder::AnyFolderMetadata::V1(v1) => v1.clone(),
 837→                        crate::crypto::folder::AnyFolderMetadata::V2(_) => {
 838→                            crate::crypto::folder::FolderMetadata {
 839→                                version: "v2".to_string(),
 840→                                children: vec![],
 841→                            }
 842→                        }
 843→                    };
 844→                    metadata_cache.set(&root_ipns_name, cache_meta, cid);
 845→
 846→                    // Populate inode table (dispatches v1/v2) — initial mount, full replace
 847→                    match inodes.populate_folder_any(inode::ROOT_INO, &any_metadata, &private_key, false) {
 848→                        Ok(()) => {
 849→                            log::info!("Root folder pre-populated successfully");
 850→
 851→                            // For v2 metadata, resolve FilePointers eagerly before mount
 852→                            let unresolved = inodes.get_unresolved_file_pointers();
 853→                            if !unresolved.is_empty() {
 854→                                log::info!("Resolving {} root FilePointer(s)...", unresolved.len());
 855→                                let root_folder_key_arr: Result<[u8; 32], _> = root_folder_key.as_slice().try_into();
 856→                                if let Ok(fk) = root_folder_key_arr {
 857→                                    for (fp_ino, fp_ipns) in &unresolved {
 858→                                        let fp_result: Result<Vec<u8>, String> = async {
 859→                                            let resp = crate::api::ipns::resolve_ipns(&state.api, fp_ipns).await?;
 860→                                            let bytes = crate::api::ipfs::fetch_content(&state.api, &resp.cid).await?;
 861→                                            Ok(bytes)
 862→                                        }.await;
 863→                                        match fp_result {
 864→                                            Ok(enc_bytes) => {
 865→                                                match operations::decrypt_file_metadata_from_ipfs_public(&enc_bytes, &fk) {
 866→                                                    Ok(fm) => {
 867→                                                        inodes.resolve_file_pointer(
 868→                                                            *fp_ino, fm.cid, fm.file_key_encrypted,
 869→                                                            fm.file_iv, fm.size, fm.encryption_mode,
 870→                                                        );
 871→                                                    }
 872→                                                    Err(e) => log::warn!("Root FilePointer decrypt failed for ino {}: {}", fp_ino, e),
 873→                                                }
 874→                                            }
 875→                                            Err(e) => log::warn!("Root FilePointer resolve failed for ino {}: {}", fp_ino, e),
 876→                                        }
 877→                                    }
 878→                                }
 879→                            }
 880→                        }
 881→                        Err(e) => log::warn!("Root folder populate failed: {}", e),
 882→                    }
 883→
 884→                    // Pre-populate immediate subfolders so Finder's first READDIR
 885→                    // returns correct data. NFS clients cache READDIR aggressively
 886→                    // and won't re-fetch even when mtime changes, so returning empty
 887→                    // on first access causes permanently stale Finder listings.
 888→                    let subfolder_infos: Vec<(u64, String, Zeroizing<Vec<u8>>)> = inodes
 889→                        .inodes
 890→                        .values()
 891→                        .filter_map(|inode| {
 892→                            if inode.parent_ino != inode::ROOT_INO { return None; }
 893→                            if let inode::InodeKind::Folder { ref ipns_name, ref folder_key, .. } = inode.kind {
 894→                                Some((inode.ino, ipns_name.clone(), folder_key.clone()))
 895→                            } else {
 896→                                None
 897→                            }
 898→                        })
 899→                        .collect();
 900→
 901→                    for (sub_ino, sub_ipns, sub_key) in &subfolder_infos {
 902→                        log::info!("Pre-populating subfolder ino={} ipns={}", sub_ino, sub_ipns);
 903→                        let sub_result: Result<(Vec<u8>, String), String> = async {
 904→                            let resp = crate::api::ipns::resolve_ipns(&state.api, sub_ipns).await?;
 905→                            let bytes = crate::api::ipfs::fetch_content(&state.api, &resp.cid).await?;
 906→                            Ok((bytes, resp.cid))
 907→                        }.await;
 908→                        match sub_result {
 909→                            Ok((enc_bytes, sub_cid)) => {
 910→                                match operations::decrypt_metadata_from_ipfs_public(&enc_bytes, sub_key) {
 911→                                    Ok(sub_any_meta) => {
 912→                                        let sub_cache_meta = match &sub_any_meta {
 913→                                            crate::crypto::folder::AnyFolderMetadata::V1(v1) => v1.clone(),
 914→                                            crate::crypto::folder::AnyFolderMetadata::V2(_) => {
 915→                                                crate::crypto::folder::FolderMetadata {
 916→                                                    version: "v2".to_string(),
 917→                                                    children: vec![],
 918→                                                }
 919→                                            }
 920→                                        };
 921→                                        metadata_cache.set(sub_ipns, sub_cache_meta, sub_cid);
 922→                                        match inodes.populate_folder_any(*sub_ino, &sub_any_meta, &private_key, false) {
 923→                                            Ok(()) => {
 924→                                                log::info!("Subfolder ino={} pre-populated", sub_ino);
 925→                                                // Resolve FilePointers in subfolder
 926→                                                let sub_unresolved = inodes.get_unresolved_file_pointers();
 927→                                                if !sub_unresolved.is_empty() {
 928→                                                    let sk_arr: Result<[u8; 32], _> = sub_key.as_slice().try_into();
 929→                                                    if let Ok(sk) = sk_arr {
 930→                                                        for (fp_ino, fp_ipns) in &sub_unresolved {
 931→                                                            let fp_result: Result<Vec<u8>, String> = async {
 932→                                                                let resp = crate::api::ipns::resolve_ipns(&state.api, fp_ipns).await?;
 933→                                                                let bytes = crate::api::ipfs::fetch_content(&state.api, &resp.cid).await?;
 934→                                                                Ok(bytes)
 935→                                                            }.await;
 936→                                                            match fp_result {
 937→                                                                Ok(enc_bytes) => {
 938→                                                                    match operations::decrypt_file_metadata_from_ipfs_public(&enc_bytes, &sk) {
 939→                                                                        Ok(fm) => {
 940→                                                                            inodes.resolve_file_pointer(
 941→                                                                                *fp_ino, fm.cid, fm.file_key_encrypted,
 942→                                                                                fm.file_iv, fm.size, fm.encryption_mode,
 943→                                                                            );
 944→                                                                        }
 945→                                                                        Err(e) => log::warn!("Sub FilePointer decrypt failed: {}", e),
 946→                                                                    }
 947→                                                                }
 948→                                                                Err(e) => log::warn!("Sub FilePointer resolve failed: {}", e),
 949→                                                            }
 950→                                                        }
 951→                                                    }
 952→                                                }
 953→                                            }
 954→                                            Err(e) => log::warn!("Subfolder ino={} populate failed: {}", sub_ino, e),
 955→                                        }
 956→                                    }
 957→                                    Err(e) => log::warn!("Subfolder ino={} decrypt failed: {}", sub_ino, e),
 958→                                }
 959→                            }
 960→                            Err(e) => log::warn!("Subfolder ino={} fetch failed: {}", sub_ino, e),
 961→                        }
 962→                    }
 963→                }
 964→                Err(e) => log::warn!("Root metadata decryption failed: {}", e),
 965→            }
 966→        }
 967→        Err(e) => log::warn!("Root folder fetch failed (mount will show empty): {}", e),
 968→    }
 969→
 970→    let fs = CipherBoxFS {
 971→        inodes,
 972→        metadata_cache,
 973→        content_cache: cache::ContentCache::new(),
 974→        api: state.api.clone(),
 975→        private_key: Zeroizing::new(private_key),
 976→        public_key: Zeroizing::new(public_key),
 977→        root_folder_key: Zeroizing::new(root_folder_key),
 978→        root_ipns_name,
 979→        rt,
 980→        next_fh: AtomicU64::new(1),
 981→        open_files: HashMap::new(),
 982→        temp_dir,
 983→        tee_public_key,
 984→        tee_key_epoch,
 985→        refresh_rx,
 986→        refresh_tx,
 987→        prefetching: std::collections::HashSet::new(),
 988→        content_rx,
 989→        content_tx,
 990→        pending_content: HashMap::new(),
 991→        upload_rx,
 992→        upload_tx,
 993→        mutated_folders: HashMap::new(),
 994→        publish_coordinator: Arc::new(PublishCoordinator::new()),
 995→        publish_queue: HashMap::new(),
 996→    };
 997→
 998→    let mount_path_clone = mount_path.clone();
 999→
1000→    // Mount options
1001→    // Note: AutoUnmount and DefaultPermissions removed for FUSE-T compatibility.
1002→    // FUSE-T is NFS-based and does not support kernel-level permission checks
1003→    // or fusermount3-based auto-unmount.
1004→    // FUSE-T mount options:
1005→    // - backend=smb: Use SMB instead of NFS backend. NFS has a known macOS kernel
1006→    //   bug where WRITE RPCs never reach the FUSE-T server for newly created files,
1007→    //   causing permanent process hangs. SMB backend avoids this entirely.
1008→    let options = vec![
1009→        MountOption::FSName("CipherBox".to_string()),
1010→        MountOption::CUSTOM("volname=CipherBox".to_string()),
1011→        MountOption::CUSTOM("noappledouble".to_string()),
1012→        MountOption::CUSTOM("noapplexattr".to_string()),
1013→        MountOption::CUSTOM("backend=smb".to_string()),
1014→        MountOption::RW,
1015→    ];
1016→
1017→    // Spawn FUSE event loop on a dedicated OS thread (not tokio).
1018→    // Use a channel so the thread can signal back if mount2 fails immediately
1019→    // (e.g. macFUSE kext not loaded). If mount2 succeeds, it blocks until
1020→    // unmount and never sends on the channel, so we use a recv_timeout.
1021→    let (tx, rx) = std::sync::mpsc::sync_channel::<Result<(), String>>(1);
1022→
1023→    let handle = std::thread::Builder::new()
1024→        .name("fuse-mount".to_string())
1025→        .spawn(move || {
1026→            log::info!(
1027→                "Mounting CipherBoxFS at {}",
1028→                mount_path_clone.display()
1029→            );
1030→            match fuser::mount2(fs, &mount_path_clone, &options) {
1031→                Ok(()) => {
1032→                    log::info!("FUSE filesystem unmounted cleanly");
1033→                    let _ = tx.send(Ok(()));
1034→                }
1035→                Err(e) => {
1036→                    log::error!("FUSE mount error: {}", e);
1037→                    let _ = tx.send(Err(format!("FUSE mount error: {}", e)));
1038→                }
1039→            }
1040→        })
1041→        .map_err(|e| format!("Failed to spawn FUSE thread: {}", e))?;
1042→
1043→    // Wait up to 2 seconds for the mount to either fail or stabilize.
1044→    // If mount2 fails (e.g. missing kext), the error arrives quickly.
1045→    // If mount2 succeeds, it blocks (running the event loop) and we get a timeout.
1046→    match rx.recv_timeout(std::time::Duration::from_secs(2)) {
1047→        Ok(Ok(())) => {
1048→            // Filesystem was unmounted immediately (unusual)
1049→            Err("FUSE filesystem unmounted immediately after mounting".to_string())
1050→        }
1051→        Ok(Err(e)) => {
1052→            // Mount failed — propagate the error
1053→            Err(e)
1054→        }
1055→        Err(std::sync::mpsc::RecvTimeoutError::Timeout) => {
1056→            // No response after 2s — mount is running
1057→            log::info!("FUSE mount confirmed at {}", mount_path.display());
1058→            Ok(handle)
1059→        }
1060→        Err(std::sync::mpsc::RecvTimeoutError::Disconnected) => {
1061→            Err("FUSE mount thread exited unexpectedly".to_string())
1062→        }
1063→    }
1064→}
1065→
1066→/// Unmount the FUSE filesystem.
1067→///
1068→/// Calls the system `umount` command to cleanly unmount ~/CipherBox.
1069→#[cfg(feature = "fuse")]
1070→pub fn unmount_filesystem() -> Result<(), String> {
1071→    let mount_path = mount_point();
1072→    log::info!("Unmounting CipherBoxFS at {}", mount_path.display());
1073→
1074→    // Clean up temp directory
1075→    let temp_dir = std::env::temp_dir().join("cipherbox");
1076→    if temp_dir.exists() {
1077→        if let Err(e) = std::fs::remove_dir_all(&temp_dir) {
1078→            log::warn!("Failed to clean temp directory: {}", e);
1079→        }
1080→    }
1081→
1082→    let status = std::process::Command::new("umount")
1083→        .arg(mount_path.to_str().unwrap())
1084→        .status()
1085→        .map_err(|e| format!("Failed to run umount: {}", e))?;
1086→
1087→    if status.success() {
1088→        log::info!("FUSE filesystem unmounted successfully");
1089→        Ok(())
1090→    } else {
1091→        // Try diskutil unmount force as fallback on macOS — Finder keeps handles open
1092→        log::info!("umount failed (likely busy), trying diskutil unmount force");
1093→        let status = std::process::Command::new("diskutil")
1094→            .args(["unmount", "force", mount_path.to_str().unwrap()])
1095→            .status()
1096→            .map_err(|e| format!("Failed to run diskutil unmount force: {}", e))?;
1097→
1098→        if status.success() {
1099→            log::info!("FUSE filesystem force-unmounted via diskutil");
1100→            Ok(())
1101→        } else {
1102→            Err(format!(
1103→                "Failed to unmount {} — close Finder windows and retry",
1104→                mount_path.display()
1105→            ))
1106→        }
1107→    }
1108→}
