  1→//! Memory caches for file content and folder metadata with TTL/LRU eviction.
  2→//!
  3→//! - MetadataCache: Folder metadata keyed by IPNS name with 30s TTL
  4→//! - ContentCache: Decrypted file content keyed by CID with 256 MiB LRU budget
  5→
  6→use std::collections::HashMap;
  7→use std::time::{Duration, Instant};
  8→use zeroize::Zeroize;
  9→
 10→use crate::crypto::folder::FolderMetadata;
 11→
 12→/// Time-to-live for cached folder metadata (matches 30s sync polling interval).
 13→pub const METADATA_TTL: Duration = Duration::from_secs(30);
 14→
 15→/// Maximum memory budget for content cache (256 MiB).
 16→pub const MAX_CACHE_SIZE: usize = 256 * 1024 * 1024;
 17→
 18→// ── Metadata Cache ────────────────────────────────────────────────────────────
 19→
 20→/// Cached folder metadata entry with timestamp.
 21→pub struct CachedMetadata {
 22→    pub metadata: FolderMetadata,
 23→    pub cid: String,
 24→    fetched_at: Instant,
 25→}
 26→
 27→/// In-memory cache for decrypted folder metadata, keyed by IPNS name.
 28→///
 29→/// Entries expire after `METADATA_TTL` (30 seconds). Stale entries return
 30→/// `None` from `get()` but remain in the map until overwritten or invalidated.
 31→pub struct MetadataCache {
 32→    entries: HashMap<String, CachedMetadata>,
 33→}
 34→
 35→impl MetadataCache {
 36→    pub fn new() -> Self {
 37→        Self {
 38→            entries: HashMap::new(),
 39→        }
 40→    }
 41→
 42→    /// Get cached metadata if it exists and is still fresh (within TTL).
 43→    ///
 44→    /// Returns `None` if the entry doesn't exist or has expired.
 45→    pub fn get(&self, ipns_name: &str) -> Option<&CachedMetadata> {
 46→        self.entries.get(ipns_name).filter(|entry| {
 47→            entry.fetched_at.elapsed() < METADATA_TTL
 48→        })
 49→    }
 50→
 51→    /// Store folder metadata in the cache.
 52→    pub fn set(&mut self, ipns_name: &str, metadata: FolderMetadata, cid: String) {
 53→        self.entries.insert(
 54→            ipns_name.to_string(),
 55→            CachedMetadata {
 56→                metadata,
 57→                cid,
 58→                fetched_at: Instant::now(),
 59→            },
 60→        );
 61→    }
 62→
 63→    /// Remove a specific cache entry (used when metadata is known to have changed).
 64→    pub fn invalidate(&mut self, ipns_name: &str) {
 65→        self.entries.remove(ipns_name);
 66→    }
 67→
 68→    /// Clear all cached metadata entries. Used during FUSE destroy().
 69→    pub fn clear(&mut self) {
 70→        self.entries.clear();
 71→    }
 72→}
 73→
 74→// ── Content Cache ─────────────────────────────────────────────────────────────
 75→
 76→/// Cached decrypted file content entry with LRU tracking.
 77→struct CachedContent {
 78→    data: Vec<u8>,
 79→    accessed_at: Instant,
 80→    size: usize,
 81→}
 82→
 83→impl Drop for CachedContent {
 84→    fn drop(&mut self) {
 85→        self.data.zeroize();
 86→    }
 87→}
 88→
 89→/// In-memory LRU cache for decrypted file content, keyed by CID.
 90→///
 91→/// Evicts least-recently-accessed entries when total size exceeds `MAX_CACHE_SIZE`.
 92→/// Content is decrypted plaintext -- never persisted to disk.
 93→pub struct ContentCache {
 94→    entries: HashMap<String, CachedContent>,
 95→    current_size: usize,
 96→}
 97→
 98→impl ContentCache {
 99→    pub fn new() -> Self {
100→        Self {
101→            entries: HashMap::new(),
102→            current_size: 0,
103→        }
104→    }
105→
106→    /// Get cached content, updating the access time for LRU tracking.
107→    ///
108→    /// Returns `None` if the CID is not in cache.
109→    pub fn get(&mut self, cid: &str) -> Option<&[u8]> {
110→        // Two-phase to satisfy borrow checker: check then update
111→        if self.entries.contains_key(cid) {
112→            let entry = self.entries.get_mut(cid).unwrap();
113→            entry.accessed_at = Instant::now();
114→            Some(&entry.data)
115→        } else {
116→            None
117→        }
118→    }
119→
120→    /// Store decrypted content in the cache, evicting LRU entries if over budget.
121→    pub fn set(&mut self, cid: &str, data: Vec<u8>) {
122→        let size = data.len();
123→
124→        // Remove existing entry for this CID if present (to update size tracking)
125→        if let Some(old) = self.entries.remove(cid) {
126→            self.current_size = self.current_size.saturating_sub(old.size);
127→        }
128→
129→        // Evict LRU entries until we have room
130→        while self.current_size + size > MAX_CACHE_SIZE && !self.entries.is_empty() {
131→            self.evict_lru();
132→        }
133→
134→        // If a single item exceeds the budget, still cache it (will be evicted next insertion)
135→        self.current_size += size;
136→        self.entries.insert(
137→            cid.to_string(),
138→            CachedContent {
139→                data,
140→                accessed_at: Instant::now(),
141→                size,
142→            },
143→        );
144→    }
145→
146→    /// Evict the least recently accessed entry from the cache.
147→    fn evict_lru(&mut self) {
148→        if let Some(oldest_key) = self
149→            .entries
150→            .iter()
151→            .min_by_key(|(_, v)| v.accessed_at)
152→            .map(|(k, _)| k.clone())
153→        {
154→            if let Some(evicted) = self.entries.remove(&oldest_key) {
155→                self.current_size = self.current_size.saturating_sub(evicted.size);
156→            }
157→        }
158→    }
159→
160→    /// Current total size of cached content in bytes.
161→    #[allow(dead_code)]
162→    pub fn current_size(&self) -> usize {
163→        self.current_size
164→    }
165→
166→    /// Clear all cached content entries, zeroizing each one via Drop.
167→    /// Used during FUSE destroy() for defense-in-depth cleanup.
168→    pub fn clear(&mut self) {
169→        self.entries.clear(); // Each CachedContent::drop() zeroizes data
170→        self.current_size = 0;
171→    }
172→}
173→
174→#[cfg(test)]
175→mod tests {
176→    use super::*;
177→
178→    // ── MetadataCache tests ───────────────────────────────────────────────
179→
180→    #[test]
181→    fn test_metadata_cache_set_and_get() {
182→        let mut cache = MetadataCache::new();
183→        let metadata = FolderMetadata {
184→            version: "v1".to_string(),
185→            children: vec![],
186→        };
187→        cache.set("k51test", metadata, "bafytest".to_string());
188→
189→        let entry = cache.get("k51test");
190→        assert!(entry.is_some());
191→        assert_eq!(entry.unwrap().cid, "bafytest");
192→    }
193→
194→    #[test]
195→    fn test_metadata_cache_miss() {
196→        let cache = MetadataCache::new();
197→        assert!(cache.get("nonexistent").is_none());
198→    }
199→
200→    #[test]
201→    fn test_metadata_cache_invalidate() {
202→        let mut cache = MetadataCache::new();
203→        let metadata = FolderMetadata {
204→            version: "v1".to_string(),
205→            children: vec![],
206→        };
207→        cache.set("k51test", metadata, "bafytest".to_string());
208→        cache.invalidate("k51test");
209→        assert!(cache.get("k51test").is_none());
210→    }
211→
212→    // TTL test: we can't easily test time expiry in a unit test without
213→    // injecting time, but we verify the check exists by ensuring fresh entries work.
214→
215→    // ── ContentCache tests ────────────────────────────────────────────────
216→
217→    #[test]
218→    fn test_content_cache_set_and_get() {
219→        let mut cache = ContentCache::new();
220→        cache.set("bafyfile1", vec![1, 2, 3, 4]);
221→
222→        let data = cache.get("bafyfile1");
223→        assert!(data.is_some());
224→        assert_eq!(data.unwrap(), &[1, 2, 3, 4]);
225→    }
226→
227→    #[test]
228→    fn test_content_cache_miss() {
229→        let mut cache = ContentCache::new();
230→        assert!(cache.get("nonexistent").is_none());
231→    }
232→
233→    #[test]
234→    fn test_content_cache_evicts_when_over_budget() {
235→        // Create a small cache with a custom max -- we'll test with the actual
236→        // struct but use large entries to trigger eviction.
237→        let mut cache = ContentCache::new();
238→
239→        // Insert entries totalling > MAX_CACHE_SIZE
240→        // Each entry is MAX_CACHE_SIZE / 2 + 1 bytes, so two entries exceed budget
241→        let half_plus = MAX_CACHE_SIZE / 2 + 1;
242→        let data1 = vec![0u8; half_plus];
243→        let data2 = vec![1u8; half_plus];
244→
245→        cache.set("cid1", data1);
246→        assert_eq!(cache.current_size(), half_plus);
247→
248→        cache.set("cid2", data2);
249→        // cid1 should have been evicted to make room for cid2
250→        assert!(cache.get("cid1").is_none());
251→        assert!(cache.get("cid2").is_some());
252→        assert_eq!(cache.current_size(), half_plus);
253→    }
254→
255→    #[test]
256→    fn test_content_cache_lru_eviction_order() {
257→        let mut cache = ContentCache::new();
258→        // Use slightly more than 1/3 so three items exceed the budget
259→        let chunk = MAX_CACHE_SIZE / 3 + 1;
260→
261→        cache.set("a", vec![0u8; chunk]);
262→        cache.set("b", vec![1u8; chunk]);
263→        // Access "a" to make it more recently used
264→        let _ = cache.get("a");
265→
266→        // Insert "c" which should evict "b" (least recently accessed)
267→        cache.set("c", vec![2u8; chunk]);
268→
269→        assert!(cache.get("a").is_some(), "a should still be cached (recently accessed)");
270→        assert!(cache.get("b").is_none(), "b should be evicted (LRU)");
271→        assert!(cache.get("c").is_some(), "c should be cached (just inserted)");
272→    }
273→
274→    #[test]
275→    fn test_content_cache_update_existing() {
276→        let mut cache = ContentCache::new();
277→        cache.set("cid1", vec![1, 2, 3]);
278→        assert_eq!(cache.current_size(), 3);
279→
280→        // Update with larger data
281→        cache.set("cid1", vec![1, 2, 3, 4, 5]);
282→        assert_eq!(cache.current_size(), 5);
283→        assert_eq!(cache.get("cid1").unwrap(), &[1, 2, 3, 4, 5]);
284→    }
285→}
