  1→//! Application state for CipherBox Desktop.
  2→//!
  3→//! Thread-safe state holding decrypted keys in memory, API client,
  4→//! and mount status. Sensitive keys are zeroed on logout via `clear_keys()`.
  5→
  6→use std::sync::Arc;
  7→use tokio::sync::RwLock;
  8→use zeroize::Zeroize;
  9→
 10→use crate::api::client::ApiClient;
 11→use crate::api::types::TeeKeysResponse;
 12→
 13→/// Channel sender type for triggering manual sync from the tray menu.
 14→pub type SyncTrigger = tokio::sync::mpsc::Sender<()>;
 15→
 16→/// FUSE mount status for the system tray indicator.
 17→#[derive(Debug, Clone, PartialEq)]
 18→pub enum MountStatus {
 19→    Unmounted,
 20→    Mounting,
 21→    Mounted,
 22→    Error(String),
 23→}
 24→
 25→/// Thread-safe application state shared across Tauri commands.
 26→///
 27→/// All sensitive key material is stored in memory only and zeroed on logout.
 28→/// The `ApiClient` is shared via `Arc` for concurrent access from async commands.
 29→pub struct AppState {
 30→    /// HTTP client for CipherBox API communication.
 31→    pub api: Arc<ApiClient>,
 32→
 33→    /// 32-byte secp256k1 private key (memory only, never persisted).
 34→    pub private_key: RwLock<Option<Vec<u8>>>,
 35→
 36→    /// 65-byte uncompressed secp256k1 public key (0x04 prefix).
 37→    pub public_key: RwLock<Option<Vec<u8>>>,
 38→
 39→    /// 32-byte AES-256 root folder encryption key.
 40→    pub root_folder_key: RwLock<Option<Vec<u8>>>,
 41→
 42→    /// Root folder IPNS name (base36 CIDv1 string, e.g., k51...).
 43→    pub root_ipns_name: RwLock<Option<String>>,
 44→
 45→    /// Decrypted 32-byte Ed25519 IPNS private key for signing root folder metadata updates.
 46→    /// Memory only, never persisted to disk.
 47→    pub root_ipns_private_key: RwLock<Option<Vec<u8>>>,
 48→
 49→    /// Authenticated user ID (JWT `sub` claim).
 50→    pub user_id: RwLock<Option<String>>,
 51→
 52→    /// Current and previous TEE public keys for IPNS key encryption.
 53→    pub tee_keys: RwLock<Option<TeeKeysResponse>>,
 54→
 55→    /// Whether the user is fully authenticated with vault keys decrypted.
 56→    pub is_authenticated: RwLock<bool>,
 57→
 58→    /// Current FUSE mount status.
 59→    pub mount_status: RwLock<MountStatus>,
 60→
 61→    /// Channel sender to trigger an immediate sync cycle from the tray "Sync Now" button.
 62→    /// Set once the SyncDaemon is spawned. Uses std::sync::RwLock because the tray
 63→    /// menu event handler is synchronous.
 64→    pub sync_trigger: std::sync::RwLock<Option<SyncTrigger>>,
 65→
 66→    /// Hex-encoded secp256k1 private key for headless auth (debug builds only).
 67→    /// Set via `--dev-key <hex>` CLI argument. Compiled out in release builds.
 68→    pub dev_key: RwLock<Option<String>>,
 69→}
 70→
 71→impl AppState {
 72→    /// Create a new AppState with the given API base URL and optional dev key.
 73→    pub fn new(api_base_url: &str, dev_key: Option<String>) -> Self {
 74→        Self {
 75→            api: Arc::new(ApiClient::new(api_base_url)),
 76→            private_key: RwLock::new(None),
 77→            public_key: RwLock::new(None),
 78→            root_folder_key: RwLock::new(None),
 79→            root_ipns_name: RwLock::new(None),
 80→            root_ipns_private_key: RwLock::new(None),
 81→            user_id: RwLock::new(None),
 82→            tee_keys: RwLock::new(None),
 83→            is_authenticated: RwLock::new(false),
 84→            mount_status: RwLock::new(MountStatus::Unmounted),
 85→            sync_trigger: std::sync::RwLock::new(None),
 86→            dev_key: RwLock::new(dev_key),
 87→        }
 88→    }
 89→
 90→    /// Zero all sensitive key material and reset authentication state.
 91→    ///
 92→    /// Uses `zeroize` to securely wipe sensitive bytes from memory.
 93→    /// Called on logout and before app exit.
 94→    pub async fn clear_keys(&self) {
 95→        // Each field uses a single lock acquisition to zeroize and clear.
 96→        {
 97→            let mut key = self.private_key.write().await;
 98→            if let Some(ref mut k) = *key { k.zeroize(); }
 99→            *key = None;
100→        }
101→        {
102→            let mut key = self.public_key.write().await;
103→            if let Some(ref mut k) = *key { k.zeroize(); }
104→            *key = None;
105→        }
106→        {
107→            let mut key = self.root_folder_key.write().await;
108→            if let Some(ref mut k) = *key { k.zeroize(); }
109→            *key = None;
110→        }
111→        {
112→            let mut key = self.root_ipns_private_key.write().await;
113→            if let Some(ref mut k) = *key { k.zeroize(); }
114→            *key = None;
115→        }
116→
117→        // Clear dev key (sensitive: contains private key hex)
118→        {
119→            let mut key = self.dev_key.write().await;
120→            if let Some(ref mut k) = *key { k.zeroize(); }
121→            *key = None;
122→        }
123→
124→        // Clear non-sensitive fields
125→        *self.root_ipns_name.write().await = None;
126→        *self.user_id.write().await = None;
127→        *self.tee_keys.write().await = None;
128→        *self.is_authenticated.write().await = false;
129→
130→        // Clear access token from API client
131→        self.api.clear_access_token().await;
132→    }
133→}
