  1→//! Tauri IPC commands for the desktop auth flow.
  2→//!
  3→//! These commands are invoked from the webview (TypeScript) via Tauri's
  4→//! `invoke()` API. They handle authentication, vault key decryption,
  5→//! Keychain storage, and logout.
  6→
  7→use std::sync::Arc;
  8→use tauri::{Manager, State};
  9→
 10→use crate::api::{auth, types};
 11→use crate::crypto;
 12→use crate::state::AppState;
 13→
 14→/// Handle completed Web3Auth authentication from the webview.
 15→///
 16→/// Called after the webview has completed the Web3Auth SDK flow and obtained
 17→/// an idToken and the user's secp256k1 private key. This command:
 18→/// 1. Sends idToken to backend to get access + refresh tokens
 19→/// 2. Stores refresh token in macOS Keychain
 20→/// 3. Stores private key and derived public key in AppState (memory only)
 21→/// 4. Fetches and decrypts vault keys (including root IPNS keypair)
 22→#[tauri::command]
 23→pub async fn handle_auth_complete(
 24→    app: tauri::AppHandle,
 25→    state: State<'_, AppState>,
 26→    id_token: String,
 27→    private_key: String,
 28→) -> Result<(), String> {
 29→    log::info!("Handling auth completion from webview");
 30→
 31→    // Update tray status: Mounting (auth in progress, about to mount)
 32→    let _ = crate::tray::update_tray_status(&app, &crate::tray::TrayStatus::Mounting);
 33→
 34→    // 1. Convert private key from hex to bytes and derive public key
 35→    //    (needed for the login request)
 36→    let private_key_hex = if private_key.starts_with("0x") {
 37→        &private_key[2..]
 38→    } else {
 39→        &private_key
 40→    };
 41→    let private_key_bytes =
 42→        hex::decode(private_key_hex).map_err(|_| "Invalid private key hex".to_string())?;
 43→    if private_key_bytes.len() != 32 {
 44→        return Err("Private key must be 32 bytes".to_string());
 45→    }
 46→
 47→    // Derive uncompressed public key from private key (65 bytes, 0x04 prefix)
 48→    // Used for both ECIES operations and backend auth (backend expects uncompressed)
 49→    let public_key_bytes = derive_public_key(&private_key_bytes)?;
 50→    let public_key_hex = hex::encode(&public_key_bytes); // 130 hex chars
 51→
 52→    // 2. Login with backend (requires uncompressed publicKey, 130 hex chars)
 53→    let login_req = types::LoginRequest {
 54→        id_token: id_token.clone(),
 55→        public_key: public_key_hex,
 56→        login_type: "corekit".to_string(),
 57→    };
 58→
 59→    let resp = state
 60→        .api
 61→        .post("/auth/login", &login_req)
 62→        .await
 63→        .map_err(|e| format!("Login request failed: {}", e))?;
 64→
 65→    if !resp.status().is_success() {
 66→        let status = resp.status();
 67→        let body = resp.text().await.unwrap_or_default();
 68→        return Err(format!("Login failed ({}): {}", status, body));
 69→    }
 70→
 71→    let login_resp: types::LoginResponse = resp
 72→        .json()
 73→        .await
 74→        .map_err(|e| format!("Failed to parse login response: {}", e))?;
 75→
 76→    // Delegate to shared post-auth setup
 77→    complete_auth_setup(
 78→        &app,
 79→        &state,
 80→        login_resp.access_token,
 81→        login_resp.refresh_token,
 82→        private_key_bytes,
 83→        public_key_bytes,
 84→        login_resp.is_new_user,
 85→        false,
 86→    )
 87→    .await
 88→}
 89→
 90→/// Shared post-auth setup used by both `handle_auth_complete` and `handle_test_login_complete`.
 91→///
 92→/// Stores tokens and keys, initializes/fetches vault, registers device, mounts FUSE,
 93→/// and hides the login window.
 94→async fn complete_auth_setup(
 95→    app: &tauri::AppHandle,
 96→    state: &AppState,
 97→    access_token: String,
 98→    refresh_token: String,
 99→    private_key_bytes: Vec<u8>,
100→    public_key_bytes: Vec<u8>,
101→    is_new_user: bool,
102→    skip_keychain: bool,
103→) -> Result<(), String> {
104→    // 1. Store access token in API client
105→    state.api.set_access_token(access_token.clone()).await;
106→
107→    // 2. Extract user ID from JWT claims (decode payload, read `sub`)
108→    let user_id = extract_user_id_from_jwt(&access_token)?;
109→    *state.user_id.write().await = Some(user_id.clone());
110→
111→    // 3. Store refresh token in Keychain (skip in test-login mode to avoid popups)
112→    if !skip_keychain {
113→        auth::store_refresh_token(&user_id, &refresh_token)
114→            .map_err(|e| format!("Keychain store failed: {}", e))?;
115→        auth::store_user_id(&user_id)
116→            .map_err(|e| format!("Keychain store user ID failed: {}", e))?;
117→    } else {
118→        log::info!("Skipping Keychain storage (test-login mode)");
119→    }
120→
121→    // 4. Store keys in AppState
122→    *state.private_key.write().await = Some(private_key_bytes.clone());
123→    *state.public_key.write().await = Some(public_key_bytes.clone());
124→
125→    // 5. Initialize vault for new users, or fetch existing vault
126→    //    Also handle the edge case where user exists but vault was deleted.
127→    if is_new_user {
128→        log::info!("New user detected, initializing vault");
129→        initialize_vault(state, &public_key_bytes).await?;
130→    }
131→    match fetch_and_decrypt_vault(state).await {
132→        Ok(()) => {}
133→        Err(e) if e.contains("404") && !is_new_user => {
134→            log::warn!("Vault not found for existing user, re-initializing");
135→            initialize_vault(state, &public_key_bytes).await?;
136→            fetch_and_decrypt_vault(state).await?;
137→        }
138→        Err(e) => return Err(e),
139→    }
140→
141→    // 6. Mark as authenticated
142→    *state.is_authenticated.write().await = true;
143→
144→    // 7. Mount FUSE filesystem (or just mark as synced if FUSE not enabled)
145→    // NOTE: Device registry spawn moved AFTER mount to avoid concurrent HTTP
146→    //       requests that cause reqwest connection pool starvation during pre-populate.
147→    #[cfg(not(feature = "fuse"))]
148→    {
149→        let _ = crate::tray::update_tray_status(app, &crate::tray::TrayStatus::Synced);
150→    }
151→    #[cfg(feature = "fuse")]
152→    {
153→        *state.mount_status.write().await = crate::state::MountStatus::Mounting;
154→        let private_key = state
155→            .private_key
156→            .read()
157→            .await
158→            .as_ref()
159→            .ok_or("Private key not available for FUSE mount")?
160→            .clone();
161→        let public_key = state
162→            .public_key
163→            .read()
164→            .await
165→            .as_ref()
166→            .ok_or("Public key not available for FUSE mount")?
167→            .clone();
168→        let root_folder_key = state
169→            .root_folder_key
170→            .read()
171→            .await
172→            .as_ref()
173→            .ok_or("Root folder key not available for FUSE mount")?
174→            .clone();
175→        let root_ipns_name = state
176→            .root_ipns_name
177→            .read()
178→            .await
179→            .as_ref()
180→            .ok_or("Root IPNS name not available for FUSE mount")?
181→            .clone();
182→        let root_ipns_private_key = state.root_ipns_private_key.read().await.clone();
183→
184→        // Extract TEE keys for new folder creation
185→        let tee_keys = state.tee_keys.read().await;
186→        let tee_public_key = tee_keys.as_ref().and_then(|tk| {
187→            hex::decode(&tk.current_public_key).ok()
188→        });
189→        let tee_key_epoch = tee_keys.as_ref().map(|tk| tk.current_epoch);
190→        drop(tee_keys);
191→
192→        let rt = tokio::runtime::Handle::current();
193→        match crate::fuse::mount_filesystem(
194→            state,
195→            rt,
196→            private_key,
197→            public_key,
198→            root_folder_key,
199→            root_ipns_name,
200→            root_ipns_private_key,
201→            tee_public_key,
202→            tee_key_epoch,
203→        ).await {
204→            Ok(_handle) => {
205→                *state.mount_status.write().await = crate::state::MountStatus::Mounted;
206→                let _ = crate::tray::update_tray_status(app, &crate::tray::TrayStatus::Synced);
207→                log::info!("FUSE filesystem mounted at ~/CipherBox");
208→            }
209→            Err(e) => {
210→                let err_msg = format!("FUSE mount failed: {}", e);
211→                *state.mount_status.write().await =
212→                    crate::state::MountStatus::Error(err_msg.clone());
213→                let _ = crate::tray::update_tray_status(
214→                    app,
215→                    &crate::tray::TrayStatus::Error(err_msg.clone()),
216→                );
217→                log::error!("{}", err_msg);
218→                // Don't fail auth -- user is authenticated but mount failed
219→            }
220→        }
221→    }
222→
223→    // 8. Register device in encrypted registry (non-blocking, after mount)
224→    {
225→        let reg_api = state.api.clone();
226→        let reg_private_key = private_key_bytes.clone();
227→        let reg_public_key = public_key_bytes.clone();
228→        let reg_user_id = user_id.clone();
229→        tokio::spawn(async move {
230→            let pk_arr: [u8; 32] = match reg_private_key.as_slice().try_into() {
231→                Ok(arr) => arr,
232→                Err(_) => {
233→                    log::warn!("Device registry: invalid private key length");
234→                    return;
235→                }
236→            };
237→            match crate::registry::register_device(
238→                &reg_api,
239→                &pk_arr,
240→                &reg_public_key,
241→                &reg_user_id,
242→            )
243→            .await
244→            {
245→                Ok(()) => log::info!("Device registry updated"),
246→                Err(e) => log::warn!("Device registry update failed (non-blocking): {}", e),
247→            }
248→        });
249→    }
250→
251→    // Close OAuth popup windows and hide the login webview
252→    for (label, window) in app.webview_windows() {
253→        if label.starts_with("oauth-popup-") {
254→            let _ = window.destroy();
255→        } else if label == "main" {
256→            let _ = window.hide();
257→        }
258→    }
259→
260→    log::info!("Authentication complete for user {}", user_id);
261→    Ok(())
262→}
