264→/// Try to silently refresh the session from a Keychain-stored refresh token.
265→///
266→/// On cold start, the private key is NOT available (it requires Web3Auth login).
267→/// This command refreshes the API session tokens only. The webview still needs
268→/// to complete Web3Auth login to obtain the private key for vault decryption.
269→///
270→/// Returns `true` if the API session was refreshed successfully.
271→/// Returns `false` if no stored session exists or refresh failed.
272→#[tauri::command]
273→pub async fn try_silent_refresh(state: State<'_, AppState>) -> Result<bool, String> {
274→    log::info!("Attempting silent refresh from Keychain");
275→
276→    // Check for stored user ID
277→    let user_id = match auth::get_last_user_id() {
278→        Ok(Some(id)) => id,
279→        Ok(None) => {
280→            log::info!("No stored user ID, silent refresh skipped");
281→            return Ok(false);
282→        }
283→        Err(e) => {
284→            log::warn!("Failed to read user ID from Keychain: {}", e);
285→            return Ok(false);
286→        }
287→    };
288→
289→    // Get refresh token from Keychain
290→    let refresh_token = match auth::get_refresh_token(&user_id) {
291→        Ok(Some(token)) => token,
292→        Ok(None) => {
293→            log::info!("No stored refresh token for user {}", user_id);
294→            return Ok(false);
295→        }
296→        Err(e) => {
297→            log::warn!("Failed to read refresh token from Keychain: {}", e);
298→            return Ok(false);
299→        }
300→    };
301→
302→    // POST /auth/refresh with the stored refresh token
303→    let refresh_req = types::RefreshRequest {
304→        refresh_token: refresh_token.clone(),
305→    };
306→
307→    let resp = match state.api.post("/auth/refresh", &refresh_req).await {
308→        Ok(r) => r,
309→        Err(e) => {
310→            log::warn!("Refresh request failed (network error): {}", e);
311→            return Ok(false);
312→        }
313→    };
314→
315→    if resp.status().as_u16() == 401 {
316→        // Stale token -- delete from Keychain
317→        log::info!("Refresh token expired, clearing Keychain");
318→        let _ = auth::delete_refresh_token(&user_id);
319→        return Ok(false);
320→    }
321→
322→    if !resp.status().is_success() {
323→        log::warn!("Refresh failed with status {}", resp.status());
324→        return Ok(false);
325→    }
326→
327→    let refresh_resp: types::RefreshResponse = resp
328→        .json()
329→        .await
330→        .map_err(|e| format!("Failed to parse refresh response: {}", e))?;
331→
332→    // Store new tokens
333→    state.api.set_access_token(refresh_resp.access_token).await;
334→    auth::store_refresh_token(&user_id, &refresh_resp.refresh_token)
335→        .map_err(|e| format!("Keychain store failed: {}", e))?;
336→    *state.user_id.write().await = Some(user_id.clone());
337→
338→    log::info!("Silent refresh successful for user {}", user_id);
339→
340→    // NOTE: Private key is NOT restored by silent refresh.
341→    // The webview must complete Web3Auth login to get the private key.
342→    // is_authenticated remains false until handle_auth_complete is called.
343→    Ok(true)
344→}
