346→/// Start the background sync daemon.
347→///
348→/// Called from the webview after successful auth + mount. Creates the sync channel,
349→/// stores the sender in AppState for the tray menu, and spawns the daemon.
350→#[tauri::command]
351→pub async fn start_sync_daemon(
352→    app: tauri::AppHandle,
353→    state: State<'_, AppState>,
354→) -> Result<(), String> {
355→    log::info!("Starting background sync daemon");
356→
357→    let (tx, rx) = tokio::sync::mpsc::channel::<()>(1);
358→
359→    // Store the sender in AppState so the tray "Sync Now" button can trigger syncs
360→    if let Ok(mut guard) = state.sync_trigger.write() {
361→        *guard = Some(tx);
362→    }
363→
364→    // Extract shared references the daemon needs from AppState.
365→    // AppState fields are RwLock which we wrap in Arc for shared ownership
366→    // between the daemon task and the Tauri-managed state.
367→    //
368→    // We use the AppHandle to get the managed state which is already Arc-wrapped by Tauri.
369→    // The daemon reads root_ipns_name and is_authenticated via the app handle's state.
370→    let api = state.api.clone();
371→    let app_handle = app.clone();
372→
373→    // Get the root IPNS name -- daemon needs to read it periodically
374→    let root_ipns_name = state.root_ipns_name.read().await.clone();
375→
376→    // Clone values for the daemon's owned copies
377→    let root_ipns_name_lock = Arc::new(tokio::sync::RwLock::new(root_ipns_name));
378→    let is_authenticated_lock = Arc::new(tokio::sync::RwLock::new(
379→        *state.is_authenticated.read().await,
380→    ));
381→
382→    // Spawn sync state bridge: periodically sync auth/ipns state from AppState to daemon
383→    let bridge_app = app.clone();
384→    let bridge_root = root_ipns_name_lock.clone();
385→    let bridge_auth = is_authenticated_lock.clone();
386→    tokio::spawn(async move {
387→        loop {
388→            tokio::time::sleep(std::time::Duration::from_secs(5)).await;
389→            let state = bridge_app.state::<AppState>();
390→            *bridge_root.write().await = state.root_ipns_name.read().await.clone();
391→            *bridge_auth.write().await = *state.is_authenticated.read().await;
392→        }
393→    });
394→
395→    tokio::spawn(async move {
396→        let mut daemon = crate::sync::SyncDaemon::new(
397→            api,
398→            root_ipns_name_lock,
399→            is_authenticated_lock,
400→            rx,
401→            app_handle,
402→        );
403→        daemon.run().await;
404→    });
405→
406→    log::info!("Sync daemon spawned");
407→    Ok(())
408→}
