420→/// Handle test-login authentication (debug builds only).
421→///
422→/// Called from the webview after a successful `POST /auth/test-login` response.
423→/// Unlike `handle_auth_complete`, this skips the `/auth/login` POST because
424→/// test-login already returns access + refresh tokens directly.
425→///
426→/// The private key comes from the test-login response (server-generated
427→/// deterministic keypair), NOT from the CLI `--dev-key` argument.
428→#[cfg(debug_assertions)]
429→#[tauri::command]
430→pub async fn handle_test_login_complete(
431→    app: tauri::AppHandle,
432→    state: State<'_, AppState>,
433→    access_token: String,
434→    refresh_token: String,
435→    private_key_hex: String,
436→    is_new_user: bool,
437→) -> Result<(), String> {
438→    log::info!("Handling test-login auth completion (debug mode)");
439→
440→    // Update tray status: Mounting
441→    let _ = crate::tray::update_tray_status(&app, &crate::tray::TrayStatus::Mounting);
442→
443→    // Convert private key hex to bytes
444→    let pk_hex = if private_key_hex.starts_with("0x") {
445→        &private_key_hex[2..]
446→    } else {
447→        &private_key_hex
448→    };
449→    let private_key_bytes =
450→        hex::decode(pk_hex).map_err(|_| "Invalid private key hex from test-login".to_string())?;
451→    if private_key_bytes.len() != 32 {
452→        return Err("Private key must be 32 bytes".to_string());
453→    }
454→
455→    // Derive public key from the test-login private key
456→    let public_key_bytes = derive_public_key(&private_key_bytes)?;
457→
458→    // Delegate to shared post-auth setup (skips /auth/login POST and Keychain)
459→    complete_auth_setup(
460→        &app,
461→        &state,
462→        access_token,
463→        refresh_token,
464→        private_key_bytes,
465→        public_key_bytes,
466→        is_new_user,
467→        true, // skip Keychain — test-login re-authenticates each time
468→    )
469→    .await
470→}
