472→/// Logout: invalidate session, clear Keychain, zero all sensitive keys.
473→#[tauri::command]
474→pub async fn logout(app: tauri::AppHandle, state: State<'_, AppState>) -> Result<(), String> {
475→    log::info!("Logging out");
476→
477→    // Unmount FUSE filesystem before clearing keys
478→    #[cfg(feature = "fuse")]
479→    {
480→        if let Err(e) = crate::fuse::unmount_filesystem() {
481→            log::warn!("FUSE unmount failed (will continue logout): {}", e);
482→        }
483→        *state.mount_status.write().await = crate::state::MountStatus::Unmounted;
484→    }
485→
486→    // POST /auth/logout (best-effort, don't fail logout if server unreachable)
487→    let resp = state.api.authenticated_post("/auth/logout", &()).await;
488→    if let Err(e) = resp {
489→        log::warn!("Logout request failed (will continue local cleanup): {}", e);
490→    }
491→
492→    // Delete refresh token from Keychain
493→    if let Some(ref user_id) = *state.user_id.read().await {
494→        let _ = auth::delete_refresh_token(user_id);
495→    }
496→
497→    // Zero all sensitive keys in memory
498→    state.clear_keys().await;
499→
500→    // Update tray status
501→    let _ = crate::tray::update_tray_status(&app, &crate::tray::TrayStatus::NotConnected);
502→
503→    log::info!("Logout complete");
504→    Ok(())
505→}
