507→/// Initialize a new vault for a first-time user.
508→///
509→/// Generates a root folder AES-256 key and derives a deterministic Ed25519 IPNS
510→/// keypair via HKDF from the user's private key. ECIES-wraps them with the
511→/// user's secp256k1 public key, and POSTs everything to `/vault/init`.
512→async fn initialize_vault(state: &AppState, public_key: &[u8]) -> Result<(), String> {
513→    // Generate root folder AES-256 key (32 random bytes)
514→    let root_folder_key = crypto::utils::generate_random_bytes(32);
515→
516→    // Derive IPNS keypair deterministically via HKDF from user's private key
517→    let private_key = state
518→        .private_key
519→        .read()
520→        .await
521→        .as_ref()
522→        .ok_or("Private key not available for vault IPNS derivation")?
523→        .clone();
524→    let private_key_arr: [u8; 32] = private_key
525→        .as_slice()
526→        .try_into()
527→        .map_err(|_| "Invalid private key length")?;
528→    let (ipns_private_key, _ipns_public_key, root_ipns_name) =
529→        crypto::hkdf::derive_vault_ipns_keypair(&private_key_arr)
530→            .map_err(|e| format!("Vault IPNS derivation failed: {:?}", e))?;
531→
532→    // ECIES-wrap keys with user's uncompressed secp256k1 public key
533→    let encrypted_root_folder_key = crypto::ecies::wrap_key(&root_folder_key, public_key)
534→        .map_err(|e| format!("Failed to wrap root folder key: {}", e))?;
535→    let encrypted_ipns_private_key = crypto::ecies::wrap_key(&ipns_private_key, public_key)
536→        .map_err(|e| format!("Failed to wrap IPNS private key: {}", e))?;
537→
538→    // 1. Register vault with backend
539→    let init_req = types::InitVaultRequest {
540→        owner_public_key: hex::encode(public_key),
541→        encrypted_root_folder_key: hex::encode(&encrypted_root_folder_key),
542→        encrypted_root_ipns_private_key: hex::encode(&encrypted_ipns_private_key),
543→        root_ipns_name: root_ipns_name.clone(),
544→    };
545→
546→    let resp = state
547→        .api
548→        .authenticated_post("/vault/init", &init_req)
549→        .await
550→        .map_err(|e| format!("Vault init request failed: {}", e))?;
551→
552→    if !resp.status().is_success() {
553→        let status = resp.status();
554→        let body = resp.text().await.unwrap_or_default();
555→        return Err(format!("Vault init failed ({}): {}", status, body));
556→    }
557→
558→    // 2. Create and publish initial empty folder metadata
559→    //    Without this, FUSE init() can't resolve the root IPNS name.
560→    log::info!("Publishing initial empty root folder metadata");
561→
562→    let empty_metadata = crypto::folder::FolderMetadata {
563→        version: "v1".to_string(),
564→        children: vec![],
565→    };
566→
567→    // Encrypt metadata with root folder key
568→    let folder_key_arr: [u8; 32] = root_folder_key
569→        .try_into()
570→        .map_err(|_| "Invalid root folder key length".to_string())?;
571→    let sealed = crypto::folder::encrypt_folder_metadata(&empty_metadata, &folder_key_arr)
572→        .map_err(|e| format!("Metadata encryption failed: {}", e))?;
573→
574→    // Format as JSON { "iv": "<hex>", "data": "<base64>" }
575→    let iv_hex = hex::encode(&sealed[..12]);
576→    use base64::Engine;
577→    let data_base64 = base64::engine::general_purpose::STANDARD.encode(&sealed[12..]);
578→    let json_metadata = serde_json::json!({
579→        "iv": iv_hex,
580→        "data": data_base64,
581→    });
582→    let json_bytes = serde_json::to_vec(&json_metadata)
583→        .map_err(|e| format!("JSON serialization failed: {}", e))?;
584→
585→    // Upload encrypted metadata to IPFS
586→    let initial_cid = crate::api::ipfs::upload_content(&state.api, &json_bytes).await?;
587→
588→    // Create and sign IPNS record (sequence 0, 24h lifetime)
589→    let ipns_key_arr: [u8; 32] = ipns_private_key
590→        .try_into()
591→        .map_err(|_| "Invalid IPNS private key length".to_string())?;
592→    let value = format!("/ipfs/{}", initial_cid);
593→    let record = crypto::ipns::create_ipns_record(&ipns_key_arr, &value, 0, 86_400_000)
594→        .map_err(|e| format!("IPNS record creation failed: {}", e))?;
595→    let marshaled = crypto::ipns::marshal_ipns_record(&record)
596→        .map_err(|e| format!("IPNS record marshaling failed: {}", e))?;
597→    let record_base64 = base64::engine::general_purpose::STANDARD.encode(&marshaled);
598→
599→    // Publish IPNS record via backend
600→    let publish_req = crate::api::ipns::IpnsPublishRequest {
601→        ipns_name: root_ipns_name.clone(),
602→        record: record_base64,
603→        metadata_cid: initial_cid,
604→        encrypted_ipns_private_key: None,
605→        key_epoch: None,
606→    };
607→    crate::api::ipns::publish_ipns(&state.api, &publish_req).await?;
608→
609→    log::info!("Vault initialized and root metadata published for new user");
610→    Ok(())
611→}
612→
613→/// Fetch vault keys from backend and decrypt them using the user's private key.
614→///
615→/// Decrypts:
616→/// - Root folder AES-256 key (32 bytes) from ECIES-wrapped hex
617→/// - Root IPNS Ed25519 private key (32 bytes) from ECIES-wrapped hex
618→///
619→/// The IPNS public key is derivable from the private key if ever needed.
620→/// Stores all keys in AppState (memory only).
621→async fn fetch_and_decrypt_vault(state: &AppState) -> Result<(), String> {
622→    log::info!("Fetching and decrypting vault keys");
623→
624→    // GET /vault
625→    let resp = state
626→        .api
627→        .authenticated_get("/vault")
628→        .await
629→        .map_err(|e| format!("Vault fetch failed: {}", e))?;
630→
631→    if !resp.status().is_success() {
632→        let status = resp.status();
633→        let body = resp.text().await.unwrap_or_default();
634→        return Err(format!("Vault fetch failed ({}): {}", status, body));
635→    }
636→
637→    let vault: types::VaultResponse = resp
638→        .json()
639→        .await
640→        .map_err(|e| format!("Failed to parse vault response: {}", e))?;
641→
642→    // Get private key for decryption
643→    let private_key = state
644→        .private_key
645→        .read()
646→        .await
647→        .as_ref()
648→        .ok_or("Private key not available for vault decryption")?
649→        .clone();
650→
651→    // Decrypt root folder key
652→    let encrypted_root_folder_key = hex::decode(&vault.encrypted_root_folder_key)
653→        .map_err(|_| "Invalid encryptedRootFolderKey hex")?;
654→    let root_folder_key = crypto::ecies::unwrap_key(&encrypted_root_folder_key, &private_key)
655→        .map_err(|e| format!("Failed to decrypt root folder key: {}", e))?;
656→    *state.root_folder_key.write().await = Some(root_folder_key);
657→
658→    // Decrypt root IPNS private key
659→    let encrypted_root_ipns_private_key = hex::decode(&vault.encrypted_root_ipns_private_key)
660→        .map_err(|_| "Invalid encryptedRootIpnsPrivateKey hex")?;
661→    let root_ipns_private_key =
662→        crypto::ecies::unwrap_key(&encrypted_root_ipns_private_key, &private_key)
663→            .map_err(|e| format!("Failed to decrypt root IPNS private key: {}", e))?;
664→
665→    // Verify stored IPNS key matches HKDF derivation (consistency check)
666→    let private_key_arr: [u8; 32] = private_key
667→        .as_slice()
668→        .try_into()
669→        .map_err(|_| "Invalid private key length for HKDF check")?;
670→    if let Ok((expected_ipns_key, _, _)) = crypto::hkdf::derive_vault_ipns_keypair(&private_key_arr) {
671→        if root_ipns_private_key != expected_ipns_key {
672→            log::warn!("Vault IPNS key mismatch: stored key differs from HKDF derivation");
673→            // Don't block - proceed with stored key for backward compatibility
674→        }
675→    }
676→
677→    *state.root_ipns_private_key.write().await = Some(root_ipns_private_key);
678→
679→    // Store IPNS name and TEE keys
680→    *state.root_ipns_name.write().await = Some(vault.root_ipns_name);
681→    *state.tee_keys.write().await = vault.tee_keys;
682→
683→    log::info!("Vault keys decrypted and stored in memory");
684→    Ok(())
685→}
686→
687→/// Extract the user ID (`sub` claim) from a JWT access token.
688→///
689→/// Decodes the JWT payload (base64url) without verification -- the server
690→/// already verified the token, we just need the `sub` field for Keychain lookup.
691→fn extract_user_id_from_jwt(token: &str) -> Result<String, String> {
692→    let parts: Vec<&str> = token.split('.').collect();
693→    if parts.len() != 3 {
694→        return Err("Invalid JWT format".to_string());
695→    }
696→
697→    // Decode the payload (second part) -- base64url encoding
698→    let payload = parts[1];
699→    // base64url: replace - with + and _ with /, then add padding
700→    let padded = match payload.len() % 4 {
701→        2 => format!("{}==", payload),
702→        3 => format!("{}=", payload),
703→        _ => payload.to_string(),
704→    };
705→    let standard = padded.replace('-', "+").replace('_', "/");
706→
707→    let decoded = base64::Engine::decode(&base64::engine::general_purpose::STANDARD, &standard)
708→        .map_err(|e| format!("Failed to decode JWT payload: {}", e))?;
709→
710→    let json: serde_json::Value = serde_json::from_slice(&decoded)
711→        .map_err(|e| format!("Failed to parse JWT payload: {}", e))?;
712→
713→    json["sub"]
714→        .as_str()
715→        .map(|s| s.to_string())
716→        .ok_or_else(|| "JWT payload missing 'sub' claim".to_string())
717→}
718→
719→/// Derive an uncompressed secp256k1 public key (65 bytes, 0x04 prefix) from a 32-byte private key.
720→///
721→/// Used for ECIES encryption/decryption operations.
722→fn derive_public_key(private_key: &[u8]) -> Result<Vec<u8>, String> {
723→    let sk = ecies::SecretKey::parse_slice(private_key)
724→        .map_err(|e| format!("Invalid secp256k1 private key: {:?}", e))?;
725→    let pk = ecies::PublicKey::from_secret_key(&sk);
726→    Ok(pk.serialize().to_vec()) // 65-byte uncompressed format
727→}
728→
729→/// Derive a compressed secp256k1 public key (33 bytes) as hex string from a 32-byte private key.
730→///
731→/// Used for backend auth — Web3Auth JWT stores the compressed key format,
732→/// so the login request must send compressed to pass the public key match check.
733→fn derive_compressed_public_key_hex(private_key: &[u8]) -> Result<String, String> {
734→    let sk = ecies::SecretKey::parse_slice(private_key)
735→        .map_err(|e| format!("Invalid secp256k1 private key: {:?}", e))?;
736→    let pk = ecies::PublicKey::from_secret_key(&sk);
737→    Ok(hex::encode(pk.serialize_compressed())) // 33-byte compressed format
738→}
