  1→//! FUSE filesystem trait implementation for CipherBoxFS.
  2→//!
  3→//! Implements read operations: init, lookup, getattr, readdir, open, read, release, statfs, access.
  4→//! Write operations: create, write, open-write, release-with-upload, unlink, setattr, flush.
  5→//!
  6→//! IMPORTANT: All async operations use block_on from the tokio runtime.
  7→//! FUSE requires synchronous replies, so we block on async operations as needed.
  8→
  9→#[cfg(feature = "fuse")]
 10→mod implementation {
 11→    use fuser::{
 12→        FileAttr, FileType, Filesystem, ReplyAttr, ReplyCreate, ReplyData, ReplyDirectory,
 13→        ReplyEntry, ReplyEmpty, ReplyOpen, ReplyStatfs, ReplyWrite, ReplyXattr, Request,
 14→    };
 15→    use std::ffi::OsStr;
 16→    use std::sync::atomic::Ordering;
 17→    use std::time::{Duration, SystemTime};
 18→
 19→    use crate::fuse::CipherBoxFS;
 20→    use crate::fuse::file_handle::OpenFileHandle;
 21→    use crate::fuse::inode::{InodeData, InodeKind, ROOT_INO, BLOCK_SIZE};
 22→
 23→    /// TTL for FUSE attribute/entry cache replies on files.
 24→    /// Longer TTL = fewer kernel callbacks = less FUSE-T NFS thread contention.
 25→    const FILE_TTL: Duration = Duration::from_secs(60);
 26→
 27→    /// TTL for directory attribute/entry cache replies.
 28→    /// Must be short (or zero) so the NFS client re-validates directory attributes
 29→    /// after mutations (rename, create, unlink, etc.) and sees updated mtime,
 30→    /// which triggers readdir cache invalidation.
 31→    const DIR_TTL: Duration = Duration::from_secs(0);
 32→
 33→    /// Pick the right TTL based on file type.
 34→    fn ttl_for(kind: FileType) -> Duration {
 35→        if kind == FileType::Directory { DIR_TTL } else { FILE_TTL }
 36→    }
 37→
 38→    /// Total storage quota in bytes (500 MiB).
 39→    const QUOTA_BYTES: u64 = 500 * 1024 * 1024;
 40→
 41→    /// Returns true if this filename is a platform-specific special file
 42→    /// that should never be created, synced, or shown in directory listings.
 43→    fn is_platform_special(name: &str) -> bool {
 44→        name.starts_with("._")
 45→            || name == ".DS_Store"
 46→            || name == ".Trashes"
 47→            || name == ".fseventsd"
 48→            || name == ".Spotlight-V100"
 49→            || name == ".hidden"
 50→            || name == ".localized"
 51→            || name == ".metadata_never_index"
 52→            || name == ".metadata_never_index_unless_rootfs"
 53→            || name == ".metadata_direct_scope_only"
 54→            || name == ".ql_disablecache"
 55→            || name == ".ql_disablethumbnails"
 56→            || name == "DCIM"
 57→            || name == "Thumbs.db"
 58→            || name == "desktop.ini"
 59→            || name == ".directory"
 60→    }
 61→
 62→    /// Maximum time for a network operation before returning EIO.
 63→    /// FUSE-T runs NFS callbacks on a single thread; blocking too long
 64→    /// causes ALL subsequent operations (including ls) to hang.
 65→    /// Keep this SHORT for non-read operations.
 66→    const NETWORK_TIMEOUT: Duration = Duration::from_secs(3);
 67→
 68→    /// Maximum time for file content download in open().
 69→    /// Large files (e.g., 64MB) can take 30-60s from staging IPFS.
 70→    /// This blocks the NFS thread, but since the content is cached after
 71→    /// open(), all subsequent reads are instant.
 72→    const CONTENT_DOWNLOAD_TIMEOUT: Duration = Duration::from_secs(120);
 73→
 74→    /// Run an async operation with a timeout, blocking the current thread.
 75→    /// Returns Err if the operation fails or times out.
 76→    fn block_with_timeout<F, T>(rt: &tokio::runtime::Handle, fut: F) -> Result<T, String>
 77→    where
 78→        F: std::future::Future<Output = Result<T, String>>,
 79→    {
 80→        rt.block_on(async {
 81→            match tokio::time::timeout(NETWORK_TIMEOUT, fut).await {
 82→                Ok(result) => result,
 83→                Err(_) => Err("Operation timed out".to_string()),
 84→            }
 85→        })
 86→    }
 87→
 88→    /// Encrypted folder metadata format from IPFS (JSON with iv + data).
 89→    #[derive(serde::Deserialize)]
 90→    struct EncryptedFolderMetadata {
 91→        /// Hex-encoded 12-byte IV for AES-GCM.
 92→        iv: String,
 93→        /// Base64-encoded AES-GCM ciphertext (includes 16-byte auth tag).
 94→        data: String,
 95→    }
 96→
 97→    /// Decrypt folder metadata fetched from IPFS, dispatching to v1 or v2.
 98→    ///
 99→    /// The IPFS content is JSON: `{ "iv": "<hex>", "data": "<base64>" }`.
100→    /// Decode IV from hex, decode ciphertext from base64, then AES-256-GCM decrypt.
101→    /// Checks the `version` field to determine v1 or v2 format.
102→    fn decrypt_metadata_from_ipfs(
103→        encrypted_bytes: &[u8],
104→        folder_key: &[u8],
105→    ) -> Result<crate::crypto::folder::AnyFolderMetadata, String> {
106→        let encrypted: EncryptedFolderMetadata = serde_json::from_slice(encrypted_bytes)
107→            .map_err(|e| format!("Failed to parse encrypted metadata JSON: {}", e))?;
108→
109→        // Decode IV from hex
110→        let iv_bytes = hex::decode(&encrypted.iv)
111→            .map_err(|_| "Invalid metadata IV hex".to_string())?;
112→        if iv_bytes.len() != 12 {
113→            return Err(format!("Invalid IV length: {} (expected 12)", iv_bytes.len()));
114→        }
115→        let iv: [u8; 12] = iv_bytes.try_into().unwrap();
116→
117→        // Decode ciphertext from base64
118→        use base64::Engine;
119→        let ciphertext = base64::engine::general_purpose::STANDARD
120→            .decode(&encrypted.data)
121→            .map_err(|e| format!("Invalid metadata base64: {}", e))?;
122→
123→        // Decrypt with AES-256-GCM
124→        let folder_key_arr: [u8; 32] = folder_key
125→            .try_into()
126→            .map_err(|_| "Invalid folder key length".to_string())?;
127→
128→        // Reconstruct sealed format: IV || ciphertext (includes tag)
129→        let mut sealed = Vec::with_capacity(12 + ciphertext.len());
130→        sealed.extend_from_slice(&iv);
131→        sealed.extend_from_slice(&ciphertext);
132→
133→        // Use version-dispatched decryption
134→        crate::crypto::folder::decrypt_any_folder_metadata(&sealed, &folder_key_arr)
135→            .map_err(|e| format!("Metadata decryption failed: {}", e))
136→    }
137→
138→    /// Helper: Fetch, decrypt, and populate a folder's children.
139→    ///
140→    /// Resolves the folder's IPNS name to CID, fetches encrypted metadata,
141→    /// decrypts with the folder key, and populates the inode table.
142→    /// Supports both v1 and v2 metadata formats.
143→    /// For v2 with FilePointers, resolves per-file IPNS metadata eagerly.
144→    fn fetch_and_populate_folder(
145→        fs: &mut CipherBoxFS,
146→        ino: u64,
147→        ipns_name: &str,
148→        folder_key: &[u8],
149→    ) -> Result<(), String> {
150→        let api = fs.api.clone();
151→        let ipns_name_owned = ipns_name.to_string();
152→        let folder_key_owned = folder_key.to_vec();
153→        let private_key = fs.private_key.clone();
154→
155→        let rt = fs.rt.clone();
156→        let result = block_with_timeout(&rt, async {
157→            let resolve_resp =
158→                crate::api::ipns::resolve_ipns(&api, &ipns_name_owned).await?;
159→            let encrypted_bytes =
160→                crate::api::ipfs::fetch_content(&api, &resolve_resp.cid).await?;
161→            Ok::<(Vec<u8>, String), String>((encrypted_bytes, resolve_resp.cid))
162→        })?;
163→
164→        let (encrypted_bytes, cid) = result;
165→
166→        // Decrypt metadata (v1 or v2)
167→        let any_metadata = decrypt_metadata_from_ipfs(&encrypted_bytes, &folder_key_owned)?;
168→
169→        // Cache metadata (store as v1 for cache compatibility; v2 folders are identified by inode state)
170→        match &any_metadata {
171→            crate::crypto::folder::AnyFolderMetadata::V1(v1) => {
172→                fs.metadata_cache.set(&ipns_name.to_string(), v1.clone(), cid);
173→            }
174→            crate::crypto::folder::AnyFolderMetadata::V2(_) => {
175→                // For v2 metadata, store a synthetic v1 entry in cache for readdir staleness checks.
176→                // The actual v2 data is handled via the inode table.
177→                let synthetic = crate::crypto::folder::FolderMetadata {
178→                    version: "v2".to_string(),
179→                    children: vec![],
180→                };
181→                fs.metadata_cache.set(&ipns_name.to_string(), synthetic, cid);
182→            }
183→        }
184→
185→        // Populate inode table with children (dispatches v1/v2).
186→        // First load for this folder — replace mode (merge_only=false).
187→        fs.inodes.populate_folder_any(ino, &any_metadata, &private_key, false)?;
188→
189→        // For v2 metadata, resolve unresolved FilePointers eagerly
190→        let unresolved = fs.inodes.get_unresolved_file_pointers();
191→        if !unresolved.is_empty() {
192→            log::info!("Resolving {} FilePointer(s) for folder ino {}", unresolved.len(), ino);
193→            resolve_file_pointers_blocking(fs, &unresolved, &folder_key_owned)?;
194→        }
195→
196→        Ok(())
197→    }
198→
