200→    ///

  1→//! HTTP client with auth header injection and desktop client type header.
  2→//!
  3→//! All requests include `X-Client-Type: desktop` header so the backend
  4→//! returns refresh tokens in the response body instead of cookies.
  5→
  6→use reqwest::{Client, Response};
  7→use serde::Serialize;
  8→use std::sync::Arc;
  9→use tokio::sync::RwLock;
 10→
 11→/// HTTP client wrapper for CipherBox API communication.
 12→///
 13→/// Manages base URL, access token, and ensures all requests
 14→/// include the `X-Client-Type: desktop` header.
 15→pub struct ApiClient {
 16→    client: Client,
 17→    base_url: String,
 18→    access_token: Arc<RwLock<Option<String>>>,
 19→}
 20→
 21→impl ApiClient {
 22→    /// Create a new API client with the given base URL.
 23→    pub fn new(base_url: &str) -> Self {
 24→        let client = Client::builder()
 25→            .timeout(std::time::Duration::from_secs(120))
 26→            .connect_timeout(std::time::Duration::from_secs(10))
 27→            .build()
 28→            .unwrap_or_else(|_| Client::new());
 29→        Self {
 30→            client,
 31→            base_url: base_url.trim_end_matches('/').to_string(),
 32→            access_token: Arc::new(RwLock::new(None)),
 33→        }
 34→    }
 35→
 36→    /// Store the access token for authenticated requests.
 37→    pub async fn set_access_token(&self, token: String) {
 38→        let mut guard = self.access_token.write().await;
 39→        *guard = Some(token);
 40→    }
 41→
 42→    /// Clear the access token (used on logout).
 43→    pub async fn clear_access_token(&self) {
 44→        let mut guard = self.access_token.write().await;
 45→        *guard = None;
 46→    }
 47→
 48→    /// Send an authenticated GET request to a relative API path.
 49→    pub async fn authenticated_get(&self, path: &str) -> Result<Response, reqwest::Error> {
 50→        let url = format!("{}{}", self.base_url, path);
 51→        let token = self.access_token.read().await;
 52→
 53→        let mut builder = self
 54→            .client
 55→            .get(&url)
 56→            .header("X-Client-Type", "desktop");
 57→
 58→        if let Some(ref t) = *token {
 59→            builder = builder.bearer_auth(t);
 60→        }
 61→
 62→        builder.send().await
 63→    }
 64→
 65→    /// Send an authenticated POST request with a JSON body to a relative API path.
 66→    pub async fn authenticated_post<T: Serialize>(
 67→        &self,
 68→        path: &str,
 69→        body: &T,
 70→    ) -> Result<Response, reqwest::Error> {
 71→        let url = format!("{}{}", self.base_url, path);
 72→        let token = self.access_token.read().await;
 73→
 74→        let mut builder = self
 75→            .client
 76→            .post(&url)
 77→            .header("X-Client-Type", "desktop")
 78→            .json(body);
 79→
 80→        if let Some(ref t) = *token {
 81→            builder = builder.bearer_auth(t);
 82→        }
 83→
 84→        builder.send().await
 85→    }
 86→
 87→    /// Send an unauthenticated POST request with a JSON body to a relative API path.
 88→    /// Used for login and refresh where no access token is available yet.
 89→    pub async fn post<T: Serialize>(
 90→        &self,
 91→        path: &str,
 92→        body: &T,
 93→    ) -> Result<Response, reqwest::Error> {
 94→        let url = format!("{}{}", self.base_url, path);
 95→        self.client
 96→            .post(&url)
 97→            .header("X-Client-Type", "desktop")
 98→            .json(body)
 99→            .send()
100→            .await
101→    }
102→
103→    /// Fetch raw bytes from an absolute URL (used for IPFS content fetching).
104→    pub async fn get_bytes(&self, url: &str) -> Result<Vec<u8>, reqwest::Error> {
105→        let resp = self.client.get(url).send().await?;
106→        let bytes = resp.bytes().await?;
107→        Ok(bytes.to_vec())
108→    }
109→
110→    /// Send an authenticated multipart POST request (used for IPFS file uploads).
111→    pub async fn authenticated_multipart_post(
112→        &self,
113→        path: &str,
114→        form: reqwest::multipart::Form,
115→    ) -> Result<Response, reqwest::Error> {
116→        let url = format!("{}{}", self.base_url, path);
117→        let token = self.access_token.read().await;
118→
119→        let mut builder = self
120→            .client
121→            .post(&url)
122→            .header("X-Client-Type", "desktop")
123→            .multipart(form);
124→
125→        if let Some(ref t) = *token {
126→            builder = builder.bearer_auth(t);
127→        }
128→
129→        builder.send().await
130→    }
131→}
