 1→//! IPNS resolution via the CipherBox backend API.
 2→//!
 3→//! Resolves IPNS names to their current CID and sequence number.
 4→
 5→use serde::Deserialize;
 6→
 7→use super::client::ApiClient;
 8→
 9→/// Response from GET /ipns/resolve.
10→#[derive(Debug, Deserialize)]
11→#[serde(rename_all = "camelCase")]
12→pub struct IpnsResolveResponse {
13→    /// Whether the resolution succeeded.
14→    pub success: bool,
15→    /// CID that the IPNS name currently points to.
16→    pub cid: String,
17→    /// Current sequence number as a string (bigint from backend).
18→    pub sequence_number: String,
19→}
20→
21→/// Resolve an IPNS name to its current CID via the backend.
22→///
23→/// GET /ipns/resolve?ipnsName={name}
24→/// Returns the CID and sequence number of the current IPNS record.
25→pub async fn resolve_ipns(
26→    client: &ApiClient,
27→    ipns_name: &str,
28→) -> Result<IpnsResolveResponse, String> {
29→    let path = format!("/ipns/resolve?ipnsName={}", urlencoding::encode(ipns_name));
30→    let resp = client
31→        .authenticated_get(&path)
32→        .await
33→        .map_err(|e| format!("IPNS resolve failed: {}", e))?;
34→
35→    if resp.status().as_u16() == 404 {
36→        return Err("IPNS name not found".to_string());
37→    }
38→
39→    if !resp.status().is_success() {
40→        let status = resp.status();
41→        let body = resp.text().await.unwrap_or_default();
42→        return Err(format!("IPNS resolve failed ({}): {}", status, body));
43→    }
44→
45→    let resolve_resp: IpnsResolveResponse = resp
46→        .json()
47→        .await
48→        .map_err(|e| format!("Failed to parse IPNS resolve response: {}", e))?;
49→
50→    Ok(resolve_resp)
51→}
52→
53→/// IPNS publish request body matching the backend PublishIpnsDto.
54→#[derive(Debug, serde::Serialize)]
55→#[serde(rename_all = "camelCase")]
56→pub struct IpnsPublishRequest {
57→    /// IPNS name (k51... CIDv1 format).
58→    pub ipns_name: String,
59→    /// Base64-encoded marshaled IPNS record (protobuf bytes).
60→    pub record: String,
61→    /// CID of the encrypted metadata this record points to.
62→    pub metadata_cid: String,
63→    /// Hex-encoded ECIES-wrapped Ed25519 private key for TEE republishing
64→    /// (only required on first publish for a new folder).
65→    #[serde(skip_serializing_if = "Option::is_none")]
66→    pub encrypted_ipns_private_key: Option<String>,
67→    /// TEE key epoch (required with encrypted_ipns_private_key).
68→    #[serde(skip_serializing_if = "Option::is_none")]
69→    pub key_epoch: Option<u32>,
70→}
71→
72→/// Publish a signed IPNS record via the backend.
73→///
74→/// POST /ipns/publish with the signed record. The backend relays
75→/// to delegated-ipfs.dev and tracks the folder for TEE republishing.
76→pub async fn publish_ipns(
77→    client: &ApiClient,
78→    request: &IpnsPublishRequest,
79→) -> Result<(), String> {
80→    let resp = client
81→        .authenticated_post("/ipns/publish", request)
82→        .await
83→        .map_err(|e| format!("IPNS publish failed: {}", e))?;
84→
85→    if !resp.status().is_success() {
86→        let status = resp.status();
87→        let body = resp.text().await.unwrap_or_default();
88→        return Err(format!("IPNS publish failed ({}): {}", status, body));
89→    }
90→
91→    Ok(())
92→}
