200→    ///
201→    /// This is called eagerly during populate_folder to ensure all files have correct
202→    /// CID/key/IV/size BEFORE the first READDIR (NFS stability requirement).
203→    fn resolve_file_pointers_blocking(
204→        fs: &mut CipherBoxFS,
205→        unresolved: &[(u64, String)],
206→        folder_key: &[u8],
207→    ) -> Result<(), String> {
208→        let api = fs.api.clone();
209→        let rt = fs.rt.clone();
210→        let folder_key_arr: [u8; 32] = folder_key.try_into()
211→            .map_err(|_| "Invalid folder key length for FilePointer resolution".to_string())?;
212→
213→        for (ino, ipns_name) in unresolved {
214→            let resolve_result = block_with_timeout(&rt, async {
215→                let resp = crate::api::ipns::resolve_ipns(&api, ipns_name).await?;
216→                let encrypted_bytes = crate::api::ipfs::fetch_content(&api, &resp.cid).await?;
217→                Ok::<Vec<u8>, String>(encrypted_bytes)
218→            });
219→
220→            match resolve_result {
221→                Ok(encrypted_bytes) => {
222→                    // Decrypt file metadata JSON (same format as folder metadata: { iv, data })
223→                    match decrypt_file_metadata_from_ipfs(&encrypted_bytes, &folder_key_arr) {
224→                        Ok(file_meta) => {
225→                            fs.inodes.resolve_file_pointer(
226→                                *ino,
227→                                file_meta.cid,
228→                                file_meta.file_key_encrypted,
229→                                file_meta.file_iv,
230→                                file_meta.size,
231→                                file_meta.encryption_mode,
232→                            );
233→                        }
234→                        Err(e) => {
235→                            log::warn!(
236→                                "FilePointer resolution failed for ino {} ({}): {}",
237→                                ino, ipns_name, e
238→                            );
239→                        }
240→                    }
241→                }
242→                Err(e) => {
243→                    log::warn!(
244→                        "FilePointer IPNS resolve failed for ino {} ({}): {}",
245→                        ino, ipns_name, e
246→                    );
247→                }
248→            }
249→        }
250→
251→        Ok(())
252→    }
