254→    /// Decrypt per-file metadata fetched from IPFS.
255→    ///
256→    /// The IPFS content is JSON: `{ "iv": "<hex>", "data": "<base64>" }`.
257→    /// Uses the parent folder's key for decryption.
258→    fn decrypt_file_metadata_from_ipfs(
259→        encrypted_bytes: &[u8],
260→        folder_key: &[u8; 32],
261→    ) -> Result<crate::crypto::folder::FileMetadata, String> {
262→        let encrypted: EncryptedFolderMetadata = serde_json::from_slice(encrypted_bytes)
263→            .map_err(|e| format!("Failed to parse encrypted file metadata JSON: {}", e))?;
264→
265→        let iv_bytes = hex::decode(&encrypted.iv)
266→            .map_err(|_| "Invalid file metadata IV hex".to_string())?;
267→        if iv_bytes.len() != 12 {
268→            return Err(format!("Invalid IV length: {} (expected 12)", iv_bytes.len()));
269→        }
270→        let iv: [u8; 12] = iv_bytes.try_into().unwrap();
271→
272→        use base64::Engine;
273→        let ciphertext = base64::engine::general_purpose::STANDARD
274→            .decode(&encrypted.data)
275→            .map_err(|e| format!("Invalid file metadata base64: {}", e))?;
276→
277→        // Reconstruct sealed format: IV || ciphertext (includes tag)
278→        let mut sealed = Vec::with_capacity(12 + ciphertext.len());
279→        sealed.extend_from_slice(&iv);
280→        sealed.extend_from_slice(&ciphertext);
281→
282→        crate::crypto::folder::decrypt_file_metadata(&sealed, folder_key)
283→            .map_err(|e| format!("File metadata decryption failed: {}", e))
284→    }
285→
286→    /// Helper: Fetch and decrypt existing file content for editing.
287→    ///
288→    /// Used when opening an existing file for writing -- need to pre-populate
289→    /// the temp file with the current decrypted content.
290→    /// Dispatches to AES-GCM or AES-CTR based on encryption_mode.
291→    fn fetch_and_decrypt_file_content(
292→        fs: &CipherBoxFS,
293→        cid: &str,
294→        encrypted_file_key_hex: &str,
295→        iv_hex: &str,
296→        encryption_mode: &str,
297→    ) -> Result<Vec<u8>, String> {
298→        let api = fs.api.clone();
299→        let private_key = fs.private_key.clone();
300→        let cid_owned = cid.to_string();
301→        let key_hex = encrypted_file_key_hex.to_string();
302→        let iv_hex_owned = iv_hex.to_string();
303→        let mode = encryption_mode.to_string();
304→        let rt = fs.rt.clone();
305→
306→        block_with_timeout(&rt, async {
307→            let encrypted_bytes =
308→                crate::api::ipfs::fetch_content(&api, &cid_owned).await?;
309→            let encrypted_file_key = hex::decode(&key_hex)
310→                .map_err(|_| "Invalid file key hex".to_string())?;
311→            let mut file_key =
312→                crate::crypto::ecies::unwrap_key(&encrypted_file_key, &private_key)
313→                    .map_err(|e| format!("File key unwrap failed: {}", e))?;
314→            let file_key_arr: [u8; 32] = file_key.as_slice().try_into()
315→                .map_err(|_| "Invalid file key length".to_string())?;
316→
317→            let plaintext = if mode == "CTR" {
318→                // AES-CTR: 16-byte IV, no auth tag
319→                let iv = hex::decode(&iv_hex_owned)
320→                    .map_err(|_| "Invalid file IV hex".to_string())?;
321→                let iv_arr: [u8; 16] = iv.try_into()
322→                    .map_err(|_| "Invalid CTR IV length (expected 16)".to_string())?;
323→                crate::crypto::aes_ctr::decrypt_aes_ctr(&encrypted_bytes, &file_key_arr, &iv_arr)
324→                    .map_err(|e| format!("CTR file decryption failed: {}", e))?
325→            } else {
326→                // AES-GCM: 12-byte IV, 16-byte auth tag appended
327→                let iv = hex::decode(&iv_hex_owned)
328→                    .map_err(|_| "Invalid file IV hex".to_string())?;
329→                let iv_arr: [u8; 12] = iv.try_into()
330→                    .map_err(|_| "Invalid GCM IV length (expected 12)".to_string())?;
331→                crate::crypto::aes::decrypt_aes_gcm(
332→                    &encrypted_bytes, &file_key_arr, &iv_arr,
333→                )
334→                .map_err(|e| format!("GCM file decryption failed: {}", e))?
335→            };
336→
337→            crate::crypto::utils::clear_bytes(&mut file_key);
338→            Ok(plaintext)
339→        })
340→    }
