342→    /// Async version of content download + decrypt for use in background prefetch tasks.
343→    /// Does not require a reference to CipherBoxFS — takes all needed params by value.
344→    async fn fetch_and_decrypt_content_async(
345→        api: &crate::api::client::ApiClient,
346→        cid: &str,
347→        encrypted_file_key_hex: &str,
348→        iv_hex: &str,
349→        encryption_mode: &str,
350→        private_key: &[u8],
351→    ) -> Result<Vec<u8>, String> {
352→        let encrypted_bytes =
353→            crate::api::ipfs::fetch_content(api, cid).await?;
354→        let encrypted_file_key = hex::decode(encrypted_file_key_hex)
355→            .map_err(|_| "Invalid file key hex".to_string())?;
356→        let mut file_key =
357→            crate::crypto::ecies::unwrap_key(&encrypted_file_key, private_key)
358→                .map_err(|e| format!("File key unwrap failed: {}", e))?;
359→        let file_key_arr: [u8; 32] = file_key.as_slice().try_into()
360→            .map_err(|_| "Invalid file key length".to_string())?;
361→
362→        let plaintext = if encryption_mode == "CTR" {
363→            let iv = hex::decode(iv_hex)
364→                .map_err(|_| "Invalid file IV hex".to_string())?;
365→            let iv_arr: [u8; 16] = iv.try_into()
366→                .map_err(|_| "Invalid CTR IV length (expected 16)".to_string())?;
367→            crate::crypto::aes_ctr::decrypt_aes_ctr(&encrypted_bytes, &file_key_arr, &iv_arr)
368→                .map_err(|e| format!("CTR decryption failed: {}", e))?
369→        } else {
370→            let iv = hex::decode(iv_hex)
371→                .map_err(|_| "Invalid file IV hex".to_string())?;
372→            let iv_arr: [u8; 12] = iv.try_into()
373→                .map_err(|_| "Invalid GCM IV length (expected 12)".to_string())?;
374→            crate::crypto::aes::decrypt_aes_gcm(&encrypted_bytes, &file_key_arr, &iv_arr)
375→                .map_err(|e| format!("GCM decryption failed: {}", e))?
376→        };
377→
378→        crate::crypto::utils::clear_bytes(&mut file_key);
379→        Ok(plaintext)
380→    }
