382→    impl Filesystem for CipherBoxFS {
383→        /// Initialize the filesystem.
384→        ///
385→        /// Root folder is pre-populated in mount_filesystem() before the FUSE
386→        /// event loop starts. No network I/O happens here — FUSE-T's NFS layer
387→        /// requires fast init() responses to avoid connection timeouts.
388→        fn init(
389→            &mut self,
390→            _req: &Request<'_>,
391→            _config: &mut fuser::KernelConfig,
392→        ) -> Result<(), libc::c_int> {
393→            log::info!("CipherBoxFS::init (root pre-populated, no network I/O)");
394→            log::info!("Root IPNS name: {}", self.root_ipns_name);
395→            log::info!("Inode count: {}", self.inodes.inodes.len());
396→            Ok(())
397→        }
398→
399→        /// Clean up all caches and zeroize sensitive data on unmount.
400→        fn destroy(&mut self) {
401→            use zeroize::Zeroize;
402→
403→            self.content_cache.clear();
404→            self.metadata_cache.clear();
405→
406→            // Zeroize pending_content values
407→            for (_, content) in self.pending_content.iter_mut() {
408→                content.zeroize();
409→            }
410→            self.pending_content.clear();
411→
412→            // Zeroize open file handles' cached content
413→            for (_, handle) in self.open_files.iter_mut() {
414→                if let Some(ref mut c) = handle.cached_content {
415→                    c.zeroize();
416→                }
417→            }
418→            self.open_files.clear();
419→
420→            log::info!("CipherBoxFS destroyed: all caches zeroized");
421→        }
422→
423→        /// Look up a child by name within a parent directory.
424→        fn lookup(
425→            &mut self,
426→            _req: &Request<'_>,
427→            parent: u64,
428→            name: &OsStr,
429→            reply: ReplyEntry,
430→        ) {
431→            self.drain_upload_completions();
432→            self.drain_refresh_completions();
433→
434→            let name_str = match name.to_str() {
435→                Some(n) => n,
436→                None => {
437→                    reply.error(libc::ENOENT);
438→                    return;
439→                }
440→            };
441→
442→            // Handle "." and ".." — NFS clients rely on these working.
443→            // Returning ENOENT for ".." causes the NFS client to disconnect.
444→            if name_str == "." {
445→                if let Some(inode) = self.inodes.get(parent) {
446→                    reply.entry(&ttl_for(inode.attr.kind), &inode.attr, 0);
447→                    return;
448→                }
449→            }

