540→        /// Return file attributes for an inode.
541→        fn getattr(
542→            &mut self,
543→            _req: &Request<'_>,
544→            ino: u64,
545→            _fh: Option<u64>,
546→            reply: ReplyAttr,
547→        ) {
548→            self.drain_upload_completions();
549→
550→            if let Some(inode) = self.inodes.get(ino) {
551→                reply.attr(&ttl_for(inode.attr.kind), &inode.attr);
552→            } else {
553→                reply.error(libc::ENOENT);
554→            }
555→        }
556→
557→        /// Set file attributes (handles truncate via size parameter).
558→        ///
559→        /// Per RESEARCH.md pitfall 4: don't try to independently set atime/mtime
560→        /// on FUSE-T -- only handle the size (truncate) operation.
561→        fn setattr(
562→            &mut self,
563→            _req: &Request<'_>,
564→            ino: u64,
565→            _mode: Option<u32>,
566→            _uid: Option<u32>,
567→            _gid: Option<u32>,
568→            size: Option<u64>,
569→            _atime: Option<fuser::TimeOrNow>,
570→            _mtime: Option<fuser::TimeOrNow>,
571→            _ctime: Option<SystemTime>,
572→            fh: Option<u64>,
573→            _crtime: Option<SystemTime>,
574→            _chgtime: Option<SystemTime>,
575→            _bkuptime: Option<SystemTime>,
576→            _flags: Option<u32>,
577→            reply: ReplyAttr,
578→        ) {
579→            // Handle truncate if size is specified
580→            if let Some(new_size) = size {
581→                // Truncate temp file if file handle exists
582→                if let Some(fh_id) = fh {
583→                    if let Some(handle) = self.open_files.get_mut(&fh_id) {
584→                        if handle.temp_path.is_some() {
585→                            if let Err(e) = handle.truncate(new_size) {
586→                                log::error!("Truncate failed for ino {}: {}", ino, e);
587→                                reply.error(libc::EIO);
588→                                return;
589→                            }
590→                            handle.dirty = true;
591→                        }
592→                    }
593→                }
594→
595→                // Update inode size
596→                if let Some(inode) = self.inodes.get_mut(ino) {
597→                    inode.attr.size = new_size;
598→                    inode.attr.blocks = (new_size + 511) / 512;
599→                    inode.attr.mtime = SystemTime::now();
600→
601→                    // Also update InodeKind::File size
602→                    if let InodeKind::File { size: ref mut s, .. } = inode.kind {
603→                        *s = new_size;
604→                    }
605→
606→                    reply.attr(&ttl_for(inode.attr.kind), &inode.attr);
607→                    return;
608→                }
609→            }
610→
611→            // For other setattr calls, just return current attributes
612→            if let Some(inode) = self.inodes.get(ino) {
613→                reply.attr(&ttl_for(inode.attr.kind), &inode.attr);
614→            } else {
615→                reply.error(libc::ENOENT);
616→            }
617→        }
618→
619→        /// List directory entries.
620→        ///
621→        /// IMPORTANT: Returns ALL entries in a single pass (FUSE-T requirement per pitfall 1).
622→        /// Never paginate readdir results.
623→        fn readdir(
624→            &mut self,
625→            _req: &Request<'_>,
626→            ino: u64,
627→            _fh: u64,
628→            offset: i64,
629→            mut reply: ReplyDirectory,
630→        ) {
631→            // 1. Drain any pending background refresh results (non-blocking)
632→            self.drain_refresh_completions();
633→
634→            // 2. Check if metadata is stale — fire background refresh if so
635→            let stale_info: Option<(String, zeroize::Zeroizing<Vec<u8>>)> = {
636→                let inode = match self.inodes.get(ino) {
637→                    Some(i) => i,
638→                    None => {
639→                        reply.error(libc::ENOENT);
640→                        return;
641→                    }
642→                };
643→
644→                match &inode.kind {
645→                    InodeKind::Root { ipns_name, .. } => {
646→                        ipns_name.as_ref().and_then(|name| {
647→                            if self.metadata_cache.get(name).is_none() {
648→                                Some((name.clone(), self.root_folder_key.clone()))
649→                            } else {
650→                                None
651→                            }
652→                        })
653→                    }
654→                    InodeKind::Folder { ipns_name, folder_key, .. } => {
655→                        if self.metadata_cache.get(ipns_name).is_none() {
656→                            Some((ipns_name.clone(), folder_key.clone()))
657→                        } else {
658→                            None
659→                        }
660→                    }
661→                    _ => None,
662→                }
663→            };
664→
665→            // Fire background refresh (non-blocking, results applied on next readdir)
666→            // Only on offset=0 to avoid duplicate refreshes (NFS calls readdir twice)
667→            if let Some((ipns_name, folder_key)) = stale_info.filter(|_| offset == 0) {
668→                let api = self.api.clone();
669→                let rt = self.rt.clone();
670→                let tx = self.refresh_tx.clone();
