 1→import type { ReactNode } from 'react';
 2→import { WagmiProvider } from 'wagmi';
 3→import { wagmiConfig } from './config';
 4→
 5→/**
 6→ * WagmiSetup wraps children with wagmi's WagmiProvider.
 7→ *
 8→ * Must be placed OUTSIDE CoreKitProvider and QueryClientProvider
 9→ * in the component tree because wagmi v3 uses React Query internally
10→ * and will discover the existing QueryClient from context.
11→ */
12→export function WagmiSetup({ children }: { children: ReactNode }) {
13→  return <WagmiProvider config={wagmiConfig}>{children}</WagmiProvider>;
14→}
