 1→import { useAuth } from '../../hooks/useAuth';
 2→
 3→interface AuthButtonProps {
 4→  apiDown?: boolean;
 5→}
 6→
 7→/**
 8→ * Sign In button that opens Web3Auth modal.
 9→ * Disables with [API OFFLINE] text when API health check fails.
10→ *
11→ * @deprecated Replaced by GoogleLoginButton + EmailLoginForm in Phase 12.
12→ * This component is no longer used from Login.tsx. Kept for backward
13→ * compatibility until Plan 05 cleanup removes it entirely.
14→ */
15→export function AuthButton({ apiDown }: AuthButtonProps) {
16→  const { login, isLoading } = useAuth();
17→
18→  const handleClick = async () => {
19→    try {
20→      await login();
21→    } catch {
22→      // Error is already logged in useAuth
23→      // User can see the error in Web3Auth modal
24→    }
25→  };
26→
27→  return (
28→    <button
29→      onClick={handleClick}
30→      disabled={isLoading || apiDown}
31→      className={['login-button', apiDown ? 'login-button--api-down' : '']
32→        .filter(Boolean)
33→        .join(' ')}
34→    >
35→      {isLoading ? 'connecting...' : apiDown ? '[API OFFLINE]' : '[CONNECT]'}
36→    </button>
37→  );
38→}
