  1→import { apiClient } from './client';
  2→
  3→type LoginRequest = {
  4→  idToken: string;
  5→  publicKey: string;
  6→  loginType: 'corekit';
  7→};
  8→
  9→type LoginResponse = {
 10→  accessToken: string;
 11→  isNewUser: boolean;
 12→};
 13→
 14→type TokenResponse = {
 15→  accessToken: string;
 16→  email?: string;
 17→};
 18→
 19→/** Response from CipherBox identity provider endpoints */
 20→type IdentityTokenResponse = {
 21→  idToken: string;
 22→  userId: string;
 23→  isNewUser: boolean;
 24→  email?: string;
 25→};
 26→
 27→export type AuthMethod = {
 28→  id: string;
 29→  type: 'google' | 'apple' | 'github' | 'email' | 'wallet';
 30→  identifier: string;
 31→  lastUsedAt: string | null;
 32→  createdAt: string;
 33→};
 34→
 35→type LinkMethodRequest = {
 36→  idToken: string;
 37→  loginType: 'google' | 'email' | 'wallet';
 38→  walletAddress?: string;
 39→  siweMessage?: string;
 40→  siweSignature?: string;
 41→};
 42→
 43→export const authApi = {
 44→  /**
 45→   * Authenticate user with Web3Auth ID token.
 46→   * Backend verifies the idToken with appropriate JWKS endpoint based on loginType.
 47→   * Refresh token is stored in HTTP-only cookie automatically.
 48→   */
 49→  login: async (data: LoginRequest): Promise<LoginResponse> => {
 50→    const response = await apiClient.post<LoginResponse>('/auth/login', data);
 51→    return response.data;
 52→  },
 53→
 54→  /**
 55→   * Refresh access token using HTTP-only refresh token cookie.
 56→   * The refresh token is automatically sent via withCredentials: true.
 57→   * New refresh token is set in HTTP-only cookie by the backend.
 58→   */
 59→  refresh: async (): Promise<TokenResponse> => {
 60→    const response = await apiClient.post<TokenResponse>('/auth/refresh');
 61→    return response.data;
 62→  },
 63→
 64→  /**
 65→   * Logout and invalidate refresh token.
 66→   * Clears HTTP-only cookie on backend.
 67→   */
 68→  logout: async (): Promise<void> => {
 69→    await apiClient.post('/auth/logout');
 70→  },
 71→
 72→  /**
 73→   * Get all linked auth methods for the current user.
 74→   */
 75→  getMethods: async (): Promise<AuthMethod[]> => {
 76→    const response = await apiClient.get<AuthMethod[]>('/auth/methods');
 77→    return response.data;
 78→  },
 79→
 80→  /**
 81→   * Link a new auth method to the current user account.
 82→   * The new auth method's publicKey must match the user's publicKey
 83→   * (via Web3Auth group connections).
 84→   */
 85→  linkMethod: async (data: LinkMethodRequest): Promise<AuthMethod[]> => {
 86→    const response = await apiClient.post<AuthMethod[]>('/auth/link', data);
 87→    return response.data;
 88→  },
 89→
 90→  /**
 91→   * Unlink an auth method from the current user account.
 92→   * Cannot unlink the last remaining auth method.
 93→   */
 94→  unlinkMethod: async (methodId: string): Promise<void> => {
 95→    await apiClient.post('/auth/unlink', { methodId });
 96→  },
 97→
 98→  // --- CipherBox Identity Provider endpoints (Plan 12-01) ---
 99→
100→  /** Get CipherBox identity JWT via Google OAuth token */
101→  identityGoogle: async (
102→    googleIdToken: string,
103→    intent?: 'login' | 'link'
104→  ): Promise<IdentityTokenResponse> => {
105→    const response = await apiClient.post<IdentityTokenResponse>('/auth/identity/google', {
106→      idToken: googleIdToken,
107→      ...(intent && { intent }),
108→    });
109→    return response.data;
110→  },
111→
112→  /** Send email OTP */
113→  identityEmailSendOtp: async (email: string): Promise<{ success: boolean }> => {
114→    const response = await apiClient.post<{ success: boolean }>('/auth/identity/email/send-otp', {
115→      email,
116→    });
117→    return response.data;
118→  },
119→
120→  /** Verify email OTP and get CipherBox identity JWT */
121→  identityEmailVerify: async (
122→    email: string,
123→    otp: string,
124→    intent?: 'login' | 'link'
125→  ): Promise<IdentityTokenResponse> => {
126→    const response = await apiClient.post<IdentityTokenResponse>(
127→      '/auth/identity/email/verify-otp',
128→      {
129→        email,
130→        otp,
131→        ...(intent && { intent }),
132→      }
133→    );
134→    return response.data;
135→  },
136→
137→  /** Get a SIWE nonce for wallet login */
138→  identityWalletNonce: async (): Promise<{ nonce: string }> => {
139→    const response = await apiClient.get<{ nonce: string }>('/auth/identity/wallet/nonce');
140→    return response.data;
141→  },
142→
143→  /** Verify SIWE wallet signature and get CipherBox identity JWT */
144→  identityWalletVerify: async (data: {
145→    message: string;
146→    signature: string;
147→  }): Promise<IdentityTokenResponse> => {
148→    const response = await apiClient.post<IdentityTokenResponse>('/auth/identity/wallet', data);
149→    return response.data;
150→  },
151→};
