  1→import {
  2→  Controller,
  3→  Get,
  4→  Post,
  5→  Body,
  6→  Header,
  7→  HttpCode,
  8→  HttpStatus,
  9→  Logger,
 10→  UnauthorizedException,
 11→  OnModuleDestroy,
 12→} from '@nestjs/common';
 13→import { ApiTags, ApiOperation, ApiResponse } from '@nestjs/swagger';
 14→import { Throttle, ThrottlerGuard } from '@nestjs/throttler';
 15→import { UseGuards } from '@nestjs/common';
 16→import { InjectRepository } from '@nestjs/typeorm';
 17→import { Repository } from 'typeorm';
 18→import { ConfigService } from '@nestjs/config';
 19→import Redis from 'ioredis';
 20→import { parseSiweMessage } from 'viem/siwe';
 21→import { JwtIssuerService } from '../services/jwt-issuer.service';
 22→import { GoogleOAuthService } from '../services/google-oauth.service';
 23→import { EmailOtpService } from '../services/email-otp.service';
 24→import { SiweService } from '../services/siwe.service';
 25→import { User } from '../entities/user.entity';
 26→import { AuthMethod } from '../entities/auth-method.entity';
 27→import {
 28→  GoogleLoginDto,
 29→  SendOtpDto,
 30→  VerifyOtpDto,
 31→  WalletVerifyDto,
 32→  IdentityTokenResponseDto,
 33→  SendOtpResponseDto,
 34→} from '../dto/identity.dto';
 35→
 36→@ApiTags('Identity')
 37→@Controller('auth')
 38→export class IdentityController implements OnModuleDestroy {
 39→  private readonly logger = new Logger(IdentityController.name);
 40→  private readonly redis: Redis;
 41→
 42→  constructor(
 43→    private jwtIssuerService: JwtIssuerService,
 44→    private googleOAuthService: GoogleOAuthService,
 45→    private emailOtpService: EmailOtpService,
 46→    private siweService: SiweService,
 47→    private configService: ConfigService,
 48→    @InjectRepository(User)
 49→    private userRepository: Repository<User>,
 50→    @InjectRepository(AuthMethod)
 51→    private authMethodRepository: Repository<AuthMethod>
 52→  ) {
 53→    this.redis = new Redis({
 54→      host: configService.get('REDIS_HOST', 'localhost'),
 55→      port: configService.get<number>('REDIS_PORT', 6379),
 56→      password: configService.get('REDIS_PASSWORD', undefined),
 57→      lazyConnect: true,
 58→    });
 59→  }
 60→
 61→  async onModuleDestroy(): Promise<void> {
 62→    await this.redis.quit();
 63→  }
 64→
 65→  @Get('.well-known/jwks.json')
 66→  @Header('Cache-Control', 'public, max-age=3600')
 67→  @ApiOperation({ summary: 'JWKS endpoint for CipherBox identity provider' })
 68→  @ApiResponse({
 69→    status: 200,
 70→    description: 'JWKS containing RS256 public key for JWT verification',
 71→    schema: {
 72→      type: 'object',
 73→      properties: { keys: { type: 'array', items: { type: 'object' } } },
 74→      required: ['keys'],
 75→    },
 76→  })
 77→  getJwks(): { keys: object[] } {
 78→    return this.jwtIssuerService.getJwksData();
 79→  }
 80→
 81→  @Post('identity/google')
 82→  @HttpCode(HttpStatus.OK)
 83→  @ApiOperation({
 84→    summary: 'Login with Google OAuth token, returns CipherBox identity JWT',
 85→  })
 86→  @ApiResponse({
 87→    status: 200,
 88→    description: 'CipherBox JWT issued',
 89→    type: IdentityTokenResponseDto,
 90→  })
 91→  @ApiResponse({ status: 401, description: 'Invalid Google token' })
 92→  async googleLogin(@Body() dto: GoogleLoginDto): Promise<IdentityTokenResponseDto> {
 93→    // 1. Verify Google token
 94→    const googlePayload = await this.googleOAuthService.verifyGoogleToken(dto.idToken);
 95→    const normalizedEmail = googlePayload.email?.toLowerCase().trim() ?? '';
 96→
 97→    // 2. Link intent: just verify ownership and issue JWT (no user creation)
 98→    if (dto.intent === 'link') {
 99→      const idToken = await this.jwtIssuerService.signIdentityJwt(
100→        'link-verification',
101→        normalizedEmail
102→      );
103→      this.logger.log(`Google link verification: email=${normalizedEmail}`);
104→      return { idToken, userId: '', isNewUser: false, email: normalizedEmail };
105→    }
106→
107→    // 3. Hash Google sub (immutable user ID) -- NOT email (which can change)
108→    const identifierHash = this.siweService.hashIdentifier(googlePayload.sub);
109→
110→    // 4. Find or create user by hashed identifier
111→    const { user, isNewUser } = await this.findOrCreateUserByIdentifier(
112→      identifierHash,
113→      this.siweService.truncateEmail(normalizedEmail),
114→      'google'
115→    );
116→
117→    // 5. Sign CipherBox identity JWT (include email for auth method identifier)
118→    const idToken = await this.jwtIssuerService.signIdentityJwt(user.id, normalizedEmail);
119→
120→    this.logger.log(`Google login: userId=${user.id}, isNew=${isNewUser}`);
121→
122→    return {
123→      idToken,
124→      userId: user.id,
125→      isNewUser,
126→      email: googlePayload.email,
127→    };
128→  }
129→
130→  @Post('identity/email/send-otp')
131→  @HttpCode(HttpStatus.OK)
132→  @UseGuards(ThrottlerGuard)
133→  @Throttle({ default: { limit: 5, ttl: 900000 } }) // 5 requests per 15 min per IP
134→  @ApiOperation({ summary: 'Send OTP to email address' })
135→  @ApiResponse({
136→    status: 200,
137→    description: 'OTP sent',
138→    type: SendOtpResponseDto,
139→  })
140→  @ApiResponse({
141→    status: 400,
142→    description: 'Invalid email or rate limit exceeded',
143→  })
144→  async sendOtp(@Body() dto: SendOtpDto): Promise<SendOtpResponseDto> {
145→    await this.emailOtpService.sendOtp(dto.email);
146→    return { success: true };
147→  }
148→
149→  @Post('identity/email/verify-otp')
150→  @HttpCode(HttpStatus.OK)
151→  @UseGuards(ThrottlerGuard)
152→  @Throttle({ default: { limit: 5, ttl: 900000 } }) // 5 requests per 15 min per IP
153→  @ApiOperation({
154→    summary: 'Verify email OTP and return CipherBox identity JWT',
155→  })
156→  @ApiResponse({
157→    status: 200,
158→    description: 'OTP verified, JWT issued',
159→    type: IdentityTokenResponseDto,
160→  })
161→  @ApiResponse({ status: 401, description: 'Invalid or expired OTP' })
162→  async verifyOtp(@Body() dto: VerifyOtpDto): Promise<IdentityTokenResponseDto> {
163→    // 1. Verify OTP
164→    await this.emailOtpService.verifyOtp(dto.email, dto.otp);
165→
166→    // 2. Normalize email
167→    const normalizedEmail = dto.email.toLowerCase().trim();
168→
169→    // 3. Link intent: just verify ownership and issue JWT (no user creation)
170→    if (dto.intent === 'link') {
171→      const idToken = await this.jwtIssuerService.signIdentityJwt(
172→        'link-verification',
173→        normalizedEmail
174→      );
175→      this.logger.log(`Email link verification: email=${normalizedEmail}`);
176→      return { idToken, userId: '', isNewUser: false, email: normalizedEmail };
177→    }
178→
179→    // 4. Hash for identifier lookup
180→    const identifierHash = this.siweService.hashIdentifier(normalizedEmail);
181→
182→    // 5. Find or create user by hashed identifier
183→    const { user, isNewUser } = await this.findOrCreateUserByIdentifier(
184→      identifierHash,
185→      this.siweService.truncateEmail(normalizedEmail),
186→      'email'
187→    );
188→
189→    // 6. Sign CipherBox identity JWT (include email for auth method identifier)
190→    const idToken = await this.jwtIssuerService.signIdentityJwt(user.id, normalizedEmail);
191→
192→    this.logger.log(`Email OTP login: userId=${user.id}, isNew=${isNewUser}`);
193→
194→    return {
195→      idToken,
196→      userId: user.id,
197→      isNewUser,
198→      email: normalizedEmail,
199→    };
200→  }
