202→  @Get('identity/wallet/nonce')
203→  @HttpCode(HttpStatus.OK)
204→  @UseGuards(ThrottlerGuard)
205→  @Throttle({ default: { limit: 10, ttl: 60000 } }) // 10 per 60s per IP
206→  @ApiOperation({ summary: 'Generate SIWE nonce for wallet login' })
207→  @ApiResponse({
208→    status: 200,
209→    description: 'Nonce generated',
210→    schema: {
211→      type: 'object',
212→      properties: { nonce: { type: 'string' } },
213→      required: ['nonce'],
214→    },
215→  })
216→  async getWalletNonce(): Promise<{ nonce: string }> {
217→    const nonce = this.siweService.generateNonce();
218→    // Store nonce in Redis with 5min TTL
219→    await this.redis.set(`siwe:nonce:${nonce}`, '1', 'EX', 300);
220→    return { nonce };
221→  }
222→
223→  @Post('identity/wallet')
224→  @HttpCode(HttpStatus.OK)
225→  @UseGuards(ThrottlerGuard)
226→  @Throttle({ default: { limit: 5, ttl: 900000 } }) // 5 per 15min per IP
227→  @ApiOperation({
228→    summary: 'Verify SIWE wallet signature and return CipherBox identity JWT',
229→  })
230→  @ApiResponse({
231→    status: 200,
232→    description: 'Wallet verified, JWT issued',
233→    type: IdentityTokenResponseDto,
234→  })
235→  @ApiResponse({ status: 401, description: 'Invalid nonce or signature' })
236→  async walletLogin(@Body() dto: WalletVerifyDto): Promise<IdentityTokenResponseDto> {
237→    // 1. Parse the message to extract nonce
238→    const parsed = parseSiweMessage(dto.message);
239→    if (!parsed.nonce) {
240→      throw new UnauthorizedException('Invalid SIWE message: missing nonce');
241→    }
242→
243→    // 2. Consume nonce from Redis (single-use)
244→    const nonceKey = `siwe:nonce:${parsed.nonce}`;
245→    let nonceDeleted: number;
246→    try {
247→      nonceDeleted = await this.redis.del(nonceKey);
248→    } catch (err) {
249→      this.logger.error('Redis error during nonce consumption', err);
250→      throw new UnauthorizedException('Nonce verification failed');
251→    }
252→    if (!nonceDeleted) {
253→      throw new UnauthorizedException('Invalid or expired nonce');
254→    }
255→
256→    // 3. Determine domain for validation
257→    const domain = this.configService.get<string>('SIWE_DOMAIN', 'localhost');
258→
259→    // 4. Verify SIWE message + signature
260→    const walletAddress = await this.siweService.verifySiweMessage(
261→      dto.message,
262→      dto.signature as `0x${string}`,
263→      parsed.nonce,
264→      domain
265→    );
266→
267→    // 5. Link intent: just verify ownership and issue JWT (no user creation)
268→    if (dto.intent === 'link') {
269→      const idToken = await this.jwtIssuerService.signIdentityJwt('link-verification');
270→      this.logger.log(`Wallet link verification: address=${walletAddress}`);
271→      return { idToken, userId: '', isNewUser: false };
272→    }
273→
274→    // 6. Find or create user by wallet address hash
275→    const addressHash = this.siweService.hashWalletAddress(walletAddress);
276→    const truncated = this.siweService.truncateWalletAddress(walletAddress);
277→    const { user, isNewUser } = await this.findOrCreateUserByIdentifier(
278→      addressHash,
279→      truncated,
280→      'wallet'
281→    );
282→
283→    // 7. Issue CipherBox JWT (sub=userId)
284→    const idToken = await this.jwtIssuerService.signIdentityJwt(user.id);
285→
286→    this.logger.log(`Wallet login: userId=${user.id}, isNew=${isNewUser}`);
287→
288→    return { idToken, userId: user.id, isNewUser };
289→  }
290→
291→  /**
292→   * Find an existing user by hashed identifier in the AuthMethod table, or create a new user.
293→   *
294→   * Each auth method type is independent -- no cross-method email matching.
295→   * Users link methods explicitly via Settings, not auto-linked by email.
296→   *
297→   * For identity provider flow, the publicKey is set to a placeholder because
298→   * the actual MPC-derived publicKey is not known until the client completes
299→   * Core Kit login with the CipherBox JWT.
300→   */
301→  private async findOrCreateUserByIdentifier(
302→    identifierHash: string,
303→    identifierDisplay: string,
304→    authMethodType: 'google' | 'email' | 'wallet'
305→  ): Promise<{ user: User; isNewUser: boolean }> {
306→    // Look for existing auth method with this hash
307→    const existingMethod = await this.authMethodRepository.findOne({
308→      where: {
309→        type: authMethodType,
310→        identifierHash,
311→      },
312→      relations: ['user'],
313→    });
314→
315→    if (existingMethod) {
316→      // Update last used timestamp
317→      existingMethod.lastUsedAt = new Date();
318→      await this.authMethodRepository.save(existingMethod);
319→      return { user: existingMethod.user, isNewUser: false };
320→    }
321→
322→    // No cross-method linking -- create new user with unique temporary publicKey
323→    const tempId = `${Date.now()}-${Math.random().toString(36).slice(2)}`;
324→    const newUser = await this.userRepository.save({
325→      publicKey: `pending-core-kit-${tempId}`,
326→    });
327→    // Update placeholder with actual userId now that we have it
328→    newUser.publicKey = `pending-core-kit-${newUser.id}`;
329→    await this.userRepository.save(newUser);
330→
331→    // Create auth method with hash-based identifier
332→    await this.authMethodRepository.save({
333→      userId: newUser.id,
334→      type: authMethodType,
335→      identifier: identifierHash,
336→      identifierHash,
337→      identifierDisplay,
338→      lastUsedAt: new Date(),
339→    });
340→
341→    return { user: newUser, isNewUser: true };
342→  }
343→}
