88→createRoot(rootElement).render(
89→  <StrictMode>
90→    <WagmiSetup>
91→      <CoreKitProvider>
92→        <QueryClientProvider client={queryClient}>
93→          <App />
94→        </QueryClientProvider>
95→      </CoreKitProvider>
96→    </WagmiSetup>
97→  </StrictMode>
98→);
