  1→import { useCallback } from 'react';
  2→import * as secp256k1 from '@noble/secp256k1';
  3→import { hexToBytes, bytesToHex } from '@cipherbox/crypto';
  4→import { useCoreKit } from './core-kit-provider';
  5→import { COREKIT_STATUS } from './core-kit';
  6→import { authApi } from '../api/auth';
  7→
  8→export type CoreKitLoginResult = 'logged_in' | 'required_share';
  9→
 10→export function useCoreKitAuth() {
 11→  const { coreKit, status, isLoggedIn, isInitialized, isRequiredShare, syncStatus } = useCoreKit();
 12→
 13→  /**
 14→   * Login with Google: Backend verifies Google idToken, issues CipherBox JWT,
 15→   * then we call loginWithJWT on Core Kit.
 16→   */
 17→  const loginWithGoogle = useCallback(
 18→    async (
 19→      googleIdToken: string
 20→    ): Promise<{
 21→      cipherboxJwt: string;
 22→      email?: string;
 23→      userId: string;
 24→      status: CoreKitLoginResult;
 25→    }> => {
 26→      if (!coreKit) throw new Error('Core Kit not initialized');
 27→
 28→      const { idToken: cipherboxJwt, userId, email } = await authApi.identityGoogle(googleIdToken);
 29→      const coreKitStatus = await doLoginWithCoreKit(coreKit, syncStatus, cipherboxJwt, userId);
 30→      return { cipherboxJwt, email, userId, status: coreKitStatus };
 31→    },
 32→    [coreKit, syncStatus]
 33→  );
 34→
 35→  /**
 36→   * Login with Email: Backend handles OTP send/verify, issues CipherBox JWT.
 37→   */
 38→  const loginWithEmailOtp = useCallback(
 39→    async (
 40→      email: string,
 41→      otp: string
 42→    ): Promise<{ cipherboxJwt: string; userId: string; status: CoreKitLoginResult }> => {
 43→      if (!coreKit) throw new Error('Core Kit not initialized');
 44→
 45→      const { idToken: cipherboxJwt, userId } = await authApi.identityEmailVerify(email, otp);
 46→      const coreKitStatus = await doLoginWithCoreKit(coreKit, syncStatus, cipherboxJwt, userId);
 47→      return { cipherboxJwt, userId, status: coreKitStatus };
 48→    },
 49→    [coreKit, syncStatus]
 50→  );
 51→
 52→  /**
 53→   * Login with Wallet: Backend verifies SIWE signature, issues CipherBox JWT,
 54→   * then we call loginWithJWT on Core Kit.
 55→   */
 56→  const loginWithWallet = useCallback(
 57→    async (
 58→      cipherboxJwt: string,
 59→      userId: string
 60→    ): Promise<{ cipherboxJwt: string; userId: string; status: CoreKitLoginResult }> => {
 61→      if (!coreKit) throw new Error('Core Kit not initialized');
 62→
 63→      const coreKitStatus = await doLoginWithCoreKit(coreKit, syncStatus, cipherboxJwt, userId);
 64→      return { cipherboxJwt, userId, status: coreKitStatus };
 65→    },
 66→    [coreKit, syncStatus]
 67→  );
 68→
 69→  /**
 70→   * Get vault keypair from Core Kit's exported TSS key.
 71→   */
 72→  const getVaultKeypair = useCallback(async (): Promise<{
 73→    publicKey: Uint8Array;
 74→    privateKey: Uint8Array;
 75→  } | null> => {
 76→    if (!coreKit || coreKit.status !== COREKIT_STATUS.LOGGED_IN) return null;
 77→
 78→    try {
 79→      console.log('[CoreKit] _UNSAFE_exportTssKey starting...');
 80→      console.time('[CoreKit] exportTssKey');
 81→      const privateKeyHex = await coreKit._UNSAFE_exportTssKey();
 82→      console.timeEnd('[CoreKit] exportTssKey');
 83→      console.log('[CoreKit] exportTssKey done, hex length:', privateKeyHex.length);
 84→      const privKeyHex = privateKeyHex.startsWith('0x') ? privateKeyHex.slice(2) : privateKeyHex;
 85→      const privateKey = hexToBytes(privKeyHex);
 86→      const publicKey = secp256k1.getPublicKey(privateKey, false); // uncompressed (65 bytes)
 87→      console.log('[CoreKit] keypair derived, pubkey hex length:', publicKey.length * 2);
 88→      return { publicKey, privateKey };
 89→    } catch (err) {
 90→      console.timeEnd('[CoreKit] exportTssKey');
 91→      console.error('[CoreKit] Failed to export TSS key:', err);
 92→      return null;
 93→    }
 94→  }, [coreKit]);
 95→
 96→  /**
 97→   * Get uncompressed public key hex for backend auth.
 98→   */
 99→  const getPublicKeyHex = useCallback(async (): Promise<string | null> => {
100→    const keypair = await getVaultKeypair();
101→    if (!keypair) return null;
102→    return bytesToHex(keypair.publicKey);
103→  }, [getVaultKeypair]);
104→
105→  /**
106→   * Logout from Core Kit.
107→   */
108→  const logout = useCallback(async (): Promise<void> => {
109→    if (!coreKit) return;
110→    try {
111→      if (coreKit.status === COREKIT_STATUS.LOGGED_IN) {
112→        await coreKit.logout();
113→      }
114→    } catch (err) {
115→      console.error('[CoreKit] Logout error:', err);
116→    } finally {
117→      syncStatus();
118→    }
119→  }, [coreKit, syncStatus]);
120→
121→  return {
122→    status,
123→    isLoggedIn,
124→    isInitialized,
125→    isRequiredShare,
126→    loginWithGoogle,
127→    loginWithEmailOtp,
128→    loginWithWallet,
129→    getVaultKeypair,
130→    getPublicKeyHex,
131→    logout,
132→  };
133→}
134→
135→/**
136→ * Shared Core Kit login logic extracted as a standalone function
137→ * (not a hook method) to avoid re-creation on every render.
138→ *
139→ * Takes a CipherBox JWT and userId, calls loginWithJWT and commitChanges.
140→ * Returns 'logged_in' if device factor found, 'required_share' if MFA
141→ * is enabled but this device lacks a factor.
142→ */
143→async function doLoginWithCoreKit(
144→  // eslint-disable-next-line @typescript-eslint/no-explicit-any
145→  coreKit: any,
146→  syncStatus: () => void,
147→  cipherboxJwt: string,
148→  userId: string
149→): Promise<CoreKitLoginResult> {
150→  // eslint-disable-next-line @typescript-eslint/no-explicit-any
151→  const loginParams: any = {
152→    verifier: 'cipherbox-identity',
153→    verifierId: userId,
154→    idToken: cipherboxJwt,
155→  };
156→
157→  console.log('[CoreKit] loginWithJWT starting...', {
158→    verifier: loginParams.verifier,
159→    verifierId: userId,
160→  });
161→  console.time('[CoreKit] loginWithJWT');
162→  try {
163→    await coreKit.loginWithJWT(loginParams);
164→    console.timeEnd('[CoreKit] loginWithJWT');
165→    console.log('[CoreKit] loginWithJWT completed, status:', coreKit.status);
166→  } catch (err) {
167→    console.timeEnd('[CoreKit] loginWithJWT');
168→    console.error('[CoreKit] loginWithJWT FAILED:', err);
169→    throw err;
170→  }
171→
172→  // Handle status
173→  if (coreKit.status === COREKIT_STATUS.LOGGED_IN) {
174→    console.log('[CoreKit] commitChanges starting...');
175→    console.time('[CoreKit] commitChanges');
176→    try {
177→      await coreKit.commitChanges();
178→      console.timeEnd('[CoreKit] commitChanges');
179→      console.log('[CoreKit] commitChanges done');
180→    } catch (err) {
181→      console.timeEnd('[CoreKit] commitChanges');
182→      console.error('[CoreKit] commitChanges FAILED:', err);
183→    }
184→
185→    // DEBUG: Test exportTssKey in isolation BEFORE triggering React updates
186→    console.log('[CoreKit] DEBUG: testing exportTssKey in isolation...');
187→    console.time('[CoreKit] exportTssKey-test');
188→    try {
189→      const testKey = await coreKit._UNSAFE_exportTssKey();
190→      console.timeEnd('[CoreKit] exportTssKey-test');
191→      console.log('[CoreKit] exportTssKey-test succeeded, length:', testKey.length);
192→    } catch (err) {
193→      console.timeEnd('[CoreKit] exportTssKey-test');
194→      console.error('[CoreKit] exportTssKey-test FAILED:', err);
195→    }
196→
197→    console.log('[CoreKit] calling syncStatus...');
198→    syncStatus();
199→    console.log('[CoreKit] syncStatus called, returning logged_in');
200→    return 'logged_in';
201→  }
202→
203→  if (coreKit.status === COREKIT_STATUS.REQUIRED_SHARE) {
204→    syncStatus();
205→    return 'required_share';
206→  }
207→
208→  throw new Error(`Unexpected Core Kit status: ${coreKit.status}`);
209→}
