 1→import { create } from 'zustand';
 2→
 3→/**
 4→ * IPNS keypair for folder metadata signing.
 5→ * Ed25519 keypair for signing IPNS records.
 6→ */
 7→type IpnsKeypair = {
 8→  publicKey: Uint8Array;
 9→  privateKey: Uint8Array;
10→};
11→
12→type VaultState = {
13→  // Decrypted vault keys (memory-only)
14→  rootFolderKey: Uint8Array | null;
15→  rootIpnsKeypair: IpnsKeypair | null;
16→  rootIpnsName: string | null;
17→
18→  // Vault metadata from server
19→  vaultId: string | null;
20→  isInitialized: boolean;
21→  isNewVault: boolean;
22→
23→  // Actions
24→  setVaultKeys: (keys: {
25→    rootFolderKey: Uint8Array;
26→    rootIpnsKeypair: IpnsKeypair;
27→    rootIpnsName: string;
28→    vaultId: string;
29→    isNewVault?: boolean;
30→  }) => void;
31→  clearVaultKeys: () => void;
32→};
33→
34→/**
35→ * Vault store for managing decrypted vault keys in memory.
36→ *
37→ * Keys are set after vault initialization/retrieval:
38→ * 1. User logs in via Web3Auth (auth.store.ts provides user's Ed25519 keypair)
39→ * 2. Login flow fetches vault from backend API (GET /vault)
40→ * 3. If vault exists: decrypt rootFolderKeyEncrypted and rootIpnsPrivateKeyEncrypted using user's private key
41→ * 4. If vault is new: generate new keys, encrypt with user's public key, POST /vault
42→ * 5. Call vaultStore.setVaultKeys() with decrypted keys
43→ * 6. On logout: auth.store.ts should call vaultStore.clearVaultKeys() before clearing auth state
44→ *
45→ * SECURITY: All keys are memory-only - never persisted to localStorage/sessionStorage.
46→ */
47→export const useVaultStore = create<VaultState>((set, get) => ({
48→  // State
49→  rootFolderKey: null,
50→  rootIpnsKeypair: null,
51→  rootIpnsName: null,
52→  vaultId: null,
53→  isInitialized: false,
54→  isNewVault: false,
55→
56→  // Actions
57→  setVaultKeys: (keys) =>
58→    set({
59→      rootFolderKey: keys.rootFolderKey,
60→      rootIpnsKeypair: keys.rootIpnsKeypair,
61→      rootIpnsName: keys.rootIpnsName,
62→      vaultId: keys.vaultId,
63→      isInitialized: true,
64→      isNewVault: keys.isNewVault ?? false,
65→    }),
66→
67→  // [SECURITY: MEDIUM-02] Zero-fill sensitive key material before clearing
68→  clearVaultKeys: () => {
69→    const state = get();
70→
71→    // Best-effort memory clearing - overwrite with zeros
72→    if (state.rootFolderKey) {
73→      state.rootFolderKey.fill(0);
74→    }
75→
76→    if (state.rootIpnsKeypair) {
77→      if (state.rootIpnsKeypair.privateKey) {
78→        state.rootIpnsKeypair.privateKey.fill(0);
79→      }
80→      if (state.rootIpnsKeypair.publicKey) {
81→        state.rootIpnsKeypair.publicKey.fill(0);
82→      }
83→    }
84→
85→    set({
86→      rootFolderKey: null,
87→      rootIpnsKeypair: null,
88→      rootIpnsName: null,
89→      vaultId: null,
90→      isInitialized: false,
91→      isNewVault: false,
92→    });
93→  },
94→}));
95→

 1→import { create } from 'zustand';
 2→
 3→type TeeKeys = {
 4→  currentEpoch: number;
 5→  currentPublicKey: string;
 6→  previousEpoch: number | null;
 7→  previousPublicKey: string | null;
 8→};
 9→
10→/**
11→ * User's secp256k1 vault keypair from Core Kit TSS export.
12→ * Memory-only, never persisted.
13→ */
14→type VaultKeypair = {
15→  publicKey: Uint8Array;
16→  privateKey: Uint8Array;
17→};
18→
19→type AuthState = {
20→  accessToken: string | null;
21→  isAuthenticated: boolean;
22→  lastAuthMethod: string | null;
23→  userEmail: string | null;
24→  teeKeys: TeeKeys | null;
25→
26→  /** User's vault keypair (memory-only, from Core Kit TSS export) */
27→  vaultKeypair: VaultKeypair | null;
28→
29→  setAccessToken: (token: string) => void;
30→  setLastAuthMethod: (method: string) => void;
31→  setUserEmail: (email: string) => void;
32→  setTeeKeys: (keys: TeeKeys) => void;
33→  setVaultKeypair: (keypair: VaultKeypair) => void;
34→  logout: () => void;
35→};
36→
37→export const useAuthStore = create<AuthState>((set, get) => ({
38→  // State - stored in memory only, NOT localStorage (XSS prevention)
39→  accessToken: null,
40→  isAuthenticated: false,
41→  lastAuthMethod: null,
42→  userEmail: null,
43→  teeKeys: null,
44→
45→  /** User's vault keypair (memory-only) */
46→  vaultKeypair: null,
47→
48→  // Actions
49→  setAccessToken: (token) => set({ accessToken: token, isAuthenticated: true }),
50→  setLastAuthMethod: (method) => set({ lastAuthMethod: method }),
51→  setUserEmail: (email) => set({ userEmail: email }),
52→  setTeeKeys: (keys) => set({ teeKeys: keys }),
53→
54→  /** Store vault keypair from Core Kit TSS export */
55→  setVaultKeypair: (keypair) => set({ vaultKeypair: keypair }),
56→
57→  logout: () => {
58→    // [SECURITY: MEDIUM-02] Zero-fill sensitive key material before clearing
59→    const state = get();
60→    if (state.vaultKeypair) {
61→      if (state.vaultKeypair.privateKey) {
62→        state.vaultKeypair.privateKey.fill(0);
63→      }
64→      if (state.vaultKeypair.publicKey) {
65→        state.vaultKeypair.publicKey.fill(0);
66→      }
67→    }
68→
69→    set({
70→      accessToken: null,
71→      isAuthenticated: false,
72→      userEmail: null,
73→      teeKeys: null,
74→      vaultKeypair: null,
75→    });
76→  },
77→}));
