 4→const environment = import.meta.env.VITE_ENVIRONMENT || 'local';
 5→
 6→const NETWORK_MAP: Record<string, (typeof WEB3AUTH_NETWORK)[keyof typeof WEB3AUTH_NETWORK]> = {
 7→  local: WEB3AUTH_NETWORK.DEVNET,
 8→  ci: WEB3AUTH_NETWORK.DEVNET,
 9→  staging: WEB3AUTH_NETWORK.DEVNET,
10→  production: WEB3AUTH_NETWORK.MAINNET,
11→};
12→
13→let instance: Web3AuthMPCCoreKit | null = null;
14→
15→/**
16→ * Get or create the singleton Core Kit instance.
17→ * Safe to call multiple times -- returns the same instance.
18→ */
19→export function getCoreKit(): Web3AuthMPCCoreKit {
20→  if (!instance && typeof window !== 'undefined') {
21→    instance = new Web3AuthMPCCoreKit({
22→      web3AuthClientId: import.meta.env.VITE_WEB3AUTH_CLIENT_ID,
23→      web3AuthNetwork: NETWORK_MAP[environment] || WEB3AUTH_NETWORK.DEVNET,
24→      storage: window.localStorage,
25→      manualSync: true, // Explicit commitChanges() after mutations
26→      tssLib,
27→    });
28→  }
29→  if (!instance) {
30→    throw new Error('getCoreKit() called outside browser environment');
31→  }
32→  return instance;
33→}
34→
35→/**
36→ * Initialize Core Kit. Checks for existing sessions (auto-login).
37→ * Returns the resulting COREKIT_STATUS after initialization.
38→ */
39→export async function initCoreKit(): Promise<COREKIT_STATUS> {
40→  const ck = getCoreKit();
41→  await ck.init();
42→  return ck.status;
43→}
