 24→export function useAuth() {
 25→  const navigate = useNavigate();
 26→  const {
 27→    isLoggedIn: coreKitLoggedIn,
 28→    isInitialized: coreKitInitialized,
 29→    isRequiredShare,
 30→    loginWithGoogle: coreKitLoginGoogle,
 31→    loginWithEmailOtp: coreKitLoginEmail,
 32→    loginWithWallet: coreKitLoginWallet,
 33→    getVaultKeypair,
 34→    getPublicKeyHex,
 35→    logout: coreKitLogout,
 36→  } = useCoreKitAuth();
 37→  const {
 38→    accessToken,
 39→    isAuthenticated,
 40→    lastAuthMethod,
 41→    userEmail,
 42→    setAccessToken,
 43→    setLastAuthMethod,
 44→    setUserEmail,
 45→    setVaultKeypair,
 46→    logout: clearAuthState,
 47→  } = useAuthStore();
 48→
 49→  const [isLoggingIn, setIsLoggingIn] = useState(false);
 50→  const [isLoggingOut, setIsLoggingOut] = useState(false);
 51→  const restoringRef = useRef(false);
 52→
 53→  // Stable refs for functions used in the session restoration effect.
 54→  // This prevents the effect from re-firing when function references change.
 55→  const initializeOrLoadVaultRef = useRef<(() => Promise<void>) | null>(null);
 56→  const coreKitLogoutRef = useRef<(() => Promise<void>) | null>(null);
 57→
 58→  // Pending auth state for REQUIRED_SHARE flow
 59→  // Stored so completeRequiredShare() can resume after factor input
 60→  const [pendingCipherboxJwt, setPendingCipherboxJwt] = useState<string | null>(null);
 61→  const [pendingAuthMethod, setPendingAuthMethod] = useState<string | null>(null);
 62→
 63→  const isLoading = !coreKitInitialized || isLoggingIn || isLoggingOut;
 64→
 65→  // Get vault store actions
 66→  const setVaultKeys = useVaultStore((state) => state.setVaultKeys);
 67→
 68→  /**
 69→   * Initialize vault for new users or load existing vault keys.
 70→   * Called after successful backend authentication.
 71→   * Uses Core Kit's getVaultKeypair() instead of PnP provider.
 72→   */
 73→  const initializeOrLoadVault = useCallback(async (): Promise<void> => {
 74→    // Get user's secp256k1 keypair from Core Kit TSS export
 75→    const userKeypair = await getVaultKeypair();
 76→
 77→    if (!userKeypair) {
 78→      throw new Error('Failed to get vault keypair from Core Kit');
 79→    }
 80→
 81→    // Store keypair in auth store for crypto operations
 82→    setVaultKeypair(userKeypair);
 83→
 84→    try {
 85→      // Try to fetch existing vault
 86→      const existingVault = await vaultApi.getVault();
 87→
 88→      // Store TEE keys if available
 89→      if (existingVault.teeKeys) {
 90→        useAuthStore.getState().setTeeKeys(existingVault.teeKeys);
 91→      }
 92→
 93→      // Vault exists - decrypt keys and load into store
 94→      // decryptVaultKeys derives the IPNS public key internally from the decrypted private key
 95→      const decryptedVault = await decryptVaultKeys(
 96→        {
 97→          encryptedRootFolderKey: hexToBytes(existingVault.encryptedRootFolderKey),
 98→          encryptedIpnsPrivateKey: hexToBytes(existingVault.encryptedRootIpnsPrivateKey),
 99→        },
100→        userKeypair.privateKey
101→      );
102→
103→      setVaultKeys({
104→        rootFolderKey: decryptedVault.rootFolderKey,
105→        rootIpnsKeypair: decryptedVault.rootIpnsKeypair,
106→        rootIpnsName: existingVault.rootIpnsName,
107→        vaultId: existingVault.id,
108→      });
109→    } catch (error) {
110→      const is404 = (error as { response?: { status?: number } })?.response?.status === 404;
111→
112→      if (is404) {
113→        // New user - initialize vault
114→        console.log(
115→          '[Auth] New user on Core Kit. If migrating from PnP, vault re-initialization may be needed.'
116→        );
117→        const newVault = await initializeVault(userKeypair.privateKey);
118→        const encryptedVault = await encryptVaultKeys(newVault, userKeypair.publicKey);
119→        const rootIpnsName = await deriveIpnsName(newVault.rootIpnsKeypair.publicKey);
120→
121→        const storedVault = await vaultApi.initVault({
122→          ownerPublicKey: bytesToHex(userKeypair.publicKey),
123→          encryptedRootFolderKey: bytesToHex(encryptedVault.encryptedRootFolderKey),
124→          encryptedRootIpnsPrivateKey: bytesToHex(encryptedVault.encryptedIpnsPrivateKey),
125→          rootIpnsName,
126→        });
127→
128→        if (storedVault.teeKeys) {
129→          useAuthStore.getState().setTeeKeys(storedVault.teeKeys);
130→        }
131→
132→        setVaultKeys({
133→          rootFolderKey: newVault.rootFolderKey,
134→          rootIpnsKeypair: newVault.rootIpnsKeypair,
135→          rootIpnsName,
136→          vaultId: storedVault.id,
137→          isNewVault: true,
138→        });
139→      } else {
140→        console.error('[useAuth] Failed to load vault:', error);
141→        throw error;
142→      }
143→    }
144→
145→    // Non-blocking device registry initialization (fire-and-forget)
146→    // Placed after vault load so registry failures never block login
147→    void (async () => {
148→      try {
149→        const deviceKeypair = await getOrCreateDeviceIdentity();
150→        const deviceInfo = detectDeviceInfo();
151→        const result = await initializeOrSyncRegistry({
152→          userPrivateKey: userKeypair.privateKey,
153→          userPublicKey: userKeypair.publicKey,
154→          deviceKeypair,
155→          deviceInfo: { ...deviceInfo, ipHash: '' },
156→        });
157→        if (result) {
158→          useDeviceRegistryStore
159→            .getState()
160→            .setRegistry(result.registry, result.ipnsName, deviceKeypair.deviceId);
161→        }
162→      } catch (error) {
163→        console.error('[Auth] Device registry init failed (non-blocking):', error);
164→      }
165→    })();
166→  }, [getVaultKeypair, setVaultKeypair, setVaultKeys]);
167→
168→  /**
169→   * Complete backend authentication after Core Kit login.
170→   * Sends the CipherBox-issued JWT (which we used for Core Kit loginWithJWT)
171→   * to the backend with loginType 'corekit'. The backend verifies it against
172→   * its own JWKS since CipherBox is the identity provider.
173→   */
174→  const completeBackendAuth = useCallback(
175→    async (authMethod: string, cipherboxJwt: string): Promise<void> => {
176→      // 1. Get real publicKey from Core Kit TSS export
177→      const publicKey = await getPublicKeyHex();
178→      if (!publicKey) {
179→        throw new Error('Failed to get publicKey from Core Kit');
180→      }
181→
182→      // publicKey available for debugging via DevTools if needed
183→
184→      // 2. Authenticate with CipherBox backend
185→      // Backend verifies our CipherBox JWT and resolves placeholder publicKey
186→      const response = await authApi.login({
187→        idToken: cipherboxJwt,
188→        publicKey,
189→        loginType: 'corekit',
190→      });
191→
192→      // 3. Store access token (refresh token in HTTP-only cookie)
193→      setAccessToken(response.accessToken);
194→
195→      // 4. Remember auth method for UX
196→      setLastAuthMethod(authMethod);
197→
198→      // 5. Initialize or load vault
199→      await initializeOrLoadVault();
200→    },
201→    [getPublicKeyHex, setAccessToken, setLastAuthMethod, initializeOrLoadVault]
202→  );
203→
204→  /**
205→   * Complete the login flow after REQUIRED_SHARE is resolved.
206→   * Called after inputFactorKey() succeeds (from recovery phrase or cross-device approval).
207→   * Uses the stored pendingCipherboxJwt to call completeBackendAuth with the REAL publicKey
