210→  const completeRequiredShare = useCallback(async (): Promise<void> => {
211→    const jwt = pendingCipherboxJwt;
212→    const method = pendingAuthMethod;
213→
214→    if (!jwt || !method) {
215→      throw new Error('No pending auth info for REQUIRED_SHARE completion');
216→    }
217→
218→    // Core Kit should now be LOGGED_IN after inputFactorKey()
219→    // Complete backend auth with the REAL publicKey (replaces the placeholder session)
220→    await completeBackendAuth(method, jwt);
221→
222→    // Clear pending state
223→    setPendingCipherboxJwt(null);
224→    setPendingAuthMethod(null);
225→
226→    // Navigate to files
227→    navigate('/files');
228→  }, [pendingCipherboxJwt, pendingAuthMethod, completeBackendAuth, navigate]);
229→
230→  /**
231→   * Login with Google OAuth token.
232→   * Flow: Google idToken -> CipherBox backend -> CipherBox JWT ->
233→   * Core Kit loginWithJWT -> backend /auth/login (corekit type)
234→   */
235→  const loginWithGoogle = useCallback(
236→    async (googleIdToken: string): Promise<void> => {
237→      if (isLoggingIn) return;
238→      setIsLoggingIn(true);
239→      try {
240→        // 1. Core Kit login via CipherBox identity provider
241→        const {
242→          cipherboxJwt,
243→          email,
244→          userId,
245→          status: coreKitStatus,
246→        } = await coreKitLoginGoogle(googleIdToken);
247→
248→        if (coreKitStatus === 'required_share') {
249→          // MFA enabled but device factor missing.
250→          // Store pending auth info for later completion after factor input.
251→          setPendingCipherboxJwt(cipherboxJwt);
252→          setPendingAuthMethod('google');
253→
254→          // Obtain temporary backend access token so the new device can
255→          // call bulletin board API endpoints (device-approval/*).
256→          // Uses placeholder publicKey since Core Kit is in REQUIRED_SHARE
257→          // state and we can't export the TSS key yet.
258→          const tempLoginResponse = await authApi.login({
259→            idToken: cipherboxJwt,
260→            publicKey: `pending-core-kit-${userId}`,
261→            loginType: 'corekit',
262→          });
263→          setAccessToken(tempLoginResponse.accessToken);
264→
265→          if (email) {
266→            setUserEmail(email);
267→          }
268→
269→          // Do NOT call completeBackendAuth() or navigate('/files')
270→          // The component tree will see isRequiredShare === true and
271→          // render recovery/approval UI.
272→          return;
273→        }
274→
275→        // Normal path: Core Kit logged in, proceed as before
276→        // 2. Complete backend auth + vault init
277→        await completeBackendAuth('google', cipherboxJwt);
278→
279→        // 3. Store email for UI display (returned from identity endpoint)
280→        if (email) {
281→          setUserEmail(email);
282→        }
283→
284→        // 4. Navigate to files
285→        navigate('/files');
286→      } catch (error) {
287→        console.error('[useAuth] Google login failed:', error);
288→        throw error;
289→      } finally {
290→        setIsLoggingIn(false);
291→      }
292→    },
293→    [isLoggingIn, coreKitLoginGoogle, completeBackendAuth, setAccessToken, setUserEmail, navigate]
294→  );
295→
296→  /**
297→   * Login with Email OTP.
298→   * Flow: email+otp -> CipherBox backend -> CipherBox JWT ->
299→   * Core Kit loginWithJWT -> backend /auth/login (corekit type)
300→   */
301→  const loginWithEmail = useCallback(
302→    async (email: string, otp: string): Promise<void> => {
303→      if (isLoggingIn) return;
304→      setIsLoggingIn(true);
305→      try {
306→        // 1. Core Kit login via CipherBox identity provider
307→        const { cipherboxJwt, userId, status: coreKitStatus } = await coreKitLoginEmail(email, otp);
308→
309→        if (coreKitStatus === 'required_share') {
310→          setPendingCipherboxJwt(cipherboxJwt);
311→          setPendingAuthMethod('email');
312→
313→          const tempLoginResponse = await authApi.login({
314→            idToken: cipherboxJwt,
315→            publicKey: `pending-core-kit-${userId}`,
316→            loginType: 'corekit',
317→          });
318→          setAccessToken(tempLoginResponse.accessToken);
319→          setUserEmail(email);
320→          return;
321→        }
322→
323→        // Normal path
324→        await completeBackendAuth('email', cipherboxJwt);
325→        setUserEmail(email);
