132→  };
133→}
134→
135→/**
136→ * Shared Core Kit login logic extracted as a standalone function
137→ * (not a hook method) to avoid re-creation on every render.
138→ *
139→ * Takes a CipherBox JWT and userId, calls loginWithJWT and commitChanges.
140→ * Returns 'logged_in' if device factor found, 'required_share' if MFA
141→ * is enabled but this device lacks a factor.
142→ */
143→async function doLoginWithCoreKit(
144→  // eslint-disable-next-line @typescript-eslint/no-explicit-any
145→  coreKit: any,
146→  syncStatus: () => void,
147→  cipherboxJwt: string,
148→  userId: string
149→): Promise<CoreKitLoginResult> {
150→  // eslint-disable-next-line @typescript-eslint/no-explicit-any
151→  const loginParams: any = {
152→    verifier: 'cipherbox-identity',
153→    verifierId: userId,
154→    idToken: cipherboxJwt,
155→  };
156→
157→  console.log('[CoreKit] loginWithJWT starting...', {
158→    verifier: loginParams.verifier,
159→    verifierId: userId,
160→  });
161→  console.time('[CoreKit] loginWithJWT');
162→  try {
163→    await coreKit.loginWithJWT(loginParams);
164→    console.timeEnd('[CoreKit] loginWithJWT');
165→    console.log('[CoreKit] loginWithJWT completed, status:', coreKit.status);
166→  } catch (err) {
167→    console.timeEnd('[CoreKit] loginWithJWT');
168→    console.error('[CoreKit] loginWithJWT FAILED:', err);
169→    throw err;
170→  }
171→
172→  // Handle status
173→  if (coreKit.status === COREKIT_STATUS.LOGGED_IN) {
174→    console.log('[CoreKit] commitChanges starting...');
175→    console.time('[CoreKit] commitChanges');
176→    try {
177→      await coreKit.commitChanges();
178→      console.timeEnd('[CoreKit] commitChanges');
179→      console.log('[CoreKit] commitChanges done');
180→    } catch (err) {
181→      console.timeEnd('[CoreKit] commitChanges');
182→      console.error('[CoreKit] commitChanges FAILED:', err);
183→    }
184→
185→    // DEBUG: Test exportTssKey in isolation BEFORE triggering React updates
186→    console.log('[CoreKit] DEBUG: testing exportTssKey in isolation...');
187→    console.time('[CoreKit] exportTssKey-test');
188→    try {
189→      const testKey = await coreKit._UNSAFE_exportTssKey();
190→      console.timeEnd('[CoreKit] exportTssKey-test');
191→      console.log('[CoreKit] exportTssKey-test succeeded, length:', testKey.length);
192→    } catch (err) {
193→      console.timeEnd('[CoreKit] exportTssKey-test');
194→      console.error('[CoreKit] exportTssKey-test FAILED:', err);
195→    }
196→
197→    console.log('[CoreKit] calling syncStatus...');
198→    syncStatus();
199→    console.log('[CoreKit] syncStatus called, returning logged_in');
200→    return 'logged_in';
201→  }
202→
203→  if (coreKit.status === COREKIT_STATUS.REQUIRED_SHARE) {
204→    syncStatus();
205→    return 'required_share';
206→  }
207→
208→  throw new Error(`Unexpected Core Kit status: ${coreKit.status}`);
209→}
