 1→import {
 2→  createContext,
 3→  useContext,
 4→  useEffect,
 5→  useState,
 6→  useCallback,
 7→  useMemo,
 8→  type ReactNode,
 9→} from 'react';
10→import { getCoreKit, initCoreKit, COREKIT_STATUS } from './core-kit';
11→import type { Web3AuthMPCCoreKit } from './core-kit';
12→
13→interface CoreKitContextValue {
14→  /** The Core Kit singleton instance (null until first render) */
15→  coreKit: Web3AuthMPCCoreKit | null;
16→  /** Current SDK status from the COREKIT_STATUS state machine */
17→  status: COREKIT_STATUS;
18→  /** True when status is anything other than NOT_INITIALIZED */
19→  isInitialized: boolean;
20→  /** True when status is LOGGED_IN (user fully authenticated) */
21→  isLoggedIn: boolean;
22→  /** True when status is REQUIRED_SHARE (MFA challenge needed -- Phase 12.4) */
23→  isRequiredShare: boolean;
24→  /** Initialization error, if any */
25→  error: Error | null;
26→  /** Re-run initialization (e.g., retry after error) */
27→  reinitialize: () => Promise<void>;
28→  /** Sync React state with CoreKit's internal status after login/logout/MFA */
29→  syncStatus: () => void;
30→}
31→
32→const CoreKitContext = createContext<CoreKitContextValue | null>(null);
33→
34→/**
35→ * React context provider for MPC Core Kit.
36→ *
37→ * Initializes Core Kit on mount (calls init() which checks for existing sessions).
38→ * Exposes the singleton instance and COREKIT_STATUS to child components.
39→ * Mounted at the top of the component tree in main.tsx.
40→ */
41→export function CoreKitProvider({ children }: { children: ReactNode }) {
42→  const [status, setStatus] = useState<COREKIT_STATUS>(COREKIT_STATUS.NOT_INITIALIZED);
43→  const [coreKit, setCoreKit] = useState<Web3AuthMPCCoreKit | null>(null);
44→  const [error, setError] = useState<Error | null>(null);
45→
46→  const initialize = useCallback(async () => {
47→    try {
48→      setError(null);
49→      const ck = getCoreKit();
50→      setCoreKit(ck);
51→      const resultStatus = await initCoreKit();
52→      setStatus(resultStatus);
53→    } catch (err) {
54→      console.error('[CoreKit] Initialization failed:', err);
55→      setError(err instanceof Error ? err : new Error(String(err)));
56→    }
57→  }, []);
58→
59→  useEffect(() => {
60→    initialize();
61→  }, [initialize]);
62→
63→  /** Sync React state with CoreKit's internal status. Call after login/logout/MFA. */
64→  const syncStatus = useCallback(() => {
65→    const ck = getCoreKit();
66→    setStatus(ck.status);
67→  }, []);
68→
69→  const value: CoreKitContextValue = useMemo(
70→    () => ({
71→      coreKit,
72→      status,
73→      isInitialized: status !== COREKIT_STATUS.NOT_INITIALIZED,
74→      isLoggedIn: status === COREKIT_STATUS.LOGGED_IN,
75→      isRequiredShare: status === COREKIT_STATUS.REQUIRED_SHARE,
76→      error,
77→      reinitialize: initialize,
78→      syncStatus,
79→    }),
80→    [coreKit, status, error, initialize, syncStatus]
81→  );
82→
83→  return <CoreKitContext.Provider value={value}>{children}</CoreKitContext.Provider>;
84→}
85→
86→/**
87→ * Hook to access Core Kit instance and status from React components.
88→ * Must be used within a CoreKitProvider.
89→ */
90→export function useCoreKit(): CoreKitContextValue {
91→  const context = useContext(CoreKitContext);
92→  if (!context) {
93→    throw new Error('useCoreKit must be used within CoreKitProvider');
94→  }
95→  return context;
96→}
