 13→/**
 14→ * Wallet login button using wagmi for wallet connection and viem for SIWE.
 15→ *
 16→ * Flow:
 17→ * 1. Show "Connect Wallet" button
 18→ * 2. On click, show discovered EIP-6963 wallet connectors
 19→ * 3. User selects connector, wagmi connects, gets address
 20→ * 4. Fetch nonce from backend
 21→ * 5. Create SIWE message with viem
 22→ * 6. Prompt user to sign in wallet
 23→ * 7. Send message+signature to backend for verification
 24→ * 8. Receive CipherBox JWT and call onLogin
 25→ * 9. Disconnect wagmi (no persistent wallet connection needed)
 26→ */
 27→export function WalletLoginButton({ onLogin, disabled }: WalletLoginButtonProps) {
 28→  const { connectors, connectAsync, isPending: isConnecting } = useConnect();
 29→  const { address, isConnected } = useAccount();
 30→  const { signMessageAsync } = useSignMessage();
 31→  const { disconnect } = useDisconnect();
 32→
 33→  const [phase, setPhase] = useState<LoginPhase>('idle');
 34→  const [error, setError] = useState<string | null>(null);
 35→  const [showConnectors, setShowConnectors] = useState(false);
 36→
 37→  // Track whether SIWE flow is in progress to prevent double-triggers
 38→  const siweInProgress = useRef(false);
 39→
 40→  // When wagmi connects and we have an address, trigger the SIWE flow
 41→  useEffect(() => {
 42→    if (isConnected && address && phase === 'connecting' && !siweInProgress.current) {
 43→      siweInProgress.current = true;
 44→      handleSiweFlow(address);
 45→    }
 46→  }, [isConnected, address, phase]);
 47→
 48→  const handleSiweFlow = async (walletAddress: `0x${string}`) => {
 49→    try {
 50→      setPhase('signing');
 51→
 52→      // 1. Get nonce from backend
 53→      const { nonce } = await authApi.identityWalletNonce();
 54→
 55→      // 2. Create SIWE message
 56→      const message = createSiweMessage({
 57→        address: walletAddress,
 58→        chainId: 1,
 59→        domain: window.location.host,
 60→        nonce,
 61→        uri: window.location.origin,
 62→        version: '1',
 63→        statement: 'Sign in to CipherBox encrypted storage',
 64→      });
 65→
 66→      // 3. Sign message with wallet
 67→      const signature = await signMessageAsync({ message });
 68→
 69→      setPhase('verifying');
 70→
 71→      // 4. Verify on backend, get CipherBox JWT
 72→      const { idToken, userId } = await authApi.identityWalletVerify({
 73→        message,
 74→        signature,
 75→      });
 76→
 77→      // 5. Disconnect wagmi (we don't need persistent wallet connection)
 78→      disconnect();
 79→
 80→      // 6. Continue with Core Kit login
 81→      await onLogin(idToken, userId);
 82→
 83→      // Reset state on success
 84→      setPhase('idle');
 85→      setShowConnectors(false);
 86→    } catch (err) {
 87→      const message = err instanceof Error ? err.message : 'Wallet login failed';
 88→
 89→      // Handle user rejection specifically
 90→      if (
 91→        message.includes('User rejected') ||
 92→        message.includes('user rejected') ||
 93→        message.includes('ACTION_REJECTED')
 94→      ) {
 95→        setError('Signature request was rejected');
 96→      } else {
 97→        setError(message);
 98→      }
 99→
100→      // Clean up on failure
101→      disconnect();
102→      setPhase('idle');
103→      setShowConnectors(false);
104→    } finally {
105→      siweInProgress.current = false;
106→    }
107→  };
108→
109→  const handleConnectorClick = useCallback(
110→    async (connector: (typeof connectors)[0]) => {
111→      setError(null);
112→      setPhase('connecting');
113→
114→      try {
115→        await connectAsync({ connector });
116→        // Don't call handleSiweFlow here — the useEffect watching
117→        // isConnected+address will trigger it. Calling it from both
118→        // places causes a race where the user gets two signing prompts
119→        // with different nonces.
120→      } catch (err) {
121→        const msg = err instanceof Error ? err.message : 'Connection failed';
122→
123→        if (
124→          msg.includes('User rejected') ||
125→          msg.includes('user rejected') ||
126→          msg.includes('ACTION_REJECTED')
127→        ) {
128→          setError('Wallet connection was rejected');
129→        } else if (msg.includes('No provider')) {
130→          setError('No wallet detected. Please install MetaMask or another wallet.');
131→        } else {
132→          setError(msg);
133→        }
134→
135→        setPhase('idle');
136→        setShowConnectors(false);
137→        siweInProgress.current = false;
138→      }
139→    },
140→    [connectAsync]
141→  );
142→
143→  const handleButtonClick = () => {
144→    if (phase !== 'idle') return;
145→    setError(null);
146→    setShowConnectors(true);
147→  };
148→
149→  const handleCancel = () => {
150→    setShowConnectors(false);
151→    setError(null);
152→    if (isConnected) {
153→      disconnect();
154→    }
155→    setPhase('idle');
156→    siweInProgress.current = false;
157→  };
158→
159→  const isWorking = phase !== 'idle';
160→  const isDisabled = disabled || isWorking;
161→
162→  const buttonText = () => {
163→    switch (phase) {
164→      case 'connecting':
165→        return 'connecting wallet...';
166→      case 'signing':
167→        return 'sign the message in your wallet...';
168→      case 'verifying':
169→        return 'verifying signature...';
170→      default:
171→        return '[WALLET]';
172→    }
173→  };
174→
175→  // Filter to unique named connectors (EIP-6963 can report duplicates)
176→  const uniqueConnectors = connectors.filter(
177→    (c, i, arr) => arr.findIndex((x) => x.name === c.name) === i
178→  );
179→
180→  return (
181→    <div className="wallet-login-wrapper">
182→      {!showConnectors ? (
183→        <button
184→          type="button"
185→          data-testid="wallet-login-button"
186→          className={['wallet-login-btn', isWorking ? 'wallet-login-btn--loading' : '']
187→            .filter(Boolean)
188→            .join(' ')}
189→          onClick={handleButtonClick}
190→          disabled={isDisabled}
191→          aria-label="Sign in with wallet"
192→          aria-busy={isWorking}
193→        >
194→          {buttonText()}
195→        </button>
196→      ) : (
197→        <div className="wallet-connector-list" role="group" aria-label="Available wallets">
198→          {phase !== 'idle' ? (
199→            <div className="wallet-login-status" aria-live="polite">
200→              {buttonText()}
201→            </div>
202→          ) : uniqueConnectors.length === 0 ? (
203→            <div className="wallet-no-providers">
204→              no wallets detected. install MetaMask or another browser wallet.
205→            </div>
206→          ) : (
207→            <>
208→              <div className="wallet-connector-header">{'// select wallet'}</div>
209→              {uniqueConnectors.map((connector) => (
210→                <button
211→                  key={connector.uid}
212→                  type="button"
213→                  className="wallet-connector-option"
214→                  onClick={() => handleConnectorClick(connector)}
215→                  disabled={isConnecting || isWorking}
216→                  aria-label={`Connect with ${connector.name}`}
217→                >
218→                  [{connector.name}]
219→                </button>
220→              ))}
221→            </>
222→          )}
223→          <button
224→            type="button"
225→            className="wallet-connector-cancel"
226→            onClick={handleCancel}
227→            disabled={phase === 'verifying'}
228→            aria-label="Cancel wallet connection"
229→          >
230→            {'// cancel'}
231→          </button>
232→        </div>
233→      )}
234→      {error && (
235→        <div className="login-error" role="alert" aria-live="polite">
236→          {error}
237→        </div>
238→      )}
239→    </div>
240→  );
241→}
