31→import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
32→import { WagmiSetup } from './lib/wagmi/provider';
33→import { CoreKitProvider } from './lib/web3auth/core-kit-provider';
34→import { registerDecryptSW, setSwApiBase, updateSwToken } from './lib/sw-registration';
35→import { useAuthStore } from './stores/auth.store';
36→import App from './App';
37→import './index.css';
38→
39→// Register Service Worker for streaming media decryption (non-blocking)
40→registerDecryptSW().then((reg) => {
41→  if (!reg) return;
42→  const apiUrl = import.meta.env.VITE_API_URL || 'http://localhost:3000';
43→  setSwApiBase(apiUrl);
44→
45→  // Send current token if already available
46→  const { accessToken } = useAuthStore.getState();
47→  if (accessToken) updateSwToken(accessToken);
48→});
49→
50→// Keep SW auth token in sync whenever it changes (clear on logout)
51→useAuthStore.subscribe((state, prevState) => {
52→  if (state.accessToken !== prevState.accessToken) {
53→    updateSwToken(state.accessToken ?? '');
54→  }
55→});
56→
57→// Expose Zustand stores for E2E test state injection (dev mode only)
58→if (import.meta.env.DEV) {
59→  import('./stores/auth.store').then(({ useAuthStore }) => {
60→    import('./stores/vault.store').then(({ useVaultStore }) => {
61→      import('./stores/folder.store').then(({ useFolderStore }) => {
62→        import('./stores/sync.store').then(({ useSyncStore }) => {
63→          // eslint-disable-next-line @typescript-eslint/no-explicit-any
64→          (window as any).__ZUSTAND_STORES = {
65→            auth: useAuthStore,
66→            vault: useVaultStore,
67→            folder: useFolderStore,
68→            sync: useSyncStore,
69→          };
70→        });
71→      });
72→    });
73→  });
74→}
