 1→/**
 2→ * Device Approval API Client
 3→ *
 4→ * Thin wrapper around the generated Orval client for the bulletin board API
 5→ * used in cross-device factor transfer. Maintains the same external API surface
 6→ * (deviceApprovalApi object with 5 methods) consumed by useDeviceApproval hook.
 7→ *
 8→ * Types are re-exported from the generated models for backward compatibility.
 9→ */
10→
11→import {
12→  deviceApprovalControllerCreateRequest,
13→  deviceApprovalControllerGetStatus,
14→  deviceApprovalControllerGetPending,
15→  deviceApprovalControllerRespond,
16→  deviceApprovalControllerCancel,
17→} from '../api/device-approval/device-approval';
18→
19→import type { CreateApprovalDto, RespondApprovalDto } from '../api/models';
20→import type { DeviceApprovalControllerGetStatus200 } from '../api/models/deviceApprovalControllerGetStatus200';
21→import type { DeviceApprovalControllerGetPending200Item } from '../api/models/deviceApprovalControllerGetPending200Item';
22→
23→// Re-export types with original names for backward compatibility
24→export type CreateApprovalRequest = CreateApprovalDto;
25→
26→export type ApprovalStatusResponse = DeviceApprovalControllerGetStatus200;
27→
28→export type PendingApproval = DeviceApprovalControllerGetPending200Item;
29→
30→export type RespondApprovalRequest = RespondApprovalDto;
31→
32→export const deviceApprovalApi = {
33→  /**
34→   * Create a new device approval request on the bulletin board.
35→   * Called by the new (requesting) device.
36→   */
37→  createRequest: async (dto: CreateApprovalDto): Promise<{ requestId: string }> => {
38→    const result = await deviceApprovalControllerCreateRequest(dto);
39→    if (!result.requestId) {
40→      throw new Error('Server did not return a requestId');
41→    }
42→    return { requestId: result.requestId };
43→  },
44→
45→  /**
46→   * Poll the status of an approval request.
47→   * Called by the new device to check if approved/denied/expired.
48→   */
49→  getStatus: async (requestId: string): Promise<ApprovalStatusResponse> => {
50→    return deviceApprovalControllerGetStatus(requestId);
51→  },
52→
53→  /**
54→   * Get all pending approval requests for the current user.
55→   * Called by existing (approving) devices.
56→   */
57→  getPending: async (): Promise<PendingApproval[]> => {
58→    return deviceApprovalControllerGetPending();
59→  },
60→
61→  /**
62→   * Respond to a pending approval request (approve or deny).
63→   * Called by the existing device.
64→   */
65→  respond: async (requestId: string, dto: RespondApprovalDto): Promise<void> => {
66→    await deviceApprovalControllerRespond(requestId, dto);
67→  },
68→
69→  /**
70→   * Cancel a pending approval request.
71→   * Called by the requesting device to clean up.
72→   */
73→  cancel: async (requestId: string): Promise<void> => {
74→    await deviceApprovalControllerCancel(requestId);
75→  },
76→};
