 100→/**
 101→ * Check if a filename has a text-editable extension.
 102→ */
 103→function isTextFile(name: string): boolean {
 104→  const lower = name.toLowerCase();
 105→  // Handle dotfiles like .gitignore, .editorconfig
 106→  if (TEXT_EXTENSIONS.has(lower)) return true;
 107→  // Handle well-known extensionless filenames like Dockerfile, Makefile
 108→  if (TEXT_FILENAMES.has(lower)) return true;
 109→  const lastDot = lower.lastIndexOf('.');
 110→  if (lastDot === -1) return false;
 111→  return TEXT_EXTENSIONS.has(lower.slice(lastDot));
 112→}
 113→
 114→/** Extensions recognized as previewable image files. */
 115→const IMAGE_EXTENSIONS = new Set([
 116→  '.png',
 117→  '.jpg',
 118→  '.jpeg',
 119→  '.gif',
 120→  '.webp',
 121→  '.svg',
 122→  '.bmp',
 123→  '.ico',
 124→  '.avif',
 125→]);
 126→
 127→/**
 128→ * Check if a filename has a previewable image extension.
 129→ */
 130→function isImageFile(name: string): boolean {
 131→  const lower = name.toLowerCase();
 132→  const lastDot = lower.lastIndexOf('.');
 133→  if (lastDot === -1) return false;
 134→  return IMAGE_EXTENSIONS.has(lower.slice(lastDot));
 135→}
