 137→/** Extensions recognized as PDF files. */
 138→const PDF_EXTENSIONS = new Set(['.pdf']);
 139→
 140→/** Extensions recognized as playable audio files. */
 141→const AUDIO_EXTENSIONS = new Set(['.mp3', '.wav', '.ogg', '.m4a', '.flac']);
 142→
 143→/** Extensions recognized as playable video files. */
 144→const VIDEO_EXTENSIONS = new Set(['.mp4', '.webm', '.mov', '.mkv']);
 145→
 146→/**
 147→ * Check if a filename has a PDF extension.
 148→ */
 149→function isPdfFile(name: string): boolean {
 150→  const lower = name.toLowerCase();
 151→  const lastDot = lower.lastIndexOf('.');
 152→  if (lastDot === -1) return false;
 153→  return PDF_EXTENSIONS.has(lower.slice(lastDot));
 154→}
 155→
 156→/**
 157→ * Check if a filename has a playable audio extension.
 158→ */
 159→function isAudioFile(name: string): boolean {
 160→  const lower = name.toLowerCase();
 161→  const lastDot = lower.lastIndexOf('.');
 162→  if (lastDot === -1) return false;
 163→  return AUDIO_EXTENSIONS.has(lower.slice(lastDot));
 164→}
 165→
 166→/**
 167→ * Check if a filename has a playable video extension.
 168→ */
 169→function isVideoFile(name: string): boolean {
 170→  const lower = name.toLowerCase();
 171→  const lastDot = lower.lastIndexOf('.');
 172→  if (lastDot === -1) return false;
 173→  return VIDEO_EXTENSIONS.has(lower.slice(lastDot));
 174→}
 175→
 176→/**
 177→ * Check if a filename is any previewable type (image, PDF, audio, video).
 178→ */
 179→function isPreviewableFile(name: string): boolean {
 180→  return isImageFile(name) || isPdfFile(name) || isAudioFile(name) || isVideoFile(name);
 181→}
