 191→/**
 192→ * Main file browser container component.
 193→ *
 194→ * Manages the file list area with in-place navigation.
 195→ * Selection state, context menu, and file/folder actions.
 196→ *
 197→ * Navigation:
 198→ * - Double-click folders to navigate into them
 199→ * - Click [..] PARENT_DIR row to navigate up
 200→ * - Browser history integration via URL routing
 201→ *
 202→ * Actions:
 203→ * - Download: Downloads file from IPFS, decrypts, and triggers browser download
 204→ * - Rename: Opens dialog to rename file/folder
 205→ * - Delete: Shows confirmation, then removes from folder metadata
 206→ * - Move: Drag-drop to folder rows in list
 207→ *
 208→ * @example
 209→ * ```tsx
 210→ * function FilesPage() {
 211→ *   return (
 212→ *     <main className="app-main">
 213→ *       <FileBrowser />
 214→ *     </main>
 215→ *   );
 216→ * }
 217→ * ```
 218→ */
 219→
 220→export function FileBrowser() {
 221→  // Navigation state from hook
 222→  const { currentFolderId, currentFolder, breadcrumbs, isLoading, navigateTo, navigateUp } =
 223→    useFolderNavigation();
 224→
 225→  // Folder operations
 226→  const {
 227→    createFolder,
 228→    renameItem,
 229→    moveItem,
 230→    moveItems,
 231→    deleteItem,
 232→    deleteItems,
 233→    isLoading: isOperating,
 234→  } = useFolder();
 235→
 236→  // File download
 237→  const { downloadFromIpns, isDownloading } = useFileDownload();
 238→
 239→  // External file drop upload
 240→  const { handleFileDrop } = useDropUpload();
 241→
 242→  // Context menu state
 243→  const contextMenu = useContextMenu();
 244→
 245→  // Vault and folder stores for sync
 246→  const { rootIpnsName } = useVaultStore();
 247→  const initialSyncComplete = useSyncStore((state) => state.initialSyncComplete);
 248→  const syncStatus = useSyncStore((state) => state.status);
 249→
 250→  // Sync callback - compare remote sequence with local, refresh if different
 251→  const handleSync = useCallback(async () => {
 252→    if (!rootIpnsName) return;
 253→
 254→    // Resolve root folder IPNS to get remote CID and sequence number
 255→    const resolved = await resolveIpnsRecord(rootIpnsName);
 256→    if (!resolved) {
 257→      // If initial sync hasn't completed yet, signal that IPNS isn't available
 258→      // so useSyncPolling keeps the syncing state visible and retries
 259→      if (!useSyncStore.getState().initialSyncComplete) {
 260→        throw new Error('IPNS not resolved yet');
 261→      }
 262→      return;
 263→    }
 264→
 265→    // Read fresh state from store — avoid stale closure from render cycle.
 266→    // On initial load, the root folder may not be in the closure's `folders`
 267→    // yet because useFolderNavigation's useEffect sets it in the same commit.
 268→    const rootFolder = useFolderStore.getState().folders['root'];
 269→    if (!rootFolder) return;
 270→
 271→    // Compare sequence numbers - if remote > local, we need to refresh
 272→    // Sequence number comparison is more reliable than CID since we
 273→    // don't cache the local CID, and sequence always increments
 274→    if (resolved.sequenceNumber <= rootFolder.sequenceNumber) {
 275→      // No changes, already up to date
 276→      return;
 277→    }
 278→
 279→    // Remote has newer version - fetch and decrypt new metadata
 280→    try {
 281→      const metadata = await fetchAndDecryptMetadata(resolved.cid, rootFolder.folderKey);
 282→
 283→      // Update folder store with new children
 284→      // Per CONTEXT.md: last write wins, instant refresh (no toast/prompt)
 285→      useFolderStore.getState().updateFolderChildren('root', metadata.children as FolderChildV2[]);
 286→      useFolderStore.getState().updateFolderSequence('root', resolved.sequenceNumber);
 287→    } catch (err) {
 288→      console.error('Sync refresh failed:', err);
 289→      // During initial sync, propagate so useSyncPolling keeps the
 290→      // loading UI visible and retries instead of showing empty state
 291→      if (!useSyncStore.getState().initialSyncComplete) {
 292→        throw err;
 293→      }
 294→    }
 295→  }, [rootIpnsName]);
 296→
 297→  // Start sync polling (30s interval, pauses when backgrounded/offline)
 298→  useSyncPolling(handleSync);
 299→
 300→  // Device registry polling (60s interval, independent from vault sync)
 301→  useDeviceRegistrySync();
 302→
 303→  // External drag state — tracks when files from OS are being dragged over
 304→  const [isDraggingExternal, setIsDraggingExternal] = useState(false);
 305→  const dragCounterRef = useRef(0);
 306→
 307→  /**
 308→   * Detect external file drag entering the content area.
 309→   * Uses a counter to handle nested dragenter/dragleave events correctly.
 310→   */
 311→  const handleContentDragEnter = useCallback((e: DragEvent) => {
 312→    if (isExternalFileDrag(e.dataTransfer)) {
 313→      dragCounterRef.current += 1;
 314→      if (dragCounterRef.current === 1) {
 315→        setIsDraggingExternal(true);
 316→      }
 317→    }
 318→  }, []);
 319→
 320→  const handleContentDragOver = useCallback((e: DragEvent) => {
 321→    if (isExternalFileDrag(e.dataTransfer)) {
 322→      e.preventDefault();
 323→      e.dataTransfer.dropEffect = 'copy';
 324→    }
 325→  }, []);
 326→
 327→  const handleContentDragLeave = useCallback((e: DragEvent) => {
 328→    if (isExternalFileDrag(e.dataTransfer)) {
 329→      dragCounterRef.current -= 1;
 330→      if (dragCounterRef.current <= 0) {
 331→        dragCounterRef.current = 0;
 332→        setIsDraggingExternal(false);
 333→      }
 334→    }
 335→  }, []);
 336→
 337→  // Reset drag state if user abandons drag outside the window (e.g. drops on desktop
 338→  // or presses Escape). Without this the counter/overlay can get stuck.
 339→  useEffect(() => {
 340→    const resetDragState = () => {
 341→      dragCounterRef.current = 0;
 342→      setIsDraggingExternal(false);
 343→    };
 344→    const handleWindowDrop = (e: Event) => {
 345→      e.preventDefault();
 346→      resetDragState();
 347→    };
 348→    const handleWindowDragLeave = (e: globalThis.DragEvent) => {
 349→      // relatedTarget is null when the drag leaves the browser window entirely
 350→      if (!e.relatedTarget) {
 351→        resetDragState();
 352→      }
 353→    };
 354→    window.addEventListener('dragend', resetDragState);
 355→    window.addEventListener('drop', handleWindowDrop);
 356→    window.addEventListener('dragleave', handleWindowDragLeave as EventListener);
 357→    return () => {
 358→      window.removeEventListener('dragend', resetDragState);
 359→      window.removeEventListener('drop', handleWindowDrop);
 360→      window.removeEventListener('dragleave', handleWindowDragLeave as EventListener);
 361→    };
 362→  }, []);
 363→
 364→  /**
 365→   * Catch-all drop handler for the content area.
 366→   * If files from OS are dropped on general area (not intercepted by a child),
 367→   * upload them to the current folder.
 368→   */
 369→  const handleContentDrop = useCallback(
 370→    (e: DragEvent) => {
 371→      dragCounterRef.current = 0;
 372→      setIsDraggingExternal(false);
 373→
 374→      // Only handle external file drops
 375→      if (!e.dataTransfer.files || e.dataTransfer.files.length === 0) return;
 376→      if (!isExternalFileDrag(e.dataTransfer)) return;
 377→
 378→      e.preventDefault();
 379→      const files = Array.from(e.dataTransfer.files);
 380→      handleFileDrop(files, currentFolderId);
 381→    },
 382→    [handleFileDrop, currentFolderId]
 383→  );
 384→
 385→  /**
 386→   * Handle external file drops targeting a specific folder.
 387→   * Called by FileListItem (folder rows) and Breadcrumbs.
 388→   */
 389→  const handleExternalFileDrop = useCallback(
 390→    (files: File[], targetFolderId: string) => {
 391→      dragCounterRef.current = 0;
 392→      setIsDraggingExternal(false);
 393→      handleFileDrop(files, targetFolderId);
 394→    },
 395→    [handleFileDrop]
 396→  );
 397→
 398→  // Get current folder's children (needed early for selection logic)
 399→  const children = currentFolder?.children ?? [];
 400→  const hasChildren = children.length > 0;
 401→
 402→  // Multi-selection state
 403→  const [selectedIds, setSelectedIds] = useState<Set<string>>(new Set());
 404→  const lastSelectedIdRef = useRef<string | null>(null);
 405→
 406→  // Prune stale selectedIds when children change (sync refresh, upload, external delete)
 407→  const childIds = useMemo(() => new Set(children.map((c) => c.id)), [children]);
 408→  useEffect(() => {
 409→    setSelectedIds((prev) => {
 410→      if (prev.size === 0) return prev;
 411→      const pruned = new Set([...prev].filter((id) => childIds.has(id)));
 412→      if (pruned.size === prev.size) return prev;
 413→      return pruned;
 414→    });
 415→  }, [childIds]);
 416→
 417→  // Compute selected items from IDs for action bar and batch operations
 418→  const selectedItems = useMemo(
 419→    () => children.filter((c) => selectedIds.has(c.id)),
 420→    [children, selectedIds]
 421→  );
 422→  const multiSelectActive = selectedIds.size > 0;
 423→
 424→  // Dialog states
 425→  const [confirmDialog, setConfirmDialog] = useState<DialogState>({ open: false, item: null });
 426→  const [renameDialog, setRenameDialog] = useState<DialogState>({ open: false, item: null });
 427→  const [moveDialog, setMoveDialog] = useState<DialogState>({ open: false, item: null });
 428→  const [detailsDialog, setDetailsDialog] = useState<DialogState>({ open: false, item: null });
 429→  const [editorDialog, setEditorDialog] = useState<DialogState>({ open: false, item: null });
 430→  const [imagePreviewDialog, setImagePreviewDialog] = useState<DialogState>({
 431→    open: false,
 432→    item: null,
 433→  });
 434→  const [pdfPreviewDialog, setPdfPreviewDialog] = useState<DialogState>({
 435→    open: false,
 436→    item: null,
 437→  });
 438→  const [audioPlayerDialog, setAudioPlayerDialog] = useState<DialogState>({
 439→    open: false,
 440→    item: null,
 441→  });
 442→  const [videoPlayerDialog, setVideoPlayerDialog] = useState<DialogState>({
 443→    open: false,
 444→    item: null,
 445→  });
