 458→  /**
 459→   * Handle item selection with modifier keys.
 460→   * - Plain click: single select (clears others)
 461→   * - Ctrl/Cmd+click: toggle selection
 462→   * - Shift+click: range select from last clicked to current
 463→   */
 464→  const handleSelect = useCallback(
 465→    (itemId: string, event: { ctrlKey: boolean; shiftKey: boolean; metaKey: boolean }) => {
 466→      const isCtrl = event.ctrlKey || event.metaKey;
 467→      const isShift = event.shiftKey;
 468→
 469→      if (isShift && lastSelectedIdRef.current) {
 470→        // Range select: select all items between lastSelected and current
 471→        // Use sorted children order so the range respects visual order
 472→        const sortedChildren = [...children].sort((a, b) => {
 473→          if (a.type === 'folder' && b.type !== 'folder') return -1;
 474→          if (a.type !== 'folder' && b.type === 'folder') return 1;
 475→          return a.name.localeCompare(b.name, undefined, { sensitivity: 'base' });
 476→        });
 477→
 478→        const ids = sortedChildren.map((c) => c.id);
 479→        const startIdx = ids.indexOf(lastSelectedIdRef.current);
 480→        const endIdx = ids.indexOf(itemId);
 481→
 482→        if (startIdx !== -1 && endIdx !== -1) {
 483→          const rangeStart = Math.min(startIdx, endIdx);
 484→          const rangeEnd = Math.max(startIdx, endIdx);
 485→          const rangeIds = ids.slice(rangeStart, rangeEnd + 1);
 486→
 487→          setSelectedIds((prev) => {
 488→            const next = new Set(prev);
 489→            for (const id of rangeIds) {
 490→              next.add(id);
 491→            }
 492→            return next;
 493→          });
 494→        }
 495→      } else if (isCtrl) {
 496→        // Toggle: add or remove from selection
 497→        setSelectedIds((prev) => {
 498→          const next = new Set(prev);
 499→          if (next.has(itemId)) {
 500→            next.delete(itemId);
 501→          } else {
 502→            next.add(itemId);
 503→          }
 504→          return next;
 505→        });
 506→        lastSelectedIdRef.current = itemId;
 507→      } else {
 508→        // Plain click: single select
 509→        setSelectedIds(new Set([itemId]));
 510→        lastSelectedIdRef.current = itemId;
 511→      }
 512→    },
 513→    [children]
 514→  );
