1093→        confirmLabel="Delete All"
1094→        isDestructive
1095→        isLoading={isOperating}
1096→      />
1097→
1098→      {/* Batch move dialog */}
1099→      <MoveDialog
1100→        open={batchMoveDialog.open}
1101→        onClose={closeBatchMoveDialog}
