  1→import { create } from 'zustand';
  2→import type { FolderChildV2 } from '@cipherbox/crypto';
  3→
  4→/**
  5→ * A folder node in the in-memory folder tree.
  6→ * Contains decrypted folder data and navigation state.
  7→ */
  8→export type FolderNode = {
  9→  /** UUID for internal reference (null string 'root' for root folder) */
 10→  id: string;
 11→  /** Folder name */
 12→  name: string;
 13→  /** IPNS name for this folder (k51.../bafzaa... format) */
 14→  ipnsName: string;
 15→  /** Parent folder ID (null for root) */
 16→  parentId: string | null;
 17→  /** Decrypted children (files and subfolders) */
 18→  children: FolderChildV2[];
 19→  /** Has metadata been fetched and decrypted? */
 20→  isLoaded: boolean;
 21→  /** Is metadata currently being fetched? */
 22→  isLoading: boolean;
 23→  /** Current IPNS sequence number */
 24→  sequenceNumber: bigint;
 25→  /** Decrypted AES-256 folder key */
 26→  folderKey: Uint8Array;
 27→  /** Decrypted Ed25519 IPNS private key */
 28→  ipnsPrivateKey: Uint8Array;
 29→};
 30→
 31→/**
 32→ * Breadcrumb entry for navigation.
 33→ */
 34→type Breadcrumb = {
 35→  id: string;
 36→  name: string;
 37→};
 38→
 39→type FolderState = {
 40→  // Folder tree indexed by id
 41→  folders: Record<string, FolderNode>;
 42→
 43→  // Current navigation
 44→  currentFolderId: string | null; // null = root
 45→  breadcrumbs: Breadcrumb[];
 46→
 47→  // Publishing state
 48→  pendingPublishes: Set<string>; // folder IDs with pending IPNS publishes
 49→
 50→  // Actions
 51→  setFolder: (folder: FolderNode) => void;
 52→  updateFolderChildren: (folderId: string, children: FolderChildV2[]) => void;
 53→  updateFolderSequence: (folderId: string, sequenceNumber: bigint) => void;
 54→  setCurrentFolder: (folderId: string | null) => void;
 55→  setBreadcrumbs: (breadcrumbs: Breadcrumb[]) => void;
 56→  addPendingPublish: (folderId: string) => void;
 57→  removePendingPublish: (folderId: string) => void;
 58→  removeFolder: (folderId: string) => void;
 59→  updateFolderName: (folderId: string, newName: string) => void;
 60→  clearFolders: () => void;
 61→};
 62→
 63→/**
 64→ * Folder store for managing folder tree state.
 65→ *
 66→ * Maintains the in-memory representation of the folder hierarchy
 67→ * with decrypted folder keys and metadata.
 68→ *
 69→ * SECURITY: All folder keys are memory-only - never persisted to storage.
 70→ * Call clearFolders() on logout to zero-fill all key material.
 71→ */
 72→export const useFolderStore = create<FolderState>((set, get) => ({
 73→  // State
 74→  folders: {},
 75→  currentFolderId: null,
 76→  breadcrumbs: [],
 77→  pendingPublishes: new Set<string>(),
 78→
 79→  // Actions
 80→  setFolder: (folder) =>
 81→    set((state) => ({
 82→      folders: { ...state.folders, [folder.id]: folder },
 83→    })),
 84→
 85→  updateFolderChildren: (folderId, children) =>
 86→    set((state) => {
 87→      const folder = state.folders[folderId];
 88→      if (!folder) return state;
 89→
 90→      return {
 91→        folders: {
 92→          ...state.folders,
 93→          [folderId]: { ...folder, children, isLoaded: true, isLoading: false },
 94→        },
 95→      };
 96→    }),
 97→
 98→  updateFolderSequence: (folderId, sequenceNumber) =>
 99→    set((state) => {
100→      const folder = state.folders[folderId];
101→      if (!folder) return state;
102→
103→      return {
104→        folders: {
105→          ...state.folders,
106→          [folderId]: { ...folder, sequenceNumber },
107→        },
108→      };
109→    }),
110→
111→  setCurrentFolder: (folderId) => set({ currentFolderId: folderId }),
112→
113→  setBreadcrumbs: (breadcrumbs) => set({ breadcrumbs }),
114→
115→  addPendingPublish: (folderId) =>
116→    set((state) => {
117→      const newPending = new Set(state.pendingPublishes);
118→      newPending.add(folderId);
119→      return { pendingPublishes: newPending };
120→    }),
121→
122→  removePendingPublish: (folderId) =>
123→    set((state) => {
124→      const newPending = new Set(state.pendingPublishes);
125→      newPending.delete(folderId);
126→      return { pendingPublishes: newPending };
127→    }),
128→
129→  // Remove a folder from local state (after deletion)
130→  // Also clears the folder's key material from memory
131→  removeFolder: (folderId) =>
132→    set((state) => {
133→      const folder = state.folders[folderId];
134→      if (!folder) return state;
135→
136→      // Zero-fill keys before removing
137→      if (folder.folderKey) folder.folderKey.fill(0);
138→      if (folder.ipnsPrivateKey) folder.ipnsPrivateKey.fill(0);
139→
140→      const { [folderId]: _removed, ...remainingFolders } = state.folders;
141→      void _removed; // Intentionally destructured to remove from object
142→      return { folders: remainingFolders };
143→    }),
144→
145→  // Update a folder's name in local state
146→  updateFolderName: (folderId, newName) =>
147→    set((state) => {
148→      const folder = state.folders[folderId];
149→      if (!folder) return state;
150→
151→      return {
152→        folders: {
153→          ...state.folders,
154→          [folderId]: { ...folder, name: newName },
155→        },
156→      };
157→    }),
158→
159→  // [SECURITY: MEDIUM-02] Zero-fill sensitive key material before clearing
160→  clearFolders: () => {
161→    const state = get();
162→
163→    // Best-effort memory clearing - overwrite all folder keys with zeros
164→    for (const folder of Object.values(state.folders)) {
165→      if (folder.folderKey) {
166→        folder.folderKey.fill(0);
167→      }
168→      if (folder.ipnsPrivateKey) {
169→        folder.ipnsPrivateKey.fill(0);
170→      }
171→    }
172→
173→    set({
174→      folders: {},
175→      currentFolderId: null,
176→      breadcrumbs: [],
177→      pendingPublishes: new Set<string>(),
178→    });
179→  },
180→}));
181→
182→// Expose store on window for E2E testing (development only)
183→if (typeof window !== 'undefined' && import.meta.env.DEV) {
184→  // eslint-disable-next-line @typescript-eslint/no-explicit-any
185→  (window as any).__ZUSTAND_FOLDER_STORE__ = useFolderStore;
186→}
