138→  identityWalletNonce: async (): Promise<{ nonce: string }> => {
139→    const response = await apiClient.get<{ nonce: string }>('/auth/identity/wallet/nonce');
140→    return response.data;
141→  },
142→
143→  /** Verify SIWE wallet signature and get CipherBox identity JWT */
144→  identityWalletVerify: async (data: {
145→    message: string;
146→    signature: string;
147→  }): Promise<IdentityTokenResponse> => {
148→    const response = await apiClient.post<IdentityTokenResponse>('/auth/identity/wallet', data);
149→    return response.data;
150→  },
151→};
