  1→import { useCallback, useEffect, useMemo, useRef, useState } from 'react';
  2→import { useNavigate, useParams } from 'react-router-dom';
  3→import { unwrapKey, hexToBytes, type FolderEntry } from '@cipherbox/crypto';
  4→import { useFolderStore, type FolderNode } from '../stores/folder.store';
  5→import { useVaultStore } from '../stores/vault.store';
  6→import { useAuthStore } from '../stores/auth.store';
  7→import { loadFolder } from '../services/folder.service';
  8→
  9→/**
 10→ * Breadcrumb entry for navigation.
 11→ */
 12→export type Breadcrumb = {
 13→  id: string;
 14→  name: string;
 15→};
 16→
 17→/**
 18→ * Hook return type.
 19→ */
 20→type UseFolderNavigationReturn = {
 21→  currentFolderId: string;
 22→  currentFolder: FolderNode | null;
 23→  breadcrumbs: Breadcrumb[];
 24→  isLoading: boolean;
 25→  navigateTo: (folderId: string) => Promise<void>;
 26→  navigateUp: () => void;
 27→};
 28→
 29→/**
 30→ * Get the root folder from vault state or folder store.
 31→ */
 32→function getRootFolder(
 33→  vaultStore: ReturnType<typeof useVaultStore.getState>,
 34→  folders: Record<string, FolderNode>
 35→): FolderNode | null {
 36→  // If we have an explicit root folder in the tree, use it
 37→  const existingRoot = folders['root'];
 38→  if (existingRoot) return existingRoot;
 39→
 40→  // Otherwise construct from vault state
 41→  if (!vaultStore.rootFolderKey || !vaultStore.rootIpnsKeypair || !vaultStore.rootIpnsName) {
 42→    return null;
 43→  }
 44→
 45→  return {
 46→    id: 'root',
 47→    name: 'My Vault',
 48→    ipnsName: vaultStore.rootIpnsName,
 49→    parentId: null,
 50→    children: [],
 51→    isLoaded: false,
 52→    isLoading: false,
 53→    sequenceNumber: 0n,
 54→    folderKey: vaultStore.rootFolderKey,
 55→    ipnsPrivateKey: vaultStore.rootIpnsKeypair.privateKey,
 56→  };
 57→}
 58→
 59→/**
 60→ * Build breadcrumb trail from current folder to root.
 61→ */
 62→function buildBreadcrumbs(
 63→  folderId: string,
 64→  folders: Record<string, FolderNode>,
 65→  vaultStore: ReturnType<typeof useVaultStore.getState>
 66→): Breadcrumb[] {
 67→  const breadcrumbs: Breadcrumb[] = [];
 68→  let currentId: string | null = folderId;
 69→
 70→  while (currentId) {
 71→    const currentFolder: FolderNode | null =
 72→      currentId === 'root' ? getRootFolder(vaultStore, folders) : folders[currentId];
 73→    if (!currentFolder) break;
 74→
 75→    breadcrumbs.unshift({ id: currentFolder.id, name: currentFolder.name });
 76→    currentId = currentFolder.parentId;
 77→  }
 78→
 79→  return breadcrumbs;
 80→}
 81→
 82→/**
 83→ * Hook for managing folder navigation state.
 84→ *
 85→ * Provides current folder, breadcrumb trail, and navigation functions.
 86→ * Integrates with folder store for folder tree state and vault store
 87→ * for root folder keys.
 88→ *
 89→ * @example
 90→ * ```tsx
 91→ * function FileBrowser() {
 92→ *   const { currentFolder, breadcrumbs, navigateTo, navigateUp } = useFolderNavigation();
 93→ *
 94→ *   return (
 95→ *     <div>
 96→ *       <Breadcrumbs items={breadcrumbs} onNavigate={navigateTo} />
 97→ *       <FileList items={currentFolder?.children ?? []} />
 98→ *     </div>
 99→ *   );
100→ * }
101→ * ```
102→ */
103→export function useFolderNavigation(): UseFolderNavigationReturn {
104→  // Get folder ID from URL params - defaults to 'root' when not present
105→  const { folderId } = useParams<{ folderId?: string }>();
106→  const navigate = useNavigate();
107→  const currentFolderId = folderId ?? 'root';
108→
109→  const [isLoading, setIsLoading] = useState(false);
110→  // Track latest navigation target to prevent stale completions from race conditions
111→  const latestNavTarget = useRef<string | null>(null);
112→
113→  // Subscribe to stores
114→  const folders = useFolderStore((state) => state.folders);
115→  const setFolder = useFolderStore((state) => state.setFolder);
116→  const setBreadcrumbs = useFolderStore((state) => state.setBreadcrumbs);
117→
118→  // Vault state for root folder
119→  const rootFolderKey = useVaultStore((state) => state.rootFolderKey);
120→  const rootIpnsKeypair = useVaultStore((state) => state.rootIpnsKeypair);
121→  const rootIpnsName = useVaultStore((state) => state.rootIpnsName);
122→
123→  // Get vault state snapshot for helper functions
124→  const vaultState = useVaultStore.getState();
125→
126→  // Get current folder node
127→  const currentFolder =
128→    currentFolderId === 'root' ? getRootFolder(vaultState, folders) : folders[currentFolderId];
129→
130→  // Build breadcrumbs whenever current folder changes (memoized to avoid recreating array)
131→  const breadcrumbs = useMemo(
132→    () => buildBreadcrumbs(currentFolderId, folders, vaultState),
133→    [currentFolderId, folders, vaultState]
134→  );
135→
136→  // Initialize root folder in store if vault is ready but root not in store
137→  useEffect(() => {
138→    if (rootFolderKey && rootIpnsKeypair && rootIpnsName && !folders['root']) {
139→      const rootFolder = getRootFolder(vaultState, folders);
140→      if (rootFolder) {
141→        setFolder(rootFolder);
142→      }
143→    }
144→  }, [rootFolderKey, rootIpnsKeypair, rootIpnsName, folders, setFolder, vaultState]);
145→
146→  // Update store breadcrumbs when local breadcrumbs change
147→  useEffect(() => {
148→    setBreadcrumbs(breadcrumbs);
149→  }, [breadcrumbs, setBreadcrumbs]);
150→
151→  /**
152→   * Navigate to a specific folder using react-router.
153→   * Browser history is automatically managed.
154→   * Loads subfolder metadata (IPNS resolve + decrypt) if not yet loaded.
155→   */
156→  const navigateTo = useCallback(
157→    async (targetFolderId: string) => {
158→      // Track this navigation to detect stale completions from rapid clicks
159→      latestNavTarget.current = targetFolderId;
160→
161→      // Root is always constructible from vault state — just navigate
162→      if (targetFolderId === 'root') {
163→        navigate('/files');
164→        return;
165→      }
166→
167→      // Use getState() to avoid stale Zustand closures in async callback
168→      const currentFolders = useFolderStore.getState().folders;
169→      const targetFolder = currentFolders[targetFolderId];
170→
171→      // If already loaded, just navigate
172→      if (targetFolder?.isLoaded) {
173→        navigate(`/files/${targetFolderId}`);
174→        return;
175→      }
176→
177→      // Find the FolderEntry for this subfolder in any loaded parent's children
178→      let folderEntry: FolderEntry | undefined;
179→      let parentId: string | null = null;
180→
181→      for (const [fId, fNode] of Object.entries(currentFolders)) {
182→        if (!fNode.isLoaded) continue;
183→        const match = fNode.children.find(
184→          (c): c is FolderEntry => c.type === 'folder' && c.id === targetFolderId
185→        );
186→        if (match) {
187→          folderEntry = match;
188→          parentId = fId;
189→          break;
190→        }
191→      }
192→
193→      if (!folderEntry) {
194→        console.error(
195→          `Cannot load folder ${targetFolderId}: no parent with its FolderEntry is loaded`
196→        );
197→        return;
198→      }
199→
200→      // Get user's ECIES private key for unwrapping
201→      const vaultKeypair = useAuthStore.getState().vaultKeypair;
202→      if (!vaultKeypair) {
203→        console.error('Cannot load folder: no vault keypair available');
204→        return;
205→      }
206→
207→      // Insert placeholder BEFORE navigating so the component re-render
208→      // finds the folder node already in the store (avoids undefined flash)
209→      setIsLoading(true);
210→      const loadingPlaceholder: FolderNode = {
211→        id: targetFolderId,
212→        name: folderEntry.name,
213→        ipnsName: folderEntry.ipnsName,
214→        parentId,
215→        children: [],
216→        isLoaded: false,
217→        isLoading: true,
218→        sequenceNumber: 0n,
219→        folderKey: new Uint8Array(0),
220→        ipnsPrivateKey: new Uint8Array(0),
221→      };
222→      useFolderStore.getState().setFolder(loadingPlaceholder);
223→
224→      // Now navigate — the placeholder is already in the store
225→      navigate(`/files/${targetFolderId}`);
226→
227→      try {
228→        // Unwrap keys using user's ECIES private key
229→        const folderKey = await unwrapKey(
230→          hexToBytes(folderEntry.folderKeyEncrypted),
231→          vaultKeypair.privateKey
232→        );
233→        const ipnsPrivateKey = await unwrapKey(
234→          hexToBytes(folderEntry.ipnsPrivateKeyEncrypted),
235→          vaultKeypair.privateKey
236→        );
237→
238→        // Load folder metadata from IPNS (retry if IPNS hasn't propagated yet)
239→        const MAX_RETRIES = 3;
240→        const RETRY_DELAY_MS = 2000;
241→        let folderNode: Awaited<ReturnType<typeof loadFolder>> | null = null;
242→
243→        for (let attempt = 0; attempt <= MAX_RETRIES; attempt++) {
244→          if (latestNavTarget.current !== targetFolderId) return;
245→
246→          folderNode = await loadFolder(
247→            targetFolderId,
248→            folderKey,
249→            ipnsPrivateKey,
250→            folderEntry.ipnsName,
251→            parentId,
252→            folderEntry.name
253→          );
254→
255→          // If loaded successfully or this is the last attempt, stop retrying
256→          if (folderNode.isLoaded || attempt === MAX_RETRIES) break;
257→
258→          // IPNS not propagated yet — wait and retry
259→          await new Promise((r) => setTimeout(r, RETRY_DELAY_MS));
260→        }
261→
262→        // Only apply result if this is still the latest navigation
263→        if (latestNavTarget.current !== targetFolderId) return;
264→
265→        useFolderStore.getState().setFolder(folderNode!);
266→      } catch (err) {
267→        console.error('Failed to load subfolder:', err);
268→        // Only clean up if this is still the latest navigation
269→        if (latestNavTarget.current !== targetFolderId) return;
270→        // Remove the loading placeholder so the user can retry
271→        useFolderStore.getState().removeFolder(targetFolderId);
272→      } finally {
273→        // Only clear loading if this is still the latest navigation
274→        if (latestNavTarget.current === targetFolderId) {
275→          setIsLoading(false);
276→        }
277→      }
278→    },
279→    [navigate]
280→  );
281→
282→  /**
283→   * Navigate up to parent folder.
284→   */
285→  const navigateUp = useCallback(() => {
286→    if (currentFolder?.parentId) {
287→      navigateTo(currentFolder.parentId);
288→    } else if (currentFolderId !== 'root') {
289→      navigateTo('root');
290→    }
291→  }, [currentFolder, currentFolderId, navigateTo]);
292→
293→  return {
294→    currentFolderId,
295→    currentFolder,
296→    breadcrumbs,
297→    isLoading,
298→    navigateTo,
299→    navigateUp,
300→  };
301→}
