  1→import { type DragEvent, type MouseEvent } from 'react';
  2→import type { FolderChild } from '@cipherbox/crypto';
  3→import { FileListItem, type DragItem } from './FileListItem';
  4→import { ParentDirRow } from './ParentDirRow';
  5→
  6→type FileListProps = {
  7→  /** Items to display (files and folders) */
  8→  items: FolderChild[];
  9→  /** Set of currently selected item IDs */
 10→  selectedIds: Set<string>;
 11→  /** Parent folder ID (for drag operations) */
 12→  parentId: string;
 13→  /** Parent folder's decrypted AES-256 key (for resolving file sizes) */
 14→  folderKey: Uint8Array | null;
 15→  /** Whether to show [..] PARENT_DIR row (non-root folders) */
 16→  showParentRow?: boolean;
 17→  /** Callback when [..] row is clicked to navigate up */
 18→  onNavigateUp?: () => void;
 19→  /** Callback when an item is selected (with modifier key info) */
 20→  onSelect: (
 21→    itemId: string,
 22→    event: { ctrlKey: boolean; shiftKey: boolean; metaKey: boolean }
 23→  ) => void;
 24→  /** Callback to select/deselect all items */
 25→  onSelectAll: () => void;
 26→  /** Callback when navigating into a folder */
 27→  onNavigate: (folderId: string) => void;
 28→  /** Callback when context menu is requested */
 29→  onContextMenu: (event: MouseEvent, item: FolderChild) => void;
 30→  /** Callback when drag starts */
 31→  onDragStart: (event: DragEvent, item: FolderChild) => void;
 32→  /** Callback when items are dropped onto a folder */
 33→  onDropOnFolder?: (items: DragItem[], sourceParentId: string, destFolderId: string) => void;
 34→  /** Callback when external files are dropped onto a folder */
 35→  onExternalFileDrop?: (files: File[], destFolderId: string) => void;
 36→};
 37→
 38→/**
 39→ * Sort items: folders first, then files, both alphabetically.
 40→ */
 41→function sortItems(items: FolderChild[]): FolderChild[] {
 42→  return [...items].sort((a, b) => {
 43→    // Folders come first
 44→    if (a.type === 'folder' && b.type !== 'folder') return -1;
 45→    if (a.type !== 'folder' && b.type === 'folder') return 1;
 46→
 47→    // Within same type, sort alphabetically (case-insensitive)
 48→    return a.name.localeCompare(b.name, undefined, { sensitivity: 'base' });
 49→  });
 50→}
 51→
 52→/**
 53→ * File list component displaying files and folders in a columnar layout.
 54→ *
 55→ * Renders items sorted by type (folders first) then alphabetically.
 56→ * Supports selection, navigation, context menu, and drag operations.
 57→ *
 58→ * @example
 59→ * ```tsx
 60→ * function FileBrowser() {
 61→ *   const { currentFolder } = useFolderNavigation();
 62→ *   const [selectedId, setSelectedId] = useState<string | null>(null);
 63→ *
 64→ *   return (
 65→ *     <FileList
 66→ *       items={currentFolder?.children ?? []}
 67→ *       selectedId={selectedId}
 68→ *       parentId={currentFolder?.id ?? 'root'}
 69→ *       onSelect={setSelectedId}
 70→ *       onNavigate={navigateTo}
 71→ *       onContextMenu={handleContextMenu}
 72→ *       onDragStart={handleDragStart}
 73→ *     />
 74→ *   );
 75→ * }
 76→ * ```
 77→ */
 78→export function FileList({
 79→  items,
 80→  selectedIds,
 81→  parentId,
 82→  folderKey,
 83→  showParentRow,
 84→  onNavigateUp,
 85→  onSelect,
 86→  onSelectAll,
 87→  onNavigate,
 88→  onContextMenu,
 89→  onDragStart,
 90→  onDropOnFolder,
 91→  onExternalFileDrop,
 92→}: FileListProps) {
 93→  const sortedItems = sortItems(items);
 94→  const allSelected = items.length > 0 && selectedIds.size === items.length;
 95→
 96→  return (
 97→    <div className="file-list" role="grid">
 98→      {/* Header row */}
 99→      <div className="file-list-header" role="row">
100→        <div className="file-list-header-name" role="columnheader">
101→          <span
102→            className="file-list-header-checkbox"
103→            onClick={onSelectAll}
104→            role="checkbox"
105→            aria-checked={allSelected}
106→            aria-label={allSelected ? 'Deselect all' : 'Select all'}
107→            tabIndex={0}
108→            onKeyDown={(e) => {
109→              if (e.key === 'Enter' || e.key === ' ') {
110→                e.preventDefault();
111→                onSelectAll();
112→              }
113→            }}
114→          >
115→            {allSelected ? '[x]' : '[ ]'}
116→          </span>
117→          [NAME]
118→        </div>
119→        <div className="file-list-header-size" role="columnheader">
120→          [SIZE]
121→        </div>
122→        <div className="file-list-header-date" role="columnheader">
123→          [MODIFIED]
124→        </div>
125→        {/* Empty header cell for mobile action column - hidden on desktop via CSS */}
126→        <div className="file-list-header-actions" aria-hidden="true" />
127→      </div>
128→
129→      {/* Item rows */}
130→      <div className="file-list-body" role="rowgroup">
131→        {showParentRow && onNavigateUp && <ParentDirRow onActivate={onNavigateUp} />}
132→        {sortedItems.map((item) => (
133→          <FileListItem
134→            key={item.id}
135→            item={item}
136→            isSelected={selectedIds.has(item.id)}
137→            parentId={parentId}
138→            selectedIds={selectedIds}
139→            allItems={items}
140→            folderKey={folderKey}
141→            onSelect={onSelect}
142→            onNavigate={onNavigate}
143→            onContextMenu={onContextMenu}
144→            onDragStart={onDragStart}
145→            onDrop={
146→              onDropOnFolder && item.type === 'folder'
147→                ? (dragItems, sourceParentId) => onDropOnFolder(dragItems, sourceParentId, item.id)
148→                : undefined
149→            }
150→            onExternalFileDrop={item.type === 'folder' ? onExternalFileDrop : undefined}
151→          />
152→        ))}
153→      </div>
154→    </div>
155→  );
156→}
