  1→import {
  2→  useState,
  3→  useCallback,
  4→  useRef,
  5→  type DragEvent,
  6→  type MouseEvent,
  7→  type TouchEvent,
  8→} from 'react';
  9→import type { FolderChild, FilePointer, FolderEntry } from '@cipherbox/crypto';
 10→import { formatBytes, formatDate } from '../../utils/format';
 11→import { useFileSize } from '../../hooks/useFileSize';
 12→import { isExternalFileDrag } from '../../hooks/useDropUpload';
 13→
 14→/**
 15→ * Long press duration in milliseconds for touch context menu.
 16→ */
 17→const LONG_PRESS_DURATION = 500;
 18→
 19→/** Drag payload item (id + type). */
 20→export type DragItem = { id: string; type: 'file' | 'folder' };
 21→
 22→type FileListItemProps = {
 23→  /** The file or folder item to display */
 24→  item: FolderChild;
 25→  /** Whether this item is currently selected */
 26→  isSelected: boolean;
 27→  /** Parent folder ID (for drag data) */
 28→  parentId: string;
 29→  /** All currently selected item IDs (for multi-select drag) */
 30→  selectedIds: Set<string>;
 31→  /** All items in the current folder (for resolving types of selected IDs) */
 32→  allItems: FolderChild[];
 33→  /** Parent folder's decrypted AES-256 key (for resolving file sizes) */
 34→  folderKey: Uint8Array | null;
 35→  /** Callback when item is clicked (with modifier key info) */
 36→  onSelect: (
 37→    itemId: string,
 38→    event: { ctrlKey: boolean; shiftKey: boolean; metaKey: boolean }
 39→  ) => void;
 40→  /** Callback when folder is double-clicked to navigate into */
 41→  onNavigate: (folderId: string) => void;
 42→  /** Callback when right-click context menu is requested */
 43→  onContextMenu: (event: MouseEvent, item: FolderChild) => void;
 44→  /** Callback when drag starts */
 45→  onDragStart: (event: DragEvent, item: FolderChild) => void;
 46→  /** Callback when items are dropped onto this folder (folders only) */
 47→  onDrop?: (items: DragItem[], sourceParentId: string) => void;
 48→  /** Callback when external files are dropped onto this folder */
 49→  onExternalFileDrop?: (files: File[], destFolderId: string) => void;
 50→};
 51→
 52→/**
 53→ * Type guard for folder entries.
 54→ */
 55→function isFolder(item: FolderChild): item is FolderEntry {
 56→  return item.type === 'folder';
 57→}
 58→
 59→/**
 60→ * Type guard for file pointers (v2).
 61→ */
 62→function isFile(item: FolderChild): item is FilePointer {
 63→  return item.type === 'file';
 64→}
 65→
 66→/**
 67→ * Get text prefix for item type (terminal-style).
 68→ */
 69→function getItemIcon(item: FolderChild): string {
 70→  if (isFolder(item)) {
 71→    return '[DIR]';
 72→  }
 73→  return '[FILE]';
 74→}
 75→
 76→/* File extension helper removed - TYPE column no longer used */
 77→
 78→/**
 79→ * Single row in the file list.
 80→ *
 81→ * Displays file/folder with icon, name, size, and date.
 82→ * Supports selection, navigation (for folders), context menu, and drag-drop.
 83→ */
 84→export function FileListItem({
 85→  item,
 86→  isSelected,
 87→  parentId,
 88→  selectedIds,
 89→  allItems,
 90→  folderKey,
 91→  onSelect,
 92→  onNavigate,
 93→  onContextMenu,
 94→  onDragStart,
 95→  onDrop,
 96→  onExternalFileDrop,
 97→}: FileListItemProps) {
 98→  // Ref for long press timer
 99→  const longPressTimerRef = useRef<ReturnType<typeof setTimeout> | null>(null);
100→  const touchStartPosRef = useRef<{ x: number; y: number } | null>(null);
101→
102→  // Drag-over state for folder drop targets
103→  const [isDragOver, setIsDragOver] = useState(false);
104→
105→  /**
106→   * Handle single click - select the item.
107→   * Passes modifier key state for multi-selection support.
108→   */
109→  const handleClick = useCallback(
110→    (e: React.MouseEvent) => {
111→      onSelect(item.id, { ctrlKey: e.ctrlKey, shiftKey: e.shiftKey, metaKey: e.metaKey });
112→    },
113→    [item.id, onSelect]
114→  );
115→
116→  /**
117→   * Handle checkbox click - toggle selection without modifiers.
118→   */
119→  const handleCheckboxClick = useCallback(
120→    (e: React.MouseEvent) => {
121→      e.stopPropagation();
122→      // Checkbox click always toggles like Ctrl+click
123→      onSelect(item.id, { ctrlKey: true, shiftKey: false, metaKey: false });
124→    },
125→    [item.id, onSelect]
126→  );
127→
128→  /**
129→   * Handle double click - navigate into folder.
130→   */
131→  const handleDoubleClick = useCallback(() => {
132→    if (isFolder(item)) {
133→      onNavigate(item.id);
134→    }
135→  }, [item, onNavigate]);
136→
137→  /**
138→   * Handle context menu (right-click).
139→   */
140→  const handleContextMenu = useCallback(
141→    (e: MouseEvent) => {
142→      e.preventDefault();
143→      onContextMenu(e, item);
144→    },
145→    [item, onContextMenu]
146→  );
147→
148→  /**
149→   * Handle mobile action button click.
150→   * Creates a synthetic mouse event positioned at the button for context menu.
151→   */
152→  const handleActionButtonClick = useCallback(
153→    (e: React.MouseEvent<HTMLButtonElement>) => {
154→      e.stopPropagation();
155→      const rect = e.currentTarget.getBoundingClientRect();
156→      const syntheticEvent = {
157→        preventDefault: () => {},
158→        stopPropagation: () => {},
159→        clientX: rect.left,
160→        clientY: rect.bottom,
161→      } as unknown as MouseEvent;
162→      onContextMenu(syntheticEvent, item);
163→    },
164→    [item, onContextMenu]
165→  );
166→
167→  /**
168→   * Handle drag start - serialize item data.
169→   */
170→  const handleDragStart = useCallback(
171→    (e: DragEvent) => {
172→      // Build items array: if dragged item is part of multi-selection, include all selected
173→      let items: DragItem[];
174→      if (isSelected && selectedIds.size > 1) {
175→        items = allItems
176→          .filter((i) => selectedIds.has(i.id))
177→          .map((i) => ({ id: i.id, type: i.type }));
178→      } else {
179→        items = [{ id: item.id, type: item.type }];
180→      }
181→
182→      e.dataTransfer.setData('application/json', JSON.stringify({ items, parentId }));
183→      e.dataTransfer.effectAllowed = 'move';
184→      onDragStart(e, item);
185→    },
186→    [item, isSelected, selectedIds, allItems, parentId, onDragStart]
187→  );
188→
189→  /**
190→   * Clear long press timer.
191→   */
192→  const clearLongPressTimer = useCallback(() => {
193→    if (longPressTimerRef.current) {
194→      clearTimeout(longPressTimerRef.current);
195→      longPressTimerRef.current = null;
196→    }
197→    touchStartPosRef.current = null;
198→  }, []);
199→
200→  /**
201→   * Handle touch start - begin long press detection.
202→   */
203→  const handleTouchStart = useCallback(
204→    (e: TouchEvent) => {
205→      const touch = e.touches[0];
206→      if (!touch) return;
207→
208→      // Store touch position for move detection
209→      touchStartPosRef.current = { x: touch.clientX, y: touch.clientY };
210→
211→      // Start long press timer
212→      longPressTimerRef.current = setTimeout(() => {
213→        // Create synthetic mouse event for context menu
214→        const syntheticEvent = {
215→          preventDefault: () => {},
216→          stopPropagation: () => {},
217→          clientX: touch.clientX,
218→          clientY: touch.clientY,
219→        } as unknown as MouseEvent;
220→        onContextMenu(syntheticEvent, item);
221→        clearLongPressTimer();
222→      }, LONG_PRESS_DURATION);
223→    },
224→    [item, onContextMenu, clearLongPressTimer]
225→  );
226→
227→  /**
228→   * Handle touch move - cancel long press if moved too far.
229→   */
230→  const handleTouchMove = useCallback(
231→    (e: TouchEvent) => {
232→      if (!touchStartPosRef.current || !longPressTimerRef.current) return;
233→
234→      const touch = e.touches[0];
235→      if (!touch) return;
236→
237→      // Cancel if moved more than 10px
238→      const dx = Math.abs(touch.clientX - touchStartPosRef.current.x);
239→      const dy = Math.abs(touch.clientY - touchStartPosRef.current.y);
240→      if (dx > 10 || dy > 10) {
241→        clearLongPressTimer();
242→      }
243→    },
244→    [clearLongPressTimer]
245→  );
246→
247→  /**
248→   * Handle touch end - clear long press timer.
249→   */
250→  const handleTouchEnd = useCallback(() => {
251→    clearLongPressTimer();
252→  }, [clearLongPressTimer]);
253→
254→  /**
255→   * Handle drag over - allow drop on folders.
256→   * Accepts both internal drags (move) and external file drags (upload).
257→   */
258→  const handleDragOver = useCallback(
259→    (e: DragEvent) => {
260→      // Only allow drop on folders
261→      if (!isFolder(item)) return;
262→
263→      e.preventDefault();
264→      e.stopPropagation();
265→
266→      // External files get 'copy' effect, internal moves get 'move'
267→      e.dataTransfer.dropEffect = isExternalFileDrag(e.dataTransfer) ? 'copy' : 'move';
268→      setIsDragOver(true);
269→    },
270→    [item]
271→  );
272→
273→  /**
274→   * Handle drag leave - clear drag-over state.
275→   */
276→  const handleDragLeave = useCallback((e: DragEvent) => {
277→    e.preventDefault();
278→    e.stopPropagation();
279→    setIsDragOver(false);
280→  }, []);
281→
282→  /**
283→   * Handle drop - move item to this folder, or upload external files.
284→   */
285→  const handleDrop = useCallback(
286→    (e: DragEvent) => {
287→      e.preventDefault();
288→      e.stopPropagation();
289→      setIsDragOver(false);
290→
291→      // Only handle drop on folders
292→      if (!isFolder(item)) return;
293→
294→      // Read JSON once — empty string means external file drag from OS
295→      const jsonData = e.dataTransfer.getData('application/json');
296→
297→      if (!jsonData) {
298→        // External file drop
299→        if (e.dataTransfer.files.length > 0 && onExternalFileDrop) {
300→          const files = Array.from(e.dataTransfer.files);
301→          onExternalFileDrop(files, item.id);
302→        }
303→        return;
304→      }
305→
306→      // Internal move operation
307→      if (!onDrop) return;
308→
309→      try {
310→        const parsed = JSON.parse(jsonData) as {
311→          items: DragItem[];
312→          parentId: string;
313→        };
314→
315→        // Filter out items that are this folder or already in this folder
316→        const validItems = parsed.items.filter(
317→          (i) => i.id !== item.id && parsed.parentId !== item.id
318→        );
319→        if (validItems.length === 0) return;
320→
321→        onDrop(validItems, parsed.parentId);
322→      } catch {
323→        // Invalid drag data, ignore
324→      }
325→    },
326→    [item, onDrop, onExternalFileDrop]
327→  );
328→
329→  // Lazily resolve file size from per-file IPNS metadata
330→  const fileSize = useFileSize(isFile(item) ? item.fileMetaIpnsName : null, folderKey);
331→
332→  // Display size: resolved from IPNS metadata for files, dash for folders
333→  const sizeDisplay = isFile(item) ? (fileSize !== null ? formatBytes(fileSize) : '...') : '-';
334→
335→  // Display modified date
336→  const dateDisplay = formatDate(item.modifiedAt);
337→
338→  // Build class name with drag-over state
339→  const className = [
340→    'file-list-item',
341→    isSelected ? 'file-list-item--selected' : '',
342→    isDragOver && isFolder(item) ? 'file-list-item--drag-over' : '',
343→  ]
344→    .filter(Boolean)
345→    .join(' ');
346→
347→  return (
348→    <div
349→      className={className}
350→      data-item-id={item.id}
351→      onClick={handleClick}
352→      onDoubleClick={handleDoubleClick}
353→      onContextMenu={handleContextMenu}
354→      draggable
355→      onDragStart={handleDragStart}
356→      onDragOver={isFolder(item) && (onDrop || onExternalFileDrop) ? handleDragOver : undefined}
357→      onDragLeave={isFolder(item) && (onDrop || onExternalFileDrop) ? handleDragLeave : undefined}
358→      onDrop={isFolder(item) && (onDrop || onExternalFileDrop) ? handleDrop : undefined}
359→      onTouchStart={handleTouchStart}
360→      onTouchMove={handleTouchMove}
361→      onTouchEnd={handleTouchEnd}
362→      onTouchCancel={handleTouchEnd}
363→      role="row"
364→      tabIndex={0}
365→      onKeyDown={(e) => {
366→        if (e.key === 'Enter') {
367→          if (isFolder(item)) {
368→            onNavigate(item.id);
369→          }
370→        }
371→      }}
372→    >
373→      {/* Row 1: Checkbox + Icon + Name (for mobile top row) */}
374→      <div className="file-list-item-row-top" role="gridcell">
375→        <span
376→          className="file-list-item-checkbox"
377→          onClick={handleCheckboxClick}
378→          role="checkbox"
379→          aria-checked={isSelected}
380→          aria-label={`Select ${item.name}`}
381→          tabIndex={-1}
382→          onKeyDown={(e) => {
383→            if (e.key === 'Enter' || e.key === ' ') {
384→              e.preventDefault();
385→              e.stopPropagation();
386→              onSelect(item.id, { ctrlKey: true, shiftKey: false, metaKey: false });
387→            }
388→          }}
389→        >
390→          {isSelected ? '[x]' : '[ ]'}
391→        </span>
392→        <span className="file-list-item-icon" aria-hidden="true">
393→          {getItemIcon(item)}
394→        </span>
395→        <span className="file-list-item-name">{item.name}</span>
396→      </div>
397→
398→      {/* Row 2: Size + Date (for mobile bottom row) */}
399→      <div className="file-list-item-row-bottom">
400→        <span className="file-list-item-size" role="gridcell">
401→          {sizeDisplay}
402→        </span>
403→        <span className="file-list-item-date" role="gridcell">
404→          {dateDisplay}
405→        </span>
406→      </div>
407→
408→      {/* Mobile action button - hidden on desktop via CSS */}
409→      <div className="file-list-item-mobile-actions" role="gridcell">
410→        <button
411→          type="button"
412→          className="file-list-item-action-btn"
413→          onClick={handleActionButtonClick}
414→          aria-label={`Actions for ${item.name}`}
415→        >
416→          ...
417→        </button>
418→      </div>
419→    </div>
420→  );
421→}
