499→/**
500→ * Add a file to a folder after successful upload (v2).
501→ *
502→ * Creates a FilePointer from the file's IPNS record and adds it to the folder's
503→ * children array. Publishes both the file IPNS record and the updated folder
504→ * metadata via a single batch API call.
505→ *
506→ * @param params.parentFolderState - Parent folder to add file to
507→ * @param params.fileId - Pre-generated file UUID
508→ * @param params.name - Original file name
509→ * @param params.fileIpnsRecord - File IPNS record payload from createFileMetadata
510→ * @returns Created file pointer and new sequence number
511→ * @throws Error if name collision exists
512→ */
513→export async function addFileToFolder(params: {
514→  parentFolderState: FolderNode;
515→  fileId: string;
516→  name: string;
517→  fileIpnsRecord: FileIpnsRecordPayload;
518→}): Promise<{ filePointer: FilePointer; newSequenceNumber: bigint }> {
519→  // 1. Check for name collision
520→  const nameExists = params.parentFolderState.children.some((c) => c.name === params.name);
521→  if (nameExists) {
522→    throw new Error('A file with this name already exists');
523→  }
524→
525→  // 2. Create FilePointer (slim reference to per-file IPNS record)
526→  const now = Date.now();
527→  const filePointer: FilePointer = {
528→    type: 'file',
529→    id: params.fileId,
530→    name: params.name,
531→    fileMetaIpnsName: params.fileIpnsRecord.ipnsName,
532→    createdAt: now,
533→    modifiedAt: now,
534→  };
535→
536→  // 3. Add FilePointer to parent's children
537→  const children: FolderChild[] = [...params.parentFolderState.children, filePointer];
538→
539→  // 4. Build folder IPNS record for batch publish
540→  const folderResult = await buildFolderIpnsRecord({
541→    children,
542→    folderKey: params.parentFolderState.folderKey,
543→    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
544→    ipnsName: params.parentFolderState.ipnsName,
545→    sequenceNumber: params.parentFolderState.sequenceNumber,
546→  });
547→
548→  // 5. Batch publish: file IPNS record + folder IPNS record
549→  const publishResult = await batchPublishIpnsRecords([
550→    { ...params.fileIpnsRecord, recordType: 'file' },
551→    folderResult.record,
552→  ]);
553→
554→  if (publishResult.totalFailed > 0) {
555→    console.error(
556→      `Batch publish partial failure: ${publishResult.totalFailed}/${publishResult.totalFailed + publishResult.totalSucceeded} records failed`
557→    );
558→    throw new Error('Failed to publish one or more IPNS records');
559→  }
560→
561→  return { filePointer, newSequenceNumber: folderResult.newSequenceNumber };
562→}
