  68→/**
  69→ * React hook for folder operations with loading/error state.
  70→ *
  71→ * Provides createFolder, renameItem, moveItem, and deleteItem operations
  72→ * that wrap folder.service functions with proper state management.
  73→ *
  74→ * @example
  75→ * ```tsx
  76→ * function FolderActions() {
  77→ *   const { isLoading, error, createFolder, deleteItem } = useFolder();
  78→ *
  79→ *   const handleCreate = async () => {
  80→ *     try {
  81→ *       await createFolder('New Folder', currentFolderId);
  82→ *     } catch (err) {
  83→ *       // Error also in error state
  84→ *     }
  85→ *   };
  86→ *
  87→ *   return (
  88→ *     <div>
  89→ *       {isLoading && <Spinner />}
  90→ *       {error && <ErrorMessage>{error}</ErrorMessage>}
  91→ *       <button onClick={handleCreate}>New Folder</button>
  92→ *     </div>
  93→ *   );
  94→ * }
  95→ * ```
  96→ */
  97→export function useFolder() {
  98→  const [state, setState] = useState<FolderOperationState>({
  99→    isLoading: false,
 100→    error: null,
 101→  });
 102→
 103→  // Get folder store for reactive updates
 104→  // Note: We use getState() in callbacks to avoid stale closures
 105→  const folderStore = useFolderStore();
 106→
 107→  /**
 108→   * Create a new folder.
 109→   *
 110→   * @param name - Folder name
 111→   * @param parentId - Parent folder ID (null for root, or folder UUID)
 112→   * @returns Created folder entry
 113→   * @throws Error if depth limit exceeded or creation fails
 114→   */
 115→  const handleCreate = useCallback(
 116→    async (name: string, parentId: string | null): Promise<FolderEntry> => {
 117→      setState({ isLoading: true, error: null });
 118→      try {
 119→        const folders = useFolderStore.getState().folders;
 120→        const vault = useVaultStore.getState();
 121→        const auth = useAuthStore.getState();
 122→
 123→        // Validate depth limit before creating (FOLD-03)
 124→        const parentDepth = folderService.getDepth(parentId, folders);
 125→        if (parentDepth >= MAX_FOLDER_DEPTH) {
 126→          throw new Error(`Cannot create folder: maximum depth of ${MAX_FOLDER_DEPTH} exceeded`);
 127→        }
 128→
 129→        // Get user's ECIES keypair for vault cryptographic operations (public + private keys stored in memory after login)
 130→        // The public key is used here for key wrapping; the private key remains client-side for decryption operations.
 131→        if (!auth.vaultKeypair) {
 132→          throw new Error('No ECIES keypair available - please log in again');
 133→        }
 134→        const userPublicKey = auth.vaultKeypair.publicKey;
 135→
 136→        // Create the folder (generates keys, wraps with user public key, TEE-encrypts IPNS key)
 137→        const { folder, ipnsPrivateKey, folderKey, encryptedIpnsPrivateKey, keyEpoch } =
 138→          await folderService.createFolder({
 139→            parentFolderId: parentId,
 140→            name,
 141→            userPublicKey,
 142→            folders,
 143→          });
 144→
 145→        // Get parent folder state
 146→        const parentFolder =
 147→          parentId && folders[parentId] ? folders[parentId] : getRootFolderState(vault, folders);
 148→
 149→        if (!parentFolder) {
 150→          throw new Error('Parent folder not found or vault not initialized');
 151→        }
 152→
 153→        // Add to parent's children
 154→        const newChildren = [...parentFolder.children, folder];
 155→
 156→        // Update parent folder metadata and publish IPNS
 157→        await folderService.updateFolderMetadata({
 158→          folderId: parentFolder.id,
 159→          children: newChildren,
 160→          folderKey: parentFolder.folderKey,
 161→          ipnsPrivateKey: parentFolder.ipnsPrivateKey,
 162→          ipnsName: parentFolder.ipnsName,
 163→          sequenceNumber: parentFolder.sequenceNumber,
 164→        });
 165→
 166→        // First publish for the new folder's own IPNS record with TEE-encrypted key
 167→        // This sends encryptedIpnsPrivateKey to backend for TEE republish enrollment
 168→        await folderService.updateFolderMetadata({
 169→          folderId: folder.id,
 170→          children: [],
 171→          folderKey,
 172→          ipnsPrivateKey,
 173→          ipnsName: folder.ipnsName,
 174→          sequenceNumber: 0n,
 175→          encryptedIpnsPrivateKey,
 176→          keyEpoch,
 177→        });
 178→
 179→        // Update local state - add new folder to tree
 180→        useFolderStore.getState().updateFolderChildren(parentFolder.id, newChildren);
 181→
 182→        // Also add the new folder node to the store (with its decrypted keys)
 183→        const newFolderNode: FolderNode = {
 184→          id: folder.id,
 185→          name: folder.name,
 186→          ipnsName: folder.ipnsName,
 187→          parentId: parentFolder.id,
 188→          children: [],
 189→          isLoaded: true,
 190→          isLoading: false,
 191→          sequenceNumber: 0n,
 192→          folderKey,
 193→          ipnsPrivateKey,
 194→        };
 195→        useFolderStore.getState().setFolder(newFolderNode);
 196→
 197→        setState({ isLoading: false, error: null });
 198→        return folder;
 199→      } catch (err) {
 200→        const error = err instanceof Error ? err.message : 'Failed to create folder';
 201→        setState({ isLoading: false, error });
 202→        throw err;
 203→      }
 204→    },
 205→    []
 206→  );
 207→
 208→  /**
 209→   * Rename a file or folder.
 210→   *
 211→   * @param itemId - ID of item to rename
 212→   * @param itemType - 'file' or 'folder'
 213→   * @param newName - New name
 214→   * @param parentId - Parent folder ID
 215→   */
 216→  const handleRename = useCallback(
 217→    async (
 218→      itemId: string,
 219→      itemType: 'file' | 'folder',
 220→      newName: string,
 221→      parentId: string
 222→    ): Promise<void> => {
 223→      setState({ isLoading: true, error: null });
 224→      try {
 225→        const folders = useFolderStore.getState().folders;
 226→        const vault = useVaultStore.getState();
 227→
 228→        // Get parent folder state
 229→        const parentFolder =
 230→          parentId === 'root' ? getRootFolderState(vault, folders) : folders[parentId];
 231→
 232→        if (!parentFolder) throw new Error('Parent folder not found');
 233→
 234→        if (itemType === 'folder') {
 235→          await folderService.renameFolder({
 236→            folderId: itemId,
 237→            newName,
 238→            parentFolderState: parentFolder,
 239→          });
 240→
 241→          // Update local folder state name
 242→          useFolderStore.getState().updateFolderName(itemId, newName);
 243→        } else {
 244→          await folderService.renameFile({
 245→            fileId: itemId,
 246→            newName,
 247→            parentFolderState: parentFolder,
 248→          });
 249→        }
 250→
 251→        // Update parent's children with new name
 252→        const updatedChildren = parentFolder.children.map((child) => {
 253→          if (child.id === itemId) {
 254→            return { ...child, name: newName, modifiedAt: Date.now() };
 255→          }
 256→          return child;
 257→        });
 258→        useFolderStore.getState().updateFolderChildren(parentId, updatedChildren);
 259→
 260→        setState({ isLoading: false, error: null });
 261→      } catch (err) {
 262→        const error = err instanceof Error ? err.message : 'Failed to rename';
 263→        setState({ isLoading: false, error });
 264→        throw err;
 265→      }
 266→    },
 267→    []
 268→  );
 269→
 270→  /**
 271→   * Move a file or folder to a different parent.
 272→   *
 273→   * @param itemId - ID of item to move
 274→   * @param itemType - 'file' or 'folder'
 275→   * @param sourceParentId - Current parent folder ID
 276→   * @param destParentId - Destination parent folder ID
 277→   */
 278→  const handleMove = useCallback(
 279→    async (
 280→      itemId: string,
 281→      itemType: 'file' | 'folder',
 282→      sourceParentId: string,
 283→      destParentId: string
 284→    ): Promise<void> => {
 285→      setState({ isLoading: true, error: null });
 286→      try {
 287→        const folders = useFolderStore.getState().folders;
 288→        const vault = useVaultStore.getState();
 289→
 290→        // Get source and destination folder states
 291→        const sourceFolder =
 292→          sourceParentId === 'root' ? getRootFolderState(vault, folders) : folders[sourceParentId];
 293→
 294→        const destFolder =
 295→          destParentId === 'root' ? getRootFolderState(vault, folders) : folders[destParentId];
 296→
 297→        if (!sourceFolder || !destFolder) {
 298→          throw new Error('Source or destination folder not found');
 299→        }
 300→
 301→        if (itemType === 'folder') {
 302→          await folderService.moveFolder({
 303→            folderId: itemId,
 304→            sourceFolderState: sourceFolder,
 305→            destFolderState: destFolder,
 306→            folders,
 307→          });
 308→
 309→          // Update the moved folder's parentId in local state
 310→          const movedFolder = folders[itemId];
 311→          if (movedFolder) {
 312→            useFolderStore.getState().setFolder({
 313→              ...movedFolder,
 314→              parentId: destParentId,
 315→            });
 316→          }
 317→        } else {
 318→          await folderService.moveFile({
 319→            fileId: itemId,
 320→            sourceFolderState: sourceFolder,
 321→            destFolderState: destFolder,
 322→          });
 323→        }
 324→
 325→        // Update source folder's children (remove item)
 326→        const updatedSourceChildren = sourceFolder.children.filter((c) => c.id !== itemId);
 327→        useFolderStore.getState().updateFolderChildren(sourceParentId, updatedSourceChildren);
 328→
 329→        // Update dest folder's children (add item)
 330→        const movedItem = sourceFolder.children.find((c) => c.id === itemId);
 331→        if (movedItem) {
 332→          const updatedDestChildren = [
 333→            ...destFolder.children,
 334→            { ...movedItem, modifiedAt: Date.now() },
 335→          ];
 336→          useFolderStore.getState().updateFolderChildren(destParentId, updatedDestChildren);
 337→        }
 338→
 339→        setState({ isLoading: false, error: null });
 340→      } catch (err) {
 341→        const error = err instanceof Error ? err.message : 'Failed to move';
 342→        setState({ isLoading: false, error });
 343→        throw err;
 344→      }
 345→    },
 346→    []
 347→  );
 348→
 349→  /**
 350→   * Move multiple files/folders to a destination in a single batch.
 351→   *
 352→   * Uses add-before-remove pattern. Publishes IPNS once for the destination
 353→   * and once for the source (2 total), regardless of how many items are moved.
 354→   *
 355→   * @param items - Array of { id, type } to move
 356→   * @param sourceParentId - Current parent folder ID (all items must share the same parent)
 357→   * @param destParentId - Destination parent folder ID
 358→   */
 359→  const handleMoveItems = useCallback(
 360→    async (
 361→      items: Array<{ id: string; type: 'file' | 'folder' }>,
 362→      sourceParentId: string,
 363→      destParentId: string
 364→    ): Promise<void> => {
 365→      setState({ isLoading: true, error: null });
 366→      try {
 367→        const folders = useFolderStore.getState().folders;
 368→        const vault = useVaultStore.getState();
 369→
 370→        const sourceFolder =
 371→          sourceParentId === 'root' ? getRootFolderState(vault, folders) : folders[sourceParentId];
 372→        const destFolder =
 373→          destParentId === 'root' ? getRootFolderState(vault, folders) : folders[destParentId];
 374→
 375→        if (!sourceFolder || !destFolder) {
 376→          throw new Error('Source or destination folder not found');
 377→        }
 378→
 379→        const itemIds = new Set(items.map((i) => i.id));
 380→        const movedChildren = sourceFolder.children.filter((c) => itemIds.has(c.id));
 381→        const now = Date.now();
 382→
 383→        // Validate all items
 384→        const batchNames = new Set<string>();
 385→        for (const child of movedChildren) {
 386→          // Intra-batch duplicate name check
 387→          if (batchNames.has(child.name)) {
 388→            throw new Error(`Multiple selected items share the name "${child.name}"`);
 389→          }
 390→          batchNames.add(child.name);
 391→
 392→          // Name collision check against destination
 393→          const nameExists = destFolder.children.some((c) => c.name === child.name);
 394→          if (nameExists) {
 395→            throw new Error(`An item named "${child.name}" already exists in the destination`);
 396→          }
 397→
 398→          if (child.type === 'folder') {
 399→            // Prevent moving folder into itself or descendant
 400→            if (folderService.isDescendantOf(destFolder.id, child.id, folders)) {
 401→              throw new Error(`Cannot move "${child.name}" into itself or its subfolder`);
 402→            }
 403→
 404→            // Depth limit check
 405→            const destDepth = folderService.getDepth(destFolder.id, folders);
 406→            const subtreeDepth = folderService.calculateSubtreeDepth(child.id, folders);
 407→            if (destDepth + 1 + subtreeDepth > MAX_FOLDER_DEPTH) {
 408→              throw new Error(
 409→                `Cannot move "${child.name}": would exceed maximum folder depth of ${MAX_FOLDER_DEPTH}`
 410→              );
 411→            }
 412→          }
 413→        }
 414→
 415→        // ADD all to destination FIRST (add-before-remove pattern)
 416→        const destChildren = [
 417→          ...destFolder.children,
 418→          ...movedChildren.map((c) => ({ ...c, modifiedAt: now })),
 419→        ];
 420→
 421→        const { newSequenceNumber: destSeq } = await folderService.updateFolderMetadata({
 422→          folderId: destFolder.id,
 423→          children: destChildren,
 424→          folderKey: destFolder.folderKey,
 425→          ipnsPrivateKey: destFolder.ipnsPrivateKey,
 426→          ipnsName: destFolder.ipnsName,
 427→          sequenceNumber: destFolder.sequenceNumber,
 428→        });
 429→
 430→        // REMOVE all from source AFTER destination confirmed
 431→        const sourceChildren = sourceFolder.children.filter((c) => !itemIds.has(c.id));
 432→
 433→        const { newSequenceNumber: sourceSeq } = await folderService.updateFolderMetadata({
 434→          folderId: sourceFolder.id,
 435→          children: sourceChildren,
 436→          folderKey: sourceFolder.folderKey,
 437→          ipnsPrivateKey: sourceFolder.ipnsPrivateKey,
 438→          ipnsName: sourceFolder.ipnsName,
 439→          // Re-read sequence in case source === dest parent was updated above
 440→          sequenceNumber: sourceFolder.id === destFolder.id ? destSeq : sourceFolder.sequenceNumber,
 441→        });
 442→
 443→        // Update local state
 444→        const store = useFolderStore.getState();
 445→        store.updateFolderChildren(sourceParentId, sourceChildren);
 446→        store.updateFolderChildren(destParentId, destChildren);
 447→        store.updateFolderSequence(destParentId, destSeq);
 448→        store.updateFolderSequence(sourceParentId, sourceSeq);
 449→
 450→        for (const item of items) {
 451→          if (item.type === 'folder') {
 452→            const movedFolder = folders[item.id];
 453→            if (movedFolder) {
 454→              store.setFolder({ ...movedFolder, parentId: destParentId });
 455→            }
 456→          }
 457→        }
 458→
 459→        setState({ isLoading: false, error: null });
 460→      } catch (err) {
 461→        const error = err instanceof Error ? err.message : 'Failed to move items';
 462→        setState({ isLoading: false, error });
 463→        throw err;
 464→      }
 465→    },
 466→    []
 467→  );
