 677→  /**
 678→   * Add multiple files to a folder after upload (v2 batch).
 679→   *
 680→   * Creates per-file IPNS metadata for each file, then registers all
 681→   * FilePointers via a single batch IPNS publish.
 682→   *
 683→   * @param parentId - Parent folder ID ('root' or folder UUID)
 684→   * @param filesData - Array of uploaded file data from upload service
 685→   * @returns Created file pointers
 686→   */
 687→  const handleAddFiles = useCallback(
 688→    async (
 689→      parentId: string,
 690→      filesData: Array<{
 691→        cid: string;
 692→        wrappedKey: string;
 693→        iv: string;
 694→        originalName: string;
 695→        originalSize: number;
 696→        mimeType?: string;
 697→        encryptionMode?: 'GCM' | 'CTR';
