 52→ * @param folderId - Folder ID to calculate depth for (null = root)
 53→ * @param folders - Current folder tree
 54→ * @returns Depth from root (0 for root)
 55→ */
 56→export function getDepth(folderId: string | null, folders: Record<string, FolderNode>): number {
 57→  if (folderId === null) return 0; // root is depth 0
 58→
 59→  let depth = 0;
 60→  let currentId: string | null = folderId;
 61→
 62→  while (currentId !== null) {
 63→    const folder: FolderNode | undefined = folders[currentId];
 64→    if (!folder) break;
 65→    depth++;
 66→    currentId = folder.parentId;
